import { notFound } from "next/navigation";
import Link from "next/link";
import { Container, Section } from "@/components/ui/container";
import { Button } from "@/components/ui/button";
import { Calendar, Clock, ArrowLeft, ArrowRight } from "lucide-react";
import { Metadata } from "next";
import { createAdminClient } from "@/lib/supabase/server";

export const revalidate = 60;

type Props = {
    params: Promise<{ slug: string }>;
};

// Helper to slugify author name
const slugify = (text: string) => text.toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]+/g, '');

async function getAuthorData(slug: string) {
    const supabase = createAdminClient();

    // Fetch all published posts
    // Note: In a larger app, we would have an 'authors' table or 'author_slug' column
    const { data: posts, error } = await supabase
        .from('blogs')
        .select('*')
        .eq('status', 'published')
        .order('published_at', { ascending: false });

    if (error) {
        console.error("Supabase Error:", error);
        return null;
    }

    // Filter posts by author slug
    const authorPosts = posts.filter((post: any) => slugify(post.author_name || '') === slug);

    if (!authorPosts.length) return null;

    // Extract author details from the most recent post
    const authorInfo = {
        name: authorPosts[0].author_name,
        role: authorPosts[0].author_role || 'Contributor',
        avatar: authorPosts[0].author_avatar,
        bio: authorPosts[0].author_bio,
        posts: authorPosts
    };

    return authorInfo;
}

export async function generateMetadata({ params }: Props): Promise<Metadata> {
    const { slug } = await params;
    const author = await getAuthorData(slug);

    if (!author) {
        return {
            title: 'Author Not Found',
        };
    }

    return {
        title: `${author.name} - Author at Digihub Solutions`,
        description: author.bio || `Read articles by ${author.name} on Digihub Solutions.`,
        openGraph: {
            title: `${author.name} - Author Profile`,
            description: author.bio,
            images: author.avatar ? [author.avatar] : [],
        },
    };
}

export default async function AuthorPage({ params }: Props) {
    const { slug } = await params;
    const author = await getAuthorData(slug);

    if (!author) {
        notFound();
    }

    return (
        <main className="min-h-screen bg-[#0B0F14] text-white pt-20">
            {/* Author Hero */}
            <Section className="py-20 relative overflow-hidden">
                {/* Background Glow */}
                <div className="absolute top-0 right-0 w-[500px] h-[500px] bg-primary/5 rounded-full blur-[100px] pointer-events-none" />

                <Container className="max-w-4xl text-center relative z-10">
                    <Link href="/blog">
                        <Button variant="ghost" className="mb-8 text-gray-400 hover:text-white">
                            <ArrowLeft className="mr-2" size={16} /> Back to Blog
                        </Button>
                    </Link>

                    <div className="w-32 h-32 mx-auto bg-gradient-to-br from-primary to-secondary rounded-full flex items-center justify-center text-4xl font-bold text-black mb-6 border-4 border-[#0B0F14] shadow-[0_0_40px_-10px_rgba(0,217,195,0.3)] overflow-hidden">
                        {author.avatar ? (
                            <img src={author.avatar} alt={author.name} className="w-full h-full object-cover" />
                        ) : (
                            author.name.charAt(0)
                        )}
                    </div>

                    <h1 className="text-4xl md:text-5xl font-bold mb-3">{author.name}</h1>
                    <p className="text-primary font-medium tracking-wide uppercase text-sm mb-6">{author.role}</p>

                    {author.bio && (
                        <p className="text-xl text-gray-400 max-w-2xl mx-auto leading-relaxed">
                            {author.bio}
                        </p>
                    )}
                </Container>
            </Section>

            {/* Author's Articles */}
            <Section className="py-20 border-t border-white/5 bg-white/[0.02]">
                <Container>
                    <div className="flex items-center justify-between mb-12">
                        <h2 className="text-2xl font-bold">Latest Articles by {author.name.split(' ')[0]}</h2>
                        <span className="text-gray-400 text-sm">{author.posts.length} Articles</span>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                        {author.posts.map((post: any) => (
                            <Link key={post.slug} href={`/blog/${post.slug}`} className="group h-full flex flex-col">
                                <div className="bg-[#0F141A] border border-white/5 rounded-2xl overflow-hidden hover:border-primary/50 hover:shadow-[0_0_30px_-10px_rgba(0,217,195,0.3)] transition-all duration-300 h-full flex flex-col">
                                    {/* Card Image */}
                                    <div className="relative h-48 overflow-hidden">
                                        {post.image ? (
                                            <img src={post.image} alt={post.title} className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110" />
                                        ) : (
                                            <div className="w-full h-full bg-gradient-to-br from-gray-800 to-gray-900" />
                                        )}
                                        <div className="absolute top-4 left-4">
                                            <span className="px-3 py-1 bg-black/50 backdrop-blur-md border border-white/10 text-white text-xs rounded-full">
                                                {post.category}
                                            </span>
                                        </div>
                                    </div>

                                    {/* Card Content */}
                                    <div className="p-6 flex-1 flex flex-col">
                                        <div className="flex items-center gap-2 text-xs text-gray-500 mb-3">
                                            <Calendar size={12} />
                                            {new Date(post.published_at).toLocaleDateString()}
                                            <span className="w-1 h-1 rounded-full bg-gray-600" />
                                            <Clock size={12} /> {post.read_time}
                                        </div>

                                        <h3 className="text-xl font-bold mb-3 group-hover:text-primary transition-colors line-clamp-2">
                                            {post.title}
                                        </h3>

                                        <p className="text-gray-400 text-sm mb-6 line-clamp-2 flex-1">
                                            {post.excerpt}
                                        </p>

                                        <div className="flex items-center justify-between pt-4 border-t border-white/5 mt-auto">
                                            <span className="text-xs font-medium text-gray-300">
                                                Read Article
                                            </span>
                                            <ArrowRight size={16} className="text-primary -rotate-45 group-hover:rotate-0 transition-transform duration-300" />
                                        </div>
                                    </div>
                                </div>
                            </Link>
                        ))}
                    </div>
                </Container>
            </Section>
        </main>
    );
}

import { notFound } from "next/navigation";
import Link from "next/link";
import { Container, Section } from "@/components/ui/container";
import { Button } from "@/components/ui/button";
import { Calendar, Clock, ArrowLeft, HelpCircle, Linkedin, Twitter, Facebook, ArrowRight } from "lucide-react";
import { Metadata } from "next";
import { ConsultationFormSection } from "@/components/sections/ConsultationForm";
import { createAdminClient } from "@/lib/supabase/server";
import { ReadingProgressBar } from "@/components/blog/ReadingProgressBar";
import { BlogAnalytics } from "@/components/blog/BlogAnalytics";
import { SidebarNewsletter } from "@/components/blog/SidebarNewsletter";
import { SidebarAudit } from "@/components/blog/SidebarAudit";
import { InlineCTA } from "@/components/blog/InlineCTA"; // Now used!
import { TableOfContents } from "@/components/blog/TableOfContents";

export const revalidate = 60;
export const dynamicParams = true;

type Props = {
    params: Promise<{ slug: string }>;
};

// Helper: Consistent ID generation
function slugify(text: string) {
    return text.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-|-$/g, '');
}

async function getPost(slug: string) {
    const supabase = createAdminClient();
    const { data: post, error } = await supabase
        .from('blogs')
        .select('*')
        .eq('slug', slug)
        .single();

    if (error) console.error("Supabase Error:", error);

    // Fallback Related Resources
    if (post && (!post.related_resources || post.related_resources.length === 0)) {
        post.related_resources = [
            { title: "Explore our Digital Marketing Services", href: "/services" },
            { title: "Book a Free Strategy Consultation", href: "/contact" },
            { title: "See our Success Stories", href: "/case-studies" }
        ];
    }

    return post;
}

async function getRelatedPosts(currentSlug: string, category: string) {
    const supabase = createAdminClient();
    const { data: posts } = await supabase
        .from('blogs')
        .select('title, slug, image, published_at, read_time, category')
        .eq('category', category)
        .neq('slug', currentSlug)
        .eq('status', 'published')
        .limit(3);

    return posts || [];
}

function extractFAQs(content: string | null | undefined) {
    if (!content) return [];
    const faqSectionMatch = content.match(/##\s+(?:FAQ|Frequently Asked Questions)([\s\S]*?)(?:##|$)/i);
    if (!faqSectionMatch) return [];

    const faqContent = faqSectionMatch[1];
    const faqs = [];
    const regex = /###\s+(.*?)\n([\s\S]*?)(?=(?:###|$))/g;

    let match;
    while ((match = regex.exec(faqContent)) !== null) {
        faqs.push({
            question: match[1].trim(),
            answer: match[2].trim()
        });
    }
    return faqs;
}

// Processing content for display (IDs, classes)
function processContentPart(content: string): string {
    const lines = content.split('\n');
    return lines.map((line) => {
        if (line.startsWith('## ')) {
            const title = line.substring(3);
            const id = slugify(title);
            return `<h2 id="${id}" class="text-3xl md:text-4xl font-bold text-white mt-16 mb-8 scroll-mt-32 border-l-4 border-primary pl-6 tracking-tight">${title}</h2>`;
        }
        if (line.startsWith('# ')) return `<h1 class="text-4xl font-bold text-white mt-12 mb-6">${line.substring(2)}</h1>`;
        if (line.startsWith('### ')) return `<h3 class="text-2xl font-bold text-white mt-12 mb-6 tracking-wide">${line.substring(4)}</h3>`;
        if (line.startsWith('- ')) return `<li class="ml-6 mb-4 text-gray-300 list-disc pl-2 marker:text-primary text-lg leading-relaxed">${line.substring(2)}</li>`;
        if (line.startsWith('> ')) return `<blockquote class="border-l-4 border-primary/50 pl-8 py-6 italic text-xl md:text-2xl text-gray-200 bg-white/[0.03] rounded-r-2xl my-10 relative font-serif"><span class="absolute top-2 left-2 text-6xl text-primary/20 -translate-y-4">"</span>${line.substring(2)}</blockquote>`;

        // Formatting
        let processedLine = line
            .replace(/\*\*(.*?)\*\*/g, '<strong class="text-white font-bold">$1</strong>')
            .replace(/`(.*?)`/g, '<code class="bg-white/10 text-primary px-1.5 py-0.5 rounded font-mono text-sm border border-white/5">$1</code>')
            .replace(/\*(.*?)\*/g, '<em class="text-gray-300 italic">$1</em>')
            .replace(/\[(.*?)\]\((.*?)\)/g, '<a href="$2" class="text-primary hover:underline underline-offset-4 decoration-primary/50 hover:decoration-primary transition-all font-medium">$1</a>');

        if (processedLine.trim()) return `<p class="mb-8 text-gray-300 leading-8 text-lg md:text-xl tracking-wide font-light">${processedLine}</p>`;
        return '';
    }).join('');
}

export async function generateMetadata({ params }: Props): Promise<Metadata> {
    const { slug } = await params;
    const post = await getPost(slug);

    if (!post) return { title: 'Article Not Found' };

    return {
        title: post.meta_title || post.title,
        description: post.meta_description || post.excerpt,
        openGraph: {
            title: post.meta_title || post.title,
            description: post.meta_description || post.excerpt,
            images: post.image ? [post.image] : [],
            type: 'article',
            publishedTime: post.published_at,
            authors: [post.author_name || 'Digihub Team'],
            modifiedTime: post.updated_at || post.published_at,
        },
    };
}

export async function generateStaticParams() {
    const supabase = createAdminClient();
    const { data: posts } = await supabase.from('blogs').select('slug').eq('status', 'published');
    return (posts || []).map((post: { slug: string }) => ({ slug: post.slug }));
}

export default async function BlogPostPage({ params }: Props) {
    const { slug } = await params;
    const post = await getPost(slug);

    if (!post) notFound();

    const relatedPosts = await getRelatedPosts(slug, post.category);
    const faqs = extractFAQs(post.content);

    const author = {
        name: post.author_name || 'Digihub Team',
        role: post.author_role || 'Editor',
        avatar: post.author_avatar,
        slug: post.author_name ? post.author_name.toLowerCase().replace(/\s+/g, '-') : 'digihub-team',
        bio: post.author_bio || `Expert in ${post.category || 'Digital Marketing'} with a passion for helping businesses grow.`
    };

    // Calculate TOC
    const tableOfContents = (post.content && post.content.match(/^##\s+(.+)$/gm)?.map((header: string) => {
        const title = header.replace(/^##\s+/, '');
        const id = slugify(title);
        return { title, id };
    })) || [];

    // Split content for CTA Injection
    // We split by "## " but we need to keep distinct blocks.
    // Ideally we want to inject after the 2nd H2.
    const contentParts: string[] = post.content ? post.content.split(/(?=## )/g) : [];

    // JSON-LD Schema
    const jsonLd = {
        "@context": "https://schema.org",
        "@graph": [
            {
                "@type": "BlogPosting",
                "headline": post.title,
                "image": post.image ? [post.image] : [],
                "author": {
                    "@type": "Person",
                    "name": author.name,
                    "url": `https://digihub.agency/author/${author.slug}`,
                    "jobTitle": author.role,
                    "description": author.bio
                },
                "publisher": {
                    "@type": "Organization",
                    "name": "Digihub Solutions",
                    "logo": { "@type": "ImageObject", "url": "https://digihub.agency/logo.png" }
                },
                "datePublished": post.published_at,
                "dateModified": post.updated_at || post.published_at,
                "description": post.excerpt,
                "mainEntityOfPage": { "@type": "WebPage", "@id": `https://digihub.agency/blog/${slug}` }
            },
            ...(faqs.length > 0 ? [{
                "@type": "FAQPage",
                "mainEntity": faqs.map(faq => ({
                    "@type": "Question",
                    "name": faq.question,
                    "acceptedAnswer": { "@type": "Answer", "text": faq.answer }
                }))
            }] : [])
        ]
    };

    return (
        <main className="min-h-screen bg-[#0B0F14] text-white pt-10 font-sans selection:bg-primary/30">
            <ReadingProgressBar />
            <BlogAnalytics slug={slug} />
            <script type="application/ld+json" dangerouslySetInnerHTML={{ __html: JSON.stringify(jsonLd) }} />

            {/* HEADER NAV REMOVED
                <div className="fixed top-0 left-0 w-full z-40 bg-[#0B0F14]/90 backdrop-blur-md border-b border-white/5 h-16 md:h-20 flex items-center transition-transform duration-300 py-0">
                    <Container className="max-w-7xl flex items-center justify-between w-full">
                        <Link href="/blog" className="flex items-center gap-2 text-sm text-gray-400 hover:text-white transition-colors group">
                            <div className="w-8 h-8 rounded-full bg-white/5 flex items-center justify-center group-hover:bg-white/10 transition-colors">
                                <ArrowLeft size={16} />
                            </div>
                            <span className="hidden md:inline">Back</span>
                        </Link>

                        <div className="hidden md:flex items-center gap-2 text-sm text-gray-500">
                            <Link href="/blog" className="hover:text-white">Blog</Link>
                            <span>/</span>
                            <Link href={`/blog?category=${post.category}`} className="text-primary hover:text-primary/80">{post.category || 'Article'}</Link>
                        </div>

                        <div className="flex gap-3">
                            <Link href="/contact">
                                <Button size="sm" className="rounded-full font-semibold shadow-[0_0_15px_-5px_var(--color-primary)]">Get Free Audit</Button>
                            </Link>
                        </div>
                    </Container> 
                </div> 
                */}

            {/* HERO SECTION - RESTRUCTURED */}
            <Section className="pt-32 pb-0 md:pb-0 relative overflow-hidden">
                <div className="absolute top-0 right-0 w-[500px] h-[500px] bg-primary/5 rounded-full blur-[120px] pointer-events-none" />

                <Container className="max-w-7xl relative z-10">
                    <div className="grid grid-cols-1 lg:grid-cols-[1.1fr_0.9fr] gap-12 lg:gap-20 items-end">
                        <div>
                            {/* Meta Tags */}
                            <div className="flex flex-wrap items-center gap-4 mb-8 text-sm">
                                <span className="px-3 py-1 bg-primary/10 border border-primary/20 text-primary rounded-full font-bold uppercase text-xs tracking-wider">
                                    {post.category || 'Insight'}
                                </span>
                                <span className="text-gray-400 flex items-center gap-1.5 font-medium">
                                    <Calendar size={14} className="text-gray-500" />
                                    {post.published_at ? new Date(post.published_at).toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' }) : 'Draft'}
                                </span>
                                {(post.updated_at && post.updated_at !== post.published_at) && (
                                    <span className="text-gray-500 italic text-xs border-l border-white/10 pl-4">
                                        Updated: {new Date(post.updated_at).toLocaleDateString()}
                                    </span>
                                )}
                                <span className="text-gray-400 flex items-center gap-1.5 font-medium border-l border-white/10 pl-4">
                                    <Clock size={14} className="text-gray-500" />
                                    {post.read_time || '5 min read'}
                                </span>
                            </div>

                            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold mb-8 leading-[1.1] tracking-tight text-white">
                                {post.title}
                            </h1>
                            <p className="text-xl text-gray-300 mb-10 leading-relaxed max-w-xl">
                                {post.excerpt}
                            </p>

                            {/* Author Block - Enhanced EEAT */}
                            <div className="flex items-center gap-4 pt-4 pb-0 border-t border-white/10">
                                <div className="w-12 h-12 rounded-full overflow-hidden border border-white/20 bg-white/5">
                                    {author.avatar ? (
                                        <img src={author.avatar} alt={author.name} className="w-full h-full object-cover" />
                                    ) : (
                                        <div className="w-full h-full flex items-center justify-center font-bold text-white bg-gradient-to-br from-gray-800 to-black">
                                            {author.name.charAt(0)}
                                        </div>
                                    )}
                                </div>
                                <div>
                                    <div className="flex items-center gap-2">
                                        <span className="font-bold text-white text-base">{author.name}</span>
                                        <a href={author.slug === 'digihub-team' ? '#' : `https://linkedin.com/in/${author.slug}`} className="text-gray-500 hover:text-[#0077b5] transition-colors"><Linkedin size={14} /></a>
                                    </div>
                                    <div className="text-sm text-gray-400">{author.role}</div>
                                </div>
                            </div>
                        </div>

                        {/* Hero Image */}
                        <div className="relative rounded-2xl overflow-hidden border border-white/10 shadow-2xl aspect-[16/10] bg-[#0F141A] group mb-52">
                            {post.image ? (
                                <img
                                    src={post.image}
                                    alt={post.title}
                                    className="object-cover w-full h-full group-hover:scale-105 transition-transform duration-700"
                                    loading="eager"
                                />
                            ) : (
                                <div className="w-full h-full flex items-center justify-center text-gray-700 font-mono text-sm">No Cover Image</div>
                            )}
                            <div className="absolute inset-0 bg-gradient-to-t from-black/40 to-transparent pointer-events-none" />
                        </div>
                    </div>
                </Container>
            </Section>

            {/* CONTENT & SIDEBAR */}

            <Section className="pt-0 md:pt-0 pb-24 md:py-10">
                <Container className="max-w-7xl">
                    <div className="grid grid-cols-1 lg:grid-cols-[1fr_360px] gap-16">

                        {/* Main Content */}
                        <div className="min-w-0">
                            {/* Mobile TOC */}
                            {tableOfContents.length > 0 && (
                                <div className="lg:hidden mb-12">
                                    <TableOfContents headings={tableOfContents} />
                                </div>
                            )}

                            <article className="prose prose-invert prose-lg max-w-none prose-headings:font-bold prose-headings:text-white prose-p:text-gray-300 prose-a:text-primary hover:prose-a:text-primary/80 prose-strong:text-white prose-code:text-primary prose-code:bg-white/5 prose-code:rounded prose-code:px-1 prose-code:before:content-none prose-code:after:content-none">
                                {contentParts.map((part, index) => (
                                    <div key={index}>
                                        <div dangerouslySetInnerHTML={{ __html: processContentPart(part) }} />
                                        {/* INJECT CTA AFTER 2nd SECTION (which is usually Introduction + First Major Heading) */}
                                        {index === 2 && <InlineCTA />}
                                    </div>
                                ))}
                            </article>

                            {/* FAQs */}
                            {faqs.length > 0 && (
                                <div className="mt-20 border-t border-white/10 pt-16">
                                    <h2 className="text-3xl font-bold mb-10 flex items-center gap-3">
                                        <HelpCircle className="text-primary" /> Frequently Asked Questions
                                    </h2>
                                    <div className="grid gap-6">
                                        {faqs.map((faq: any, i: number) => (
                                            <div key={i} className="bg-[#0F141A] border border-white/5 rounded-2xl p-8 hover:border-primary/20 transition-all shadow-sm">
                                                <h3 className="text-lg font-bold text-white mb-3">{faq.question}</h3>
                                                <p className="text-gray-400 leading-relaxed text-base">{faq.answer}</p>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Sticky Sidebar */}
                        <aside className="hidden lg:block h-full">
                            <div className="sticky top-28 space-y-8 max-h-[calc(100vh-120px)] overflow-y-auto no-scrollbar pb-10">

                                <SidebarAudit />

                                {tableOfContents.length > 0 && (
                                    <TableOfContents headings={tableOfContents} />
                                )}

                                <SidebarNewsletter />

                                {/* Social Share */}
                                <div className="border border-white/10 rounded-2xl p-6 bg-white/[0.02]">
                                    <h3 className="text-xs font-bold text-gray-500 uppercase tracking-widest mb-4">Share Article</h3>
                                    <div className="flex gap-2">
                                        <Button variant="outline" size="icon" className="rounded-full bg-white/5 border-white/10 hover:bg-[#0077b5] hover:border-transparent hover:text-white transition-all text-gray-400">
                                            <Linkedin size={18} />
                                        </Button>
                                        <Button variant="outline" size="icon" className="rounded-full bg-white/5 border-white/10 hover:bg-black hover:border-white hover:text-white transition-all text-gray-400">
                                            <Twitter size={18} />
                                        </Button>
                                        <Button variant="outline" size="icon" className="rounded-full bg-white/5 border-white/10 hover:bg-[#1877f2] hover:border-transparent hover:text-white transition-all text-gray-400">
                                            <Facebook size={18} />
                                        </Button>
                                    </div>
                                </div>
                            </div>
                        </aside>
                    </div>
                </Container>
            </Section>

            {/* AUTHOR BIO EXPANDED */}
            <Section className="py-24 bg-[#0F141A] border-y border-white/5">
                <Container className="max-w-4xl">
                    <div className="flex flex-col md:flex-row items-center md:items-start gap-10 text-center md:text-left">
                        <div className="w-32 h-32 rounded-full overflow-hidden border-4 border-white/10 shrink-0 shadow-2xl">
                            {author.avatar ? (
                                <img src={author.avatar} alt={author.name} className="w-full h-full object-cover" />
                            ) : (
                                <div className="w-full h-full bg-gray-800 flex items-center justify-center text-4xl font-bold bg-gradient-to-br from-gray-700 to-black">{author.name.charAt(0)}</div>
                            )}
                        </div>
                        <div className="flex-1">
                            <h2 className="text-3xl font-bold mb-2 text-white">{author.name}</h2>
                            <div className="text-primary font-medium mb-4 flex items-center justify-center md:justify-start gap-2">
                                {author.role} <span className="w-1 h-1 bg-gray-600 rounded-full"></span>
                                <span className="text-gray-500 text-sm font-normal">Expert Contributor</span>
                            </div>
                            <p className="text-gray-300 leading-relaxed mb-8 text-lg">
                                {author.bio}
                            </p>
                            <div className="flex items-center justify-center md:justify-start gap-4">
                                <Link href={`/author/${author.slug}`}>
                                    <Button variant="outline" className="rounded-full border-white/10 hover:bg-white/10 px-6">View Full Profile</Button>
                                </Link>
                            </div>
                        </div>
                    </div>
                </Container>
            </Section>

            {/* RELATED ARTICLES */}
            <Section className="py-24">
                <Container className="max-w-7xl">
                    <div className="flex items-center justify-between mb-12">
                        <h2 className="text-3xl font-bold text-white">More from {post.category || 'our blog'}</h2>
                        <Link href="/blog">
                            <Button variant="ghost" className="text-primary hover:text-primary/80 group">View all <ArrowRight className="ml-2 w-4 h-4 group-hover:translate-x-1 transition-transform" /></Button>
                        </Link>
                    </div>
                    {relatedPosts.length > 0 ? (
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                            {relatedPosts.map((related: any) => (
                                <Link key={related.slug} href={`/blog/${related.slug}`} className="group block h-full">
                                    <div className="bg-[#0F141A] border border-white/5 rounded-2xl overflow-hidden h-full flex flex-col hover:border-primary/50 transition-all duration-500 hover:shadow-2xl hover:shadow-primary/5 group-hover:-translate-y-2">
                                        <div className="relative h-56 overflow-hidden">
                                            {related.image ? (
                                                <img src={related.image} alt={related.title} className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-700" />
                                            ) : (
                                                <div className="w-full h-full bg-gray-800" />
                                            )}
                                            <div className="absolute top-4 left-4">
                                                <span className="bg-black/80 backdrop-blur-md text-white text-[10px] font-bold px-3 py-1 rounded-full uppercase tracking-wider border border-white/10 shadow-lg">
                                                    {related.category}
                                                </span>
                                            </div>
                                        </div>
                                        <div className="p-8 flex-1 flex flex-col">
                                            <h3 className="text-xl font-bold mb-4 text-white group-hover:text-primary transition-colors line-clamp-2 leading-tight">{related.title}</h3>
                                            <div className="mt-auto pt-6 border-t border-white/5 text-xs text-gray-500 flex justify-between items-center font-medium">
                                                <span>{new Date(related.published_at).toLocaleDateString()}</span>
                                                <span className="flex items-center gap-1.5"><Clock size={14} /> {related.read_time}</span>
                                            </div>
                                        </div>
                                    </div>
                                </Link>
                            ))}
                        </div>
                    ) : (
                        <div className="text-center py-16 border border-dashed border-white/10 rounded-2xl bg-white/[0.02]">
                            <p className="text-gray-500">No related articles found.</p>
                        </div>
                    )}
                </Container>
            </Section>

            {/* FINAL CTA */}
            <ConsultationFormSection
                title={`Ready to Master ${post.category || 'Digital Marketing'}?`}
                subtitle="Stop guessing with your strategy. Get a data-driven roadmap customized for your business."
                source={`Blog Details CTA: ${post.title}`}
            />
        </main>
    );
}

"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import { Container } from "@/components/ui/container";
import { Button } from "@/components/ui/button";
import { categories } from "@/data/blog";
import { ArrowUpRight, Loader2 } from "lucide-react";
import { createClient } from "@/lib/supabase/client";
import { BlogPost } from "@/types";
import { BlogFilters } from "@/components/blog/BlogFilters";
import { BlogCard, BlogCardSkeleton } from "@/components/blog/BlogCard";
import { NewsletterCTA } from "@/components/blog/NewsletterCTA";

export default function BlogPage() {
    const [selectedCategory, setSelectedCategory] = useState("All");
    const [searchQuery, setSearchQuery] = useState("");
    const [posts, setPosts] = useState<BlogPost[]>([]);
    const [loading, setLoading] = useState(true);
    const [visibleCount, setVisibleCount] = useState(9); // Initial number of posts to show
    const supabase = createClient();

    useEffect(() => {
        const fetchBlogs = async () => {
            const { data, error } = await supabase
                .from('blogs')
                .select('*')
                .eq('status', 'published')
                .order('published_at', { ascending: false });

            if (error) {
                console.error("Error fetching blogs:", error);
            } else {
                const formattedPosts = (data || []).map((post: any) => ({
                    ...post,
                    author: {
                        name: post.author_name || "Digihub Team",
                        role: post.author_role || "Editor",
                        avatar: post.author_avatar
                    },
                    readTime: post.read_time,
                    publishedAt: post.published_at,
                    image: post.image || null,
                    // If category is missing in DB, try to infer or default
                    category: post.category || "General"
                }));
                setPosts(formattedPosts);
            }
            setLoading(false);
        };

        fetchBlogs();
    }, []);

    const filteredPosts = posts.filter((post) => {
        const matchesCategory = selectedCategory === "All" || post.category === selectedCategory;
        const matchesSearch = (post.title?.toLowerCase().includes(searchQuery.toLowerCase()) ||
            post.excerpt?.toLowerCase().includes(searchQuery.toLowerCase()));
        return matchesCategory && matchesSearch;
    });

    const featuredPost = filteredPosts.find(post => post.featured) || filteredPosts[0];
    const regularPosts = filteredPosts.filter(post => post.id !== featuredPost?.id);
    const visiblePosts = regularPosts.slice(0, visibleCount);

    // Check if we need to insert the newsletter CTA
    // We want to insert it after the 6th item (or close to middle if fewer)
    const renderPosts = () => {
        const items = [];
        let newsletterInserted = false;

        for (let i = 0; i < visiblePosts.length; i++) {
            items.push(
                <div key={visiblePosts[i].slug} className="h-full">
                    <BlogCard post={visiblePosts[i] as any} />
                </div>
            );

            // Insert Newsletter CTA after 6 items
            if (i === 5 && !newsletterInserted) {
                items.push(<NewsletterCTA key="newsletter-cta" />);
                newsletterInserted = true;
            }
        }

        // If we have fewer than 6 items but more than 2, maybe put it at the end? 
        // Or just stick to the rule: "Mid-grid". If grid is small, maybe don't show or show at bottom.
        // Let's just stick to the loop insertion for now.

        return items;
    };


    return (
        <main className="min-h-screen bg-[#050505] text-white pt-24 pb-20">
            <Container className="max-w-[1280px]">
                {/* 1. FEATURED HERO POST */}
                {loading ? (
                    <div className="w-full h-[400px] md:h-[500px] bg-white/5 animate-pulse rounded-[2rem] mb-20" />
                ) : featuredPost ? (
                    <div className="relative w-full h-[400px] md:h-[500px] rounded-[2rem] overflow-hidden mb-16 group border border-white/5">
                        {/* Background Image */}
                        {featuredPost.image ? (
                            <img
                                src={featuredPost.image}
                                alt={featuredPost.title}
                                className="absolute inset-0 w-full h-full object-cover transition-transform duration-700 group-hover:scale-105"
                            />
                        ) : (
                            <div className="absolute inset-0 bg-gradient-to-br from-blue-900 via-slate-900 to-black" />
                        )}

                        {/* Gradient Overlay */}
                        <div className="absolute inset-0 bg-gradient-to-t from-black via-black/50 to-transparent" />

                        {/* Content Overlay */}
                        <div className="absolute bottom-0 left-0 w-full p-8 md:p-12 flex flex-col items-start justify-end h-full">
                            <div className="flex items-center gap-3 mb-4">
                                <span className="text-xs font-bold text-black bg-white px-3 py-1 rounded-full uppercase tracking-wider">
                                    {featuredPost.category || "Featured"}
                                </span>
                                {featuredPost.readTime && (
                                    <span className="text-xs font-medium text-white/80 bg-black/40 backdrop-blur-md px-3 py-1 rounded-full border border-white/10">
                                        {featuredPost.readTime}
                                    </span>
                                )}
                            </div>

                            <h1 className="text-3xl md:text-5xl font-bold mb-4 max-w-4xl leading-tight">
                                {featuredPost.title}
                            </h1>
                            <p className="text-lg text-gray-300 max-w-2xl mb-8 leading-relaxed line-clamp-2 md:line-clamp-3">
                                {featuredPost.excerpt}
                            </p>

                            <div className="flex items-center justify-between w-full max-w-2xl">
                                <Link href={`/blog/${featuredPost.slug}`} className="flex items-center gap-2 text-white font-semibold bg-primary/90 hover:bg-primary px-6 py-3 rounded-full transition-all shadow-lg hover:shadow-primary/25">
                                    Read Article <ArrowUpRight className="w-4 h-4" />
                                </Link>

                                {/* Author Info in Hero */}
                                <div className="hidden md:flex items-center gap-3">
                                    {featuredPost.author?.avatar && (
                                        <img src={featuredPost.author.avatar} alt="Author" className="w-10 h-10 rounded-full border border-white/20" />
                                    )}
                                    <div className="flex flex-col">
                                        <span className="text-sm font-semibold">{featuredPost.author?.name}</span>
                                        <span className="text-xs text-gray-400">{featuredPost.author?.role}</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                ) : null}

                {/* 2. FILTERS & SEARCH */}
                <BlogFilters
                    categories={categories}
                    selectedCategory={selectedCategory}
                    setSelectedCategory={setSelectedCategory}
                    searchQuery={searchQuery}
                    setSearchQuery={setSearchQuery}
                />

                {/* 3. POSTS GRID */}
                <div className="mb-24">
                    {/* Section Header */}
                    <div className="flex items-end justify-between mb-8">
                        <div>
                            <h2 className="text-2xl font-bold mb-2">Latest from the blog</h2>
                            <p className="text-gray-400">Insights, strategies, and updates from the Digihub team.</p>
                        </div>
                        <div className="hidden md:block text-sm text-gray-500">
                            Showing {visiblePosts.length} of {regularPosts.length} articles
                        </div>
                    </div>

                    {loading ? (
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-x-8 gap-y-12">
                            {[1, 2, 3, 4, 5, 6].map(i => <BlogCardSkeleton key={i} />)}
                        </div>
                    ) : (
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-x-8 gap-y-12">
                            {visiblePosts.length > 0 ? (
                                renderPosts()
                            ) : (
                                <div className="col-span-3 py-20 text-center">
                                    <p className="text-2xl font-bold text-gray-500 mb-2">No articles found</p>
                                    <p className="text-gray-600">Try adjusting your search or category filter.</p>
                                </div>
                            )}
                        </div>
                    )}

                    {/* Load More Button */}
                    {regularPosts.length > visibleCount && (
                        <div className="mt-16 flex justify-center pt-8">
                            <Button
                                variant="outline"
                                onClick={() => setVisibleCount(prev => prev + 6)}
                                className="rounded-full px-8 py-6 h-auto text-base border-white/10 hover:bg-white/5 gap-2 group min-w-[200px]"
                            >
                                Load more articles <Loader2 className="w-4 h-4 group-hover:animate-spin" />
                            </Button>
                        </div>
                    )}
                </div>

                {/* 4. FOOTER CTA */}
                <div className="bg-gradient-to-b from-[#0F141A] to-black rounded-[2.5rem] p-12 md:p-24 text-center border border-white/5 relative overflow-hidden">
                    <div className="relative z-10 max-w-3xl mx-auto">
                        <span className="text-primary font-semibold mb-4 block">Ready to grow?</span>
                        <h2 className="text-4xl md:text-5xl font-bold mb-6 tracking-tight">Transform your digital presence today</h2>
                        <p className="text-xl text-gray-400 mb-10 max-w-2xl mx-auto">
                            Get a free comprehensive growth audit and see exactly how we can help you scale your revenue.
                        </p>
                        <div className="flex flex-col sm:flex-row items-center justify-center gap-4">
                            <Link href="/contact" className="w-full sm:w-auto">
                                <Button className="w-full sm:w-auto rounded-full h-14 px-10 text-base bg-white text-black hover:bg-gray-200 font-semibold shadow-[0_0_20px_rgba(255,255,255,0.2)] hover:shadow-[0_0_30px_rgba(255,255,255,0.3)] transition-all">
                                    Get your free audit
                                </Button>
                            </Link>
                            <Link href="/contact" className="w-full sm:w-auto">
                                <Button variant="outline" className="w-full sm:w-auto rounded-full h-14 px-10 text-base border-white/10 hover:bg-white/5">
                                    Talk to an expert
                                </Button>
                            </Link>
                        </div>
                    </div>
                    {/* Background decoration */}
                    <div className="absolute top-0 left-0 w-full h-full bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-primary/5 via-transparent to-transparent opacity-60" />
                </div>
            </Container>
        </main>
    );
}

import { notFound } from "next/navigation";
import { servicesData } from "@/data/services";
import { Container, Section } from "@/components/ui/container";
import { Button } from "@/components/ui/button";
import { Check, ArrowRight, XCircle, BrainCircuit } from "lucide-react";
import Link from "next/link";
import { Metadata } from "next";
import { createClient, createAdminClient } from "@/lib/supabase/server";
import { ConsultationFormSection } from "@/components/sections/ConsultationForm";
import { PerformanceMarketingPage } from "@/components/services/PerformanceMarketingPage";
import { SEOPage } from "@/components/services/SEOPage";
import { SocialMediaMarketingPage } from "@/components/services/SocialMediaMarketingPage";
import { ContentMarketingPage } from '@/components/services/ContentMarketingPage';
import { ServicePricing } from "@/components/sections/ServicePricing";
import { InfluencerMarketingPage } from "@/components/services/InfluencerMarketingPage";
import { AIMarketingPage } from "@/components/services/AIMarketingPage";
import { PersonalBrandingPage } from "@/components/services/PersonalBrandingPage";
import { EmailMarketingPage } from "@/components/services/EmailMarketingPage";



export const revalidate = 3600;

// Next.js 15+ Page Props Interface
interface Props {
    params: Promise<{ slug: string }>;
}

async function getService(slug: string) {
    const supabase = await createClient();

    // 1. Fetch Service Details
    const { data: service } = await supabase
        .from('services')
        .select('*')
        .eq('slug', slug)
        .eq('status', 'Published')
        .eq('is_deleted', false)
        .single();

    if (!service) return null;

    // 2. Fetch Dynamic Pricing Plans
    const { data: dbPlans } = await supabase
        .from('pricing_plans')
        .select(`
            *,
            features:plan_features(*)
        `)
        .eq('service_id', service.id)
        .eq('is_active', true)
        .order('sort_order', { ascending: true }); // or order by price

    // Map snake_case DB fields to camelCase for the component
    // Supabase returns JSON columns as objects usually, but sometimes as strings if not typed
    const benefits = typeof service.benefits === 'string' ? JSON.parse(service.benefits) : service.benefits;
    const problems = typeof service.problems === 'string' ? JSON.parse(service.problems) : service.problems;
    const approach = typeof service.approach === 'string' ? JSON.parse(service.approach) : service.approach;
    const tools = typeof service.tools === 'string' ? JSON.parse(service.tools) : service.tools;
    const outcomes = typeof service.outcomes === 'string' ? JSON.parse(service.outcomes) : service.outcomes;
    const industries = typeof service.industries === 'string' ? JSON.parse(service.industries) : service.industries;
    const pricing = typeof service.pricing === 'string' ? JSON.parse(service.pricing) : service.pricing;
    const faq = typeof service.faq === 'string' ? JSON.parse(service.faq) : service.faq;

    return {
        ...service,
        metaTitle: service.meta_title,
        metaDesc: service.meta_desc,
        heroText: service.hero_text,
        whyMatters: service.why_matters,
        benefits: benefits || [],
        problems: problems || [],
        approach: approach || [],
        tools: tools || [],
        outcomes: outcomes || [],
        industries: industries || [],
        pricing: pricing || {},
        faq: faq || [],
        // Attach dynamic plans
        dynamicPlans: dbPlans || []
    };
}

export async function generateMetadata({ params }: Props): Promise<Metadata> {
    const { slug } = await params;

    // Custom metadata for Performance Marketing page
    if (slug === 'performance') {
        return {
            title: "Performance Marketing Services - ROI-Focused Campaigns | Digihub",
            description: "Data-driven Google & Meta ad campaigns focused on ROI, not vanity metrics. Get qualified leads with 3-5x ROAS. Free performance audit available.",
        };
    }


    if (slug === 'seo') {
        return {
            title: "SEO Services - Build Trust with Google | Digihub",
            description: "Your website exists. Google just doesn't trust it yet. Strategic SEO built on search intent, structure, and authority. 4-6 month realistic timeline.",
        };
    }

    if (slug === 'social') {
        return {
            title: "Social Media Marketing - Build A Cult Brand | Digihub",
            description: "Stop shouting into the void. Build a loyal community that buys. Premium social media management, content strategy, and brand building.",
        };
    }

    if (slug === 'content-marketing') {
        return {
            title: "Content Marketing - Build Authority, Not Noise | Digihub",
            description: "Break through the AI slop. Expert-led content strategies that build trust, authority, and inbound leads. Blogs, whitepapers, and more.",
        };
    }

    if (slug === 'influencer') {
        return {
            title: "Influencer Marketing - Turn Views into Trust | Digihub",
            description: "Don't just buy posts, buy influence. Connect with 500+ vetted creators to drive authentic brand growth. Data-driven campaigns, not guesswork.",
        };
    }

    if (slug === 'ai-marketing') {
        return {
            title: "AI Marketing Services - The Neural Edge | Digihub",
            description: "Replace guesswork with neural intelligence. Predictive analytics, automated creative, and hyper-personalization at scale.",
        };
    }

    if (slug === 'personal-branding') {
        return {
            title: "Expert Personal Branding Services for Founders & Executives | Digihub",
            description: "Build a high-value personal brand that attracts clients, media & inbound opportunities. Authority building, ghostwriting & PR for executives.",
            keywords: ["personal branding services", "executive branding", "founder personal branding", "linkedin personal branding", "personal brand agency India"]
        };
    }

    if (slug === 'email' || slug === 'email-marketing') {
        return {
            title: "Email Marketing Services - 30% Higher Open Rates | Digihub",
            description: "Turn every email into a growth channel with our managed email marketing services. Automated campaigns, high deliverability, and stunning designs.",
            keywords: ["email marketing", "newsletter services", "email automation", "klaviyo agency", "email list growth"]
        };
    }

    const service = await getService(slug);
    if (!service) return {};

    return {
        title: service.metaTitle,
        description: service.metaDesc,
    };
}




export async function generateStaticParams() {
    // Generate params only for PUBLISHED services
    // Use Admin Client to bypass cookie check (since this runs at build time / static generation)
    const supabase = createAdminClient();
    const { data } = await supabase
        .from('services')
        .select('slug')
        .eq('status', 'Published')
        .eq('is_deleted', false);

    return (data || []).map((service: any) => ({
        slug: service.slug,
    }));
}

export default async function ServicePage({ params }: Props) {
    const { slug } = await params;

    // 1. Always attempt to fetch service data first to get dynamic pricing
    const service = await getService(slug);

    // 2. Prepare standardized plans for the UI
    let finalPlans: any[] = [];

    if (service) {
        const plans = service.dynamicPlans;
        const pricing = service.pricing;

        finalPlans = (plans && plans.length > 0) ? plans : [
            {
                id: 'static-starter',
                title: 'Starter Plan',
                price: pricing?.starter?.price?.replace(/,/g, '').replace(/[^\d.]/g, '') || "9999",
                currency: 'INR',
                billing_cycle: 'month',
                is_popular: false,
                features: pricing?.starter?.features || []
            },
            {
                id: 'static-growth',
                title: 'Growth Plan',
                price: pricing?.growth?.price?.replace(/,/g, '').replace(/[^\d.]/g, '') || "24999",
                currency: 'INR',
                billing_cycle: 'month',
                is_popular: true,
                features: pricing?.growth?.features || []
            },
            {
                id: 'static-scale',
                title: 'Scale Plan',
                price: 0,
                currency: 'INR',
                billing_cycle: 'month',
                is_popular: false,
                features: pricing?.scale?.features || []
            }
        ];
    }

    // Use custom Performance Marketing page for 'performance' slug
    if (slug === 'performance') {
        return <PerformanceMarketingPage plans={finalPlans} />;
    }

    // Use custom SEO page for 'seo' slug
    if (slug === 'seo') {
        return <SEOPage plans={finalPlans} />;
    }

    // Use custom Social Media Marketing page for 'social' slug
    if (slug === 'social') {
        return <SocialMediaMarketingPage plans={finalPlans} />;
    }

    // Use custom Content Marketing page for 'content-marketing' slug
    if (slug === 'content-marketing') {
        return <ContentMarketingPage plans={finalPlans} />;
    }

    // Use custom Influencer Marketing page for 'influencer' slug
    if (slug === 'influencer') {
        return <InfluencerMarketingPage />;
    }

    // Use custom AI Marketing page for 'ai-marketing' slug
    if (slug === 'ai-marketing') {
        return <AIMarketingPage plans={finalPlans} />;
    }

    if (slug === 'personal-branding') {
        return <PersonalBrandingPage plans={finalPlans} />;
    }

    if (slug === 'email' || slug === 'email-marketing') {
        return <EmailMarketingPage plans={finalPlans} />;
    }

    // If no custom page and no DB service, 404
    if (!service) {
        notFound();
    }

    // Default values for new fields if not present
    const description = service.description || `Digihub Solutions delivers AI-powered ${service.name} services designed to help businesses improve visibility, generate high-quality leads, and scale profitably using data-driven strategies.`;
    const benefits = service.benefits || [
        "Increased online visibility",
        "Better-quality leads",
        "Improved conversion rates",
        "Lower customer acquisition cost",
        "Long-term scalable growth"
    ];
    const industries = service.industries || [
        "Startups", "SMEs", "E-commerce brands", "Service-based businesses", "Local & global companies"
    ];
    // pricing object is already extracted above but used for static fallback logic

    // Use dynamic plans if available, otherwise arrayify the static object for mapping (rough fallback)
    // Actually, distinct logic is better.
    const plans = service.dynamicPlans && service.dynamicPlans.length > 0
        ? service.dynamicPlans
        : null;

    const problems = service.problems || ["Low traffic", "Poor conversion", "Wasted ad budget"];
    const approach = service.approach || [
        { step: "Audit", desc: "We analyze your current status." },
        { step: "Strategy", desc: "We build a custom roadmap." },
        { step: "Execution", desc: "We implement the plan." },
        { step: "Scale", desc: "We optimize for growth." }
    ];
    const tools = service.tools || ["Google Analytics", "AI Tools", "SEMrush", "React"];
    const outcomes = service.outcomes || ["Higher Traffic", "More Leads", "Better ROI"];
    const faq = service.faq || [
        { q: `What is included in ${service.name}?`, a: "We provide end-to-end management and optimization." },
        { q: "How long to see results?", a: "Typically 3-6 months for significant impact." }
    ];


    return (
        <main className="min-h-screen bg-[#0B0F14] text-text-primary pt-20">

            {/* 2 SERVICE HERO SECTION (H1) */}
            <Section className="relative py-24 md:py-32 overflow-hidden bg-[#0B0F14]">
                <div className="absolute inset-0 bg-gradient-to-b from-primary/5 to-transparent opacity-50" />
                <Container className="relative z-10 text-center max-w-4xl">
                    <div className="inline-block px-4 py-1.5 rounded-full border border-primary/20 bg-primary/10 text-primary text-sm font-mono tracking-widest uppercase mb-6">
                        {service.name} Services
                    </div>
                    <h1 className="text-4xl md:text-6xl font-bold text-white mb-6 leading-tight">
                        {service.title} That Drive Measurable Growth
                    </h1>
                    <p className="text-xl text-text-muted mb-10 leading-relaxed max-w-3xl mx-auto">
                        {description}
                    </p>
                    <div className="flex justify-center gap-4">
                        <Link href="/contact">
                            <Button size="lg" className="h-12 px-8 shadow-[0_0_20px_-5px_var(--color-primary)] transition-all">Get Free Consultation</Button>
                        </Link>
                        <Link href="/contact">
                            <Button size="lg" variant="outline" className="h-12 px-8 border-white/10 hover:bg-white/5 transition-all">Talk to an Expert</Button>
                        </Link>
                    </div>
