"use client";

import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { cn } from "@/lib/utils";
import {
    Users,
    TrendingUp,
    Clock,
    Activity,
    DollarSign,
    ArrowUpRight,
    ArrowDownRight,
    MoreVertical,
    Calendar,
    Download,
    RefreshCw
} from "lucide-react";

// Generate realistic demo data
const generateMonthlyData = () => {
    const months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
    return months.map((month, index) => ({
        month,
        value: Math.floor(Math.random() * 400) + 100,
        users: Math.floor(Math.random() * 5000) + 1000,
        revenue: Math.floor(Math.random() * 50000) + 10000
    }));
};

const generateWeeklyData = () => {
    const weeks = ["Week 1", "Week 2", "Week 3", "Week 4"];
    return weeks.map((week) => ({
        month: week,
        value: Math.floor(Math.random() * 300) + 150,
        users: Math.floor(Math.random() * 2000) + 500,
        revenue: Math.floor(Math.random() * 20000) + 5000
    }));
};

export default function AnalyticsPage() {
    const [timeRange, setTimeRange] = useState<"week" | "month">("month");
    const [viewType, setViewType] = useState<"default" | "monthly" | "quarterly" | "yearly">("default");
    const [selectedMonth, setSelectedMonth] = useState("August");
    const [isRefreshing, setIsRefreshing] = useState(false);
    const [statisticsData, setStatisticsData] = useState(generateMonthlyData());

    // Simulate real-time updates
    useEffect(() => {
        const interval = setInterval(() => {
            // Update active users count randomly
            setStats(prev => prev.map(stat =>
                stat.label === "Active Now"
                    ? { ...stat, value: `${Math.floor(Math.random() * 20) + 40}k` }
                    : stat
            ));
        }, 5000);

        return () => clearInterval(interval);
    }, []);

    // Handle time range change
    useEffect(() => {
        if (timeRange === "week") {
            setStatisticsData(generateWeeklyData());
        } else {
            setStatisticsData(generateMonthlyData());
        }
    }, [timeRange]);

    const [stats, setStats] = useState([
        {
            label: "Total Users",
            value: "993k",
            change: "+25%",
            trending: "up",
            color: "from-blue-500 to-blue-600",
            bgColor: "bg-blue-500/10",
            icon: Users,
            description: "Total registered users"
        },
        {
            label: "New Users",
            value: "13k",
            change: "+4.3%",
            trending: "up",
            color: "from-gray-700 to-gray-800",
            bgColor: "bg-white/5",
            icon: TrendingUp,
            description: "New users this month"
        },
        {
            label: "Avg. Time on Platform",
            value: "00:14:45",
            change: "-7.0%",
            trending: "down",
            color: "from-gray-700 to-gray-800",
            bgColor: "bg-white/5",
            icon: Clock,
            description: "Average session duration"
        },
        {
            label: "Active Now",
            value: "49k",
            change: "+0.4%",
            trending: "up",
            color: "from-gray-700 to-gray-800",
            bgColor: "bg-white/5",
            icon: Activity,
            description: "Currently active users"
        }
    ]);

    const maxValue = Math.max(...statisticsData.map(d => d.value));

    const userExploreData = [
        {
            label: "Home Page",
            sessions: "32.50%",
            avgTime: "00:08:23",
            bounceRate: "18.24%",
            transactions: 85.5,
            pageViews: "125.4k"
        },
        {
            label: "Services",
            sessions: "28.30%",
            avgTime: "00:12:15",
            bounceRate: "22.18%",
            transactions: 78.2,
            pageViews: "98.2k"
        },
        {
            label: "Blog",
            sessions: "17.50%",
            avgTime: "00:14:45",
            bounceRate: "24.48%",
            transactions: 75.5,
            pageViews: "76.3k"
        },
        {
            label: "Contact",
            sessions: "12.40%",
            avgTime: "00:05:32",
            bounceRate: "45.67%",
            transactions: 62.8,
            pageViews: "54.1k"
        },
        {
            label: "About",
            sessions: "9.30%",
            avgTime: "00:06:18",
            bounceRate: "38.92%",
            transactions: 58.3,
            pageViews: "41.7k"
        }
    ];

    const topReferrers = [
        { source: "Google Search", visits: "45.2k", percentage: 42 },
        { source: "Direct", visits: "32.8k", percentage: 31 },
        { source: "Social Media", visits: "18.5k", percentage: 17 },
        { source: "Email Campaign", visits: "10.2k", percentage: 10 }
    ];

    const deviceBreakdown = [
        { device: "Desktop", percentage: 58, color: "bg-blue-500" },
        { device: "Mobile", percentage: 35, color: "bg-teal-500" },
        { device: "Tablet", percentage: 7, color: "bg-purple-500" }
    ];

    const handleRefresh = () => {
        setIsRefreshing(true);
        setStatisticsData(timeRange === "week" ? generateWeeklyData() : generateMonthlyData());
        setTimeout(() => setIsRefreshing(false), 1000);
    };

    const handleExport = () => {
        // Simulate data export
        const dataStr = JSON.stringify(statisticsData, null, 2);
        const dataBlob = new Blob([dataStr], { type: 'application/json' });
        const url = URL.createObjectURL(dataBlob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `analytics-${timeRange}-${Date.now()}.json`;
        link.click();
    };

    return (
        <div className="max-w-[1400px] mx-auto">
