"use client";

import { useState, useEffect, useMemo } from "react";
import Link from "next/link";
import { Button } from "@/components/ui/button";
import { createClient } from "@/lib/supabase/client";
import {
    Eye, EyeOff, Loader2, Plus, Pencil, Trash2, Search,
    Filter, MoreHorizontal, CheckSquare, BarChart,
    TrendingUp, Calendar, AlertCircle, CheckCircle2, ChevronDown
} from "lucide-react";
import { BlogPost } from "@/types";
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuLabel,
    DropdownMenuSeparator,
    DropdownMenuTrigger,
    DropdownMenuCheckboxItem,
} from "@/components/ui/dropdown-menu";
import { Badge } from "@/components/ui/badge";

// Extended type for UI purposes (mocking SEO/Stats for now)
interface ExtendedBlog extends BlogPost {
    seo_score?: number;
    views?: number;
    featured?: boolean;
}

export default function AdminBlogs() {
    const [blogs, setBlogs] = useState<ExtendedBlog[]>([]);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState("");
    const [statusFilter, setStatusFilter] = useState<string>("all");
    const [selectedIds, setSelectedIds] = useState<Set<string>>(new Set());
    const [viewMode, setViewMode] = useState<"list" | "grid">("list");

    const supabase = createClient();

    useEffect(() => {
        fetchBlogs();
    }, []);

    const fetchBlogs = async () => {
        setLoading(true);
        const { data, error } = await supabase
            .from('blogs')
            .select('*')
            .order('created_at', { ascending: false });

        if (error) {
            console.error("Failed to fetch blogs", error);
        } else {
            // Mocking some extra data for the demo of "Enterprise Features"
            const enhancedData = (data || []).map((blog: any) => ({
                ...blog,
                seo_score: Math.floor(Math.random() * 40) + 60, // Mock score 60-100
                views: Math.floor(Math.random() * 1000) + 100, // Mock views
                featured: blog.featured || false
            }));
            setBlogs(enhancedData);
        }
        setLoading(false);
    };

    const toggleStatus = async (id: string, currentStatus: string) => {
        const newStatus = currentStatus === 'published' ? 'draft' : 'published';
        const publishedAt = newStatus === 'published' ? new Date().toISOString() : null;

        setBlogs(prev => prev.map(b =>
            b.id === id ? { ...b, status: newStatus as any, published_at: publishedAt || undefined } : b
        ));

        await supabase.from('blogs').update({ status: newStatus, published_at: publishedAt }).eq('id', id);
    };

    const handleDelete = async (id: string) => {
        if (!confirm("Are you sure you want to delete this blog post? This cannot be undone.")) return;

        setBlogs(prev => prev.filter(b => b.id !== id));
        await supabase.from('blogs').delete().eq('id', id);
        setSelectedIds(prev => {
            const next = new Set(prev);
            next.delete(id);
            return next;
        });
    };

    const handleBulkDelete = async () => {
        if (!confirm(`Delete ${selectedIds.size} posts? This cannot be undone.`)) return;

        const ids = Array.from(selectedIds);
        setBlogs(prev => prev.filter(b => !selectedIds.has(b.id)));
        await supabase.from('blogs').delete().in('id', ids);
        setSelectedIds(new Set());
    };

    const handleBulkPublish = async (status: 'published' | 'draft') => {
        const ids = Array.from(selectedIds);
        const publishedAt = status === 'published' ? new Date().toISOString() : null;

        setBlogs(prev => prev.map(b =>
            selectedIds.has(b.id) ? { ...b, status: status, published_at: publishedAt || undefined } : b
        ));

        await supabase.from('blogs').update({ status: status, published_at: publishedAt }).in('id', ids);
        setSelectedIds(new Set());
    };

    const toggleSelection = (id: string) => {
        setSelectedIds(prev => {
            const next = new Set(prev);
            if (next.has(id)) next.delete(id);
            else next.add(id);
            return next;
        });
    };

    const selectAll = () => {
        if (selectedIds.size === filteredBlogs.length) {
            setSelectedIds(new Set());
        } else {
            setSelectedIds(new Set(filteredBlogs.map(b => b.id)));
        }
    };

    const filteredBlogs = useMemo(() => {
        return blogs.filter(blog => {
            const matchesSearch = blog.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                blog.slug.toLowerCase().includes(searchQuery.toLowerCase());
            const matchesStatus = statusFilter === "all" || blog.status === statusFilter;
            return matchesSearch && matchesStatus;
        });
    }, [blogs, searchQuery, statusFilter]);

    // Stats
    const stats = useMemo(() => ({
        total: blogs.length,
        published: blogs.filter(b => b.status === "published").length,
        views: blogs.reduce((acc, b) => acc + (b.views || 0), 0)
    }), [blogs]);

    return (
        <div className="flex flex-col h-full w-full bg-[#0B0F14]">
            <div className="flex flex-col h-full overflow-hidden">
                {/* Header */}
                <div className="p-6 md:p-8 border-b border-white/5 bg-[#0F141A]/50 backdrop-blur-sm">
                    <div className="max-w-7xl mx-auto flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
                        <div>
                            <h1 className="text-2xl font-bold text-white font-poppins">Blog Management</h1>
                            <div className="flex items-center gap-4 mt-2 text-sm text-gray-400">
                                <span className="flex items-center gap-1"><CheckCircle2 size={14} className="text-green-400" /> {stats.published} Published</span>
                                <span className="flex items-center gap-1"><Pencil size={14} className="text-yellow-400" /> {stats.total - stats.published} Drafts</span>
                                <span className="flex items-center gap-1"><Eye size={14} className="text-blue-400" /> {stats.views.toLocaleString()} Views</span>
                            </div>
                        </div>
                        <Link href="/admin/blogs/new">
                            <Button className="gap-2 bg-primary text-black hover:bg-primary/90 font-medium">
                                <Plus size={18} /> Write New Blog
                            </Button>
                        </Link>
                    </div>
                </div>

                {/* Toolbar */}
                <div className="p-4 md:px-8 border-b border-white/5 bg-[#0B0F14]">
                    <div className="max-w-7xl mx-auto flex flex-col md:flex-row gap-4 items-start md:items-center justify-between">
                        {/* Search & Filter */}
                        <div className="flex items-center gap-3 w-full md:flex-1">
                            <div className="relative flex-1 md:max-w-md">
                                <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-500" size={16} />
                                <input
                                    type="text"
                                    placeholder="Search by title..."
                                    value={searchQuery}
                                    onChange={(e) => setSearchQuery(e.target.value)}
                                    className="w-full bg-[#0F141A] border border-white/10 rounded-lg pl-10 pr-4 py-2.5 text-sm text-white placeholder:text-gray-500 focus:outline-none focus:border-primary/50 transition-colors"
                                />
                            </div>
                            <DropdownMenu>
                                <DropdownMenuTrigger asChild>
                                    <Button variant="outline" size="default" className="gap-2 border-white/10 text-gray-300 hover:text-white hover:border-white/20 min-w-[140px] justify-between">
                                        <div className="flex items-center gap-2">
                                            <Filter size={16} />
                                            <span className="text-sm">
                                                {statusFilter === 'all' ? 'All Status' : statusFilter === 'published' ? 'Published' : 'Drafts'}
                                            </span>
                                        </div>
                                        <ChevronDown size={14} className="opacity-50" />
                                    </Button>
                                </DropdownMenuTrigger>
                                <DropdownMenuContent align="start" className="w-40 bg-[#0F141A] border-white/10">
                                    <DropdownMenuItem onClick={() => setStatusFilter('all')} className="cursor-pointer">
                                        All Posts
                                    </DropdownMenuItem>
                                    <DropdownMenuItem onClick={() => setStatusFilter('published')} className="cursor-pointer">
                                        Published
                                    </DropdownMenuItem>
                                    <DropdownMenuItem onClick={() => setStatusFilter('draft')} className="cursor-pointer">
                                        Drafts
                                    </DropdownMenuItem>
                                </DropdownMenuContent>
                            </DropdownMenu>
                        </div>

                        {/* Bulk Actions (Conditional) */}
                        {selectedIds.size > 0 && (
                            <div className="flex items-center gap-2 animate-in fade-in slide-in-from-top-2">
                                <span className="text-sm text-gray-400 mr-2">{selectedIds.size} selected</span>
                                <Button size="sm" variant="secondary" onClick={() => handleBulkPublish('published')} className="text-xs">
                                    Publish
                                </Button>
                                <Button size="sm" variant="secondary" onClick={() => handleBulkPublish('draft')} className="text-xs">
                                    Unpublish
                                </Button>
                                <Button size="sm" variant="destructive" onClick={handleBulkDelete} className="text-xs">
                                    Delete
                                </Button>
                            </div>
                        )}
                    </div>
                </div>

                {/* Custom Scrollbar Styles */}
                <style jsx global>{`
                    .custom-scrollbar::-webkit-scrollbar {
                        width: 8px;
                        height: 8px;
                    }
                    .custom-scrollbar::-webkit-scrollbar-track {
                        background: rgba(255, 255, 255, 0.02);
                    }
                    .custom-scrollbar::-webkit-scrollbar-thumb {
                        background: rgba(255, 255, 255, 0.2);
                        border-radius: 4px;
                        border: 2px solid rgba(0, 0, 0, 0); /* Creates padding effect */
                        background-clip: padding-box;
                    }
                    .custom-scrollbar::-webkit-scrollbar-thumb:hover {
                        background: rgba(255, 255, 255, 0.4);
                        border: 2px solid rgba(0, 0, 0, 0);
                        background-clip: padding-box;
                    }
                `}</style>

                {/* Content */}
                <div className="flex-1 overflow-auto p-4 md:px-6 md:py-6 custom-scrollbar">
                    <div className="max-w-7xl mx-auto">
                        {loading ? (
                            <div className="flex flex-col items-center justify-center py-20 text-gray-500">
                                <Loader2 className="animate-spin text-primary w-8 h-8 mb-4" />
                                <p>Loading articles...</p>
                            </div>
                        ) : (
                            <div className="bg-[#0F141A] border border-white/5 rounded-xl overflow-hidden shadow-xl">
                                <table className="w-full text-left text-sm">
                                    <thead className="bg-white/[0.02] text-white/60 font-medium">
                                        <tr className="border-b border-white/5">
                                            <th className="p-4 w-12 text-center">
                                                <input
                                                    type="checkbox"
                                                    className="rounded border-white/20 bg-transparent text-primary focus:ring-primary/20 cursor-pointer"
                                                    onChange={selectAll}
                                                    checked={filteredBlogs.length > 0 && selectedIds.size === filteredBlogs.length}
                                                />
                                            </th>
                                            <th className="p-4 min-w-[300px]">Article</th>
                                            <th className="p-4">Status</th>
                                            <th className="p-4">SEO Score</th>
                                            <th className="p-4 text-right">Views</th>
                                            <th className="p-4 text-right">Last Updated</th>
                                            <th className="p-4 text-center">Actions</th>
                                        </tr>
                                    </thead>
                                    <tbody className="divide-y divide-white/5">
                                        {filteredBlogs.length > 0 ? filteredBlogs.map((blog) => (
                                            <tr key={blog.id} className={`group transition-colors ${selectedIds.has(blog.id) ? "bg-primary/5" : "hover:bg-white/[0.02]"}`}>
                                                <td className="p-4 text-center">
                                                    <input
                                                        type="checkbox"
                                                        className="rounded border-white/20 bg-transparent text-primary focus:ring-primary/20 cursor-pointer"
                                                        checked={selectedIds.has(blog.id)}
                                                        onChange={() => toggleSelection(blog.id)}
                                                    />
                                                </td>
                                                <td className="p-4">
                                                    <div className="flex flex-col">
                                                        <Link href={`/admin/blogs/${blog.id}`} className="font-medium text-white hover:text-primary transition-colors line-clamp-1 text-base mb-1">
                                                            {blog.title}
                                                        </Link>
                                                        <div className="flex items-center gap-2 text-xs text-gray-500">
                                                            <span className="bg-white/5 px-2 py-0.5 rounded text-gray-400 border border-white/5 capitalize">{blog.type}</span>
                                                            {blog.featured && <span className="text-yellow-500 flex items-center gap-1"><TrendingUp size={10} /> Featured</span>}
                                                            <span> {blog.slug}</span>
                                                        </div>
                                                    </div>
                                                </td>
                                                <td className="p-4">
                                                    <Badge variant="outline" className={`${blog.status === 'published' ? "bg-green-500/10 text-green-400 border-green-500/20" : "bg-yellow-500/10 text-yellow-400 border-yellow-500/20"
                                                        }`}>
                                                        {blog.status}
                                                    </Badge>
                                                </td>
                                                <td className="p-4">
                                                    <div className="flex items-center gap-2">
                                                        <div className="w-16 h-1.5 bg-white/10 rounded-full overflow-hidden">
                                                            <div
                                                                className={`h-full rounded-full ${(blog.seo_score || 0) > 80 ? "bg-green-500" : (blog.seo_score || 0) > 50 ? "bg-yellow-500" : "bg-red-500"
                                                                    }`}
                                                                style={{ width: `${blog.seo_score || 0}%` }}
                                                            />
                                                        </div>
                                                        <span className="text-xs text-gray-400">{blog.seo_score}/100</span>
                                                    </div>
                                                </td>
                                                <td className="p-4 text-right font-mono text-gray-400">
                                                    {(blog.views || 0).toLocaleString()}
                                                </td>
                                                <td className="p-4 text-right text-gray-500 text-xs">
                                                    {new Date(blog.created_at).toLocaleDateString()}
                                                </td>
                                                <td className="p-4 text-center">
                                                    <DropdownMenu>
                                                        <DropdownMenuTrigger asChild>
                                                            <Button variant="ghost" size="icon" className="h-8 w-8 text-gray-500 hover:text-white">
                                                                <MoreHorizontal size={16} />
                                                            </Button>
                                                        </DropdownMenuTrigger>
                                                        <DropdownMenuContent align="end" className="bg-[#0B0F14] border-white/10">
                                                            <DropdownMenuLabel>Actions</DropdownMenuLabel>
                                                            <DropdownMenuItem onClick={() => toggleStatus(blog.id, blog.status)}>
                                                                {blog.status === 'published' ? <><EyeOff size={14} className="mr-2" /> Unpublish</> : <><Eye size={14} className="mr-2" /> Publish</>}
                                                            </DropdownMenuItem>
                                                            <Link href={`/admin/blogs/${blog.id}`}>
                                                                <DropdownMenuItem>
                                                                    <Pencil size={14} className="mr-2" /> Edit Article
                                                                </DropdownMenuItem>
                                                            </Link>
                                                            <DropdownMenuSeparator className="bg-white/10" />
                                                            <DropdownMenuItem className="text-red-400 focus:text-red-400" onClick={() => handleDelete(blog.id)}>
                                                                <Trash2 size={14} className="mr-2" /> Delete
                                                            </DropdownMenuItem>
                                                        </DropdownMenuContent>
                                                    </DropdownMenu>
                                                </td>
                                            </tr>
                                        )) : (
                                            <tr>
                                                <td colSpan={7} className="p-16 text-center text-gray-500">
                                                    <div className="flex flex-col items-center gap-3">
                                                        <div className="p-4 rounded-full bg-white/5">
                                                            <Search size={24} />
                                                        </div>
                                                        <p className="text-lg font-medium text-white">No articles found</p>
                                                        <p className="text-sm">Try adjusting your search or filters.</p>
                                                    </div>
                                                </td>
                                            </tr>
                                        )}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect } from "react";
import AdminSidebar from "@/components/AdminSidebar";
import {
    Briefcase,
    Search,
    Plus,
    MoreVertical,
    TrendingUp,
    ExternalLink,
    Trash2,
    Edit3,
    Globe,
    Building2
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { motion, AnimatePresence } from "framer-motion";

export default function CaseStudiesManagement() {
    const [caseStudies, setCaseStudies] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState("");

    useEffect(() => {
        fetchCaseStudies();
    }, []);

    const fetchCaseStudies = async () => {
        try {
            const res = await fetch("/api/case-studies");
            if (res.ok) {
                const data = await res.json();
                setCaseStudies(data);
            }
        } catch (error) {
            console.error("Failed to fetch case studies", error);
        } finally {
            setLoading(false);
        }
    };

    const deleteCaseStudy = async (id: string) => {
        if (!confirm("Are you sure you want to delete this case study?")) return;
        try {
            const res = await fetch(`/api/case-studies?id=${id}`, { method: "DELETE" });
            if (res.ok) {
                setCaseStudies(caseStudies.filter(cs => cs.id !== id));
            }
        } catch (error) {
            console.error("Failed to delete case study", error);
        }
    };

    const filtered = caseStudies.filter(cs =>
        cs.client.toLowerCase().includes(searchTerm.toLowerCase()) ||
        cs.industry.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="flex h-screen bg-[#0B0F14]">
            <AdminSidebar />
            <main className="flex-1 md:ml-64 p-4 md:p-8 overflow-y-auto">
                <div className="max-w-7xl mx-auto">
                    <div className="flex items-center justify-between mb-8">
                        <div>
                            <h1 className="text-3xl font-bold text-white font-poppins">Case Studies</h1>
                            <p className="text-gray-400 mt-1">Showcase your success stories and client results</p>
                        </div>
                        <Button className="gap-2">
                            <Plus size={18} />
                            Add Case Study
                        </Button>
                    </div>

                    <div className="bg-[#0F141A] border border-white/5 rounded-2xl p-4 mb-8 flex items-center gap-4">
                        <div className="relative flex-1 max-w-md">
                            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-500" size={18} />
                            <input
                                type="text"
                                placeholder="Search by client or industry..."
                                className="w-full pl-10 pr-4 py-2 bg-white/5 border border-white/10 rounded-xl text-white focus:outline-none focus:ring-2 focus:ring-primary/50"
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                            />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {loading ? (
                            <div className="col-span-2 text-center py-12 text-gray-500">Loading case studies...</div>
                        ) : filtered.length === 0 ? (
                            <div className="col-span-2 text-center py-12 text-gray-500">No case studies found</div>
                        ) : filtered.map((cs) => (
                            <motion.div
                                key={cs.id}
                                layout
                                initial={{ opacity: 0, scale: 0.95 }}
                                animate={{ opacity: 1, scale: 1 }}
                                className="bg-[#0F141A] border border-white/5 rounded-2xl p-6 group hover:border-primary/20 transition-all"
                            >
                                <div className="flex items-start justify-between mb-6">
                                    <div className="p-3 bg-primary/10 rounded-xl text-primary">
                                        <Briefcase size={24} />
                                    </div>
                                    <div className="flex gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                                        <button className="p-2 text-gray-400 hover:text-white bg-white/5 rounded-lg">
                                            <Edit3 size={18} />
                                        </button>
                                        <button
                                            onClick={() => deleteCaseStudy(cs.id)}
                                            className="p-2 text-gray-400 hover:text-red-400 bg-white/5 rounded-lg"
                                        >
                                            <Trash2 size={18} />
                                        </button>
                                    </div>
                                </div>

                                <h3 className="text-xl font-bold text-white mb-2">{cs.client}</h3>
                                <div className="flex items-center gap-4 text-xs text-gray-400 mb-6">
                                    <span className="flex items-center gap-1">
                                        <Building2 size={12} /> {cs.industry}
                                    </span>
                                    <span className="flex items-center gap-1">
                                        <Globe size={12} /> {cs.service}
                                    </span>
                                </div>

                                <div className="bg-white/5 rounded-xl p-4 border border-white/5">
                                    <div className="flex items-center gap-2 text-primary font-bold mb-1">
                                        <TrendingUp size={16} /> Result
                                    </div>
                                    <p className="text-sm text-gray-300">{cs.result}</p>
                                </div>

                                <div className="mt-6 flex items-center justify-between">
                                    <span className={cn(
                                        "text-[10px] font-bold uppercase py-1 px-3 rounded-full border",
                                        cs.status === "Published" ? "bg-green-500/10 text-green-400 border-green-500/20" : "bg-yellow-500/10 text-yellow-400 border-yellow-500/20"
                                    )}>
                                        {cs.status}
                                    </span>
                                    <button className="text-xs text-gray-500 hover:text-white flex items-center gap-1 transition-colors">
                                        Preview <ExternalLink size={12} />
                                    </button>
                                </div>
                            </motion.div>
                        ))}
                    </div>
                </div>
            </main>
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { MessageCircle, User, Mail, Phone, Globe, Target, TrendingUp, Calendar, Filter, Search } from 'lucide-react';
import { motion } from 'framer-motion';

interface ChatMessage {
    id: string;
    role: 'user' | 'assistant';
    content: string;
    created_at: string;
}

interface ChatConversation {
    id: string;
    session_id: string;
    visitor_name: string | null;
    visitor_email: string | null;
    visitor_phone: string | null;
    visitor_business: string | null;
    visitor_website: string | null;
    visitor_city: string | null;
    visitor_goal: string | null;
    status: 'active' | 'lead_captured' | 'closed';
    lead_score: number;
    created_at: string;
    updated_at: string;
    messages?: ChatMessage[];
}

export default function ChatConversationsPage() {
    const [conversations, setConversations] = useState<ChatConversation[]>([]);
    const [selectedConversation, setSelectedConversation] = useState<ChatConversation | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [filter, setFilter] = useState<'all' | 'active' | 'lead_captured' | 'closed'>('all');
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        fetchConversations();
    }, []);

    const fetchConversations = async () => {
        try {
            const response = await fetch('/api/admin/chat-conversations');
            const data = await response.json();
            setConversations(data.conversations || []);
        } catch (error) {
            console.error('Failed to fetch conversations:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const fetchConversationMessages = async (conversationId: string) => {
        try {
            const response = await fetch(`/api/chat?conversationId=${conversationId}`);
            const data = await response.json();

            setSelectedConversation((prev) =>
                prev ? { ...prev, messages: data.messages } : null
            );
        } catch (error) {
            console.error('Failed to fetch messages:', error);
        }
    };

    const selectConversation = (conversation: ChatConversation) => {
        setSelectedConversation(conversation);
        fetchConversationMessages(conversation.id);
    };

    const filteredConversations = conversations
        .filter((conv) => filter === 'all' || conv.status === filter)
        .filter((conv) => {
            if (!searchQuery) return true;
            const query = searchQuery.toLowerCase();
            return (
                conv.visitor_name?.toLowerCase().includes(query) ||
                conv.visitor_email?.toLowerCase().includes(query) ||
                conv.visitor_business?.toLowerCase().includes(query) ||
                conv.session_id.toLowerCase().includes(query)
            );
        })
        .sort((a, b) => new Date(b.created_at).getTime() - new Date(a.created_at).getTime());

    const getLeadScoreColor = (score: number) => {
        if (score >= 40) return 'text-green-600 bg-green-100';
        if (score >= 20) return 'text-yellow-600 bg-yellow-100';
        return 'text-gray-600 bg-gray-100';
    };

    const getStatusBadge = (status: string) => {
        const badges = {
            active: 'bg-blue-100 text-blue-700',
            lead_captured: 'bg-green-100 text-green-700',
            closed: 'bg-gray-100 text-gray-700',
        };
        return badges[status as keyof typeof badges] || badges.active;
    };

    if (isLoading) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-50 p-6">
            <div className="max-w-7xl mx-auto">
                {/* Header */}
                <div className="mb-8">
                    <h1 className="text-3xl font-bold text-gray-900 mb-2">Chat Conversations</h1>
                    <p className="text-gray-600">Monitor and manage AI chatbot conversations</p>
                </div>

                {/* Stats */}
                <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
                    <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-sm text-gray-600 mb-1">Total Chats</p>
                                <p className="text-2xl font-bold text-gray-900">{conversations.length}</p>
                            </div>
                            <MessageCircle className="w-10 h-10 text-blue-600" />
                        </div>
                    </div>

                    <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-sm text-gray-600 mb-1">Active</p>
                                <p className="text-2xl font-bold text-blue-600">
                                    {conversations.filter((c) => c.status === 'active').length}
                                </p>
                            </div>
                            <TrendingUp className="w-10 h-10 text-blue-600" />
                        </div>
                    </div>

                    <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-sm text-gray-600 mb-1">Leads Captured</p>
                                <p className="text-2xl font-bold text-green-600">
                                    {conversations.filter((c) => c.status === 'lead_captured').length}
                                </p>
                            </div>
                            <Target className="w-10 h-10 text-green-600" />
                        </div>
                    </div>

                    <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-sm text-gray-600 mb-1">Avg Lead Score</p>
                                <p className="text-2xl font-bold text-purple-600">
                                    {Math.round(
                                        conversations.reduce((sum, c) => sum + c.lead_score, 0) / conversations.length || 0
                                    )}
                                </p>
                            </div>
                            <TrendingUp className="w-10 h-10 text-purple-600" />
                        </div>
                    </div>
                </div>

                {/* Filters */}
                <div className="bg-white rounded-xl p-4 shadow-sm border border-gray-100 mb-6">
                    <div className="flex flex-col md:flex-row gap-4">
                        <div className="flex-1">
                            <div className="relative">
                                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                                <input
                                    type="text"
                                    placeholder="Search by name, email, business..."
                                    value={searchQuery}
                                    onChange={(e) => setSearchQuery(e.target.value)}
                                    className="w-full pl-10 pr-4 py-2 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                />
                            </div>
                        </div>
                        <div className="flex gap-2">
                            {(['all', 'active', 'lead_captured', 'closed'] as const).map((status) => (
                                <button
                                    key={status}
                                    onClick={() => setFilter(status)}
                                    className={`px-4 py-2 rounded-lg font-medium transition-colors ${filter === status
                                            ? 'bg-blue-600 text-white'
                                            : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                                        }`}
                                >
                                    {status.replace('_', ' ').charAt(0).toUpperCase() + status.slice(1).replace('_', ' ')}
                                </button>
                            ))}
                        </div>
                    </div>
                </div>

                {/* Main Content */}
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                    {/* Conversations List */}
                    <div className="lg:col-span-1 bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden">
                        <div className="p-4 border-b border-gray-100">
                            <h2 className="font-semibold text-gray-900">Conversations ({filteredConversations.length})</h2>
                        </div>
                        <div className="overflow-y-auto max-h-[600px]">
                            {filteredConversations.map((conversation) => (
                                <motion.div
                                    key={conversation.id}
                                    whileHover={{ backgroundColor: '#f9fafb' }}
                                    onClick={() => selectConversation(conversation)}
                                    className={`p-4 border-b border-gray-100 cursor-pointer transition-colors ${selectedConversation?.id === conversation.id ? 'bg-blue-50' : ''
                                        }`}
                                >
                                    <div className="flex items-start justify-between mb-2">
                                        <div className="flex-1">
                                            <p className="font-medium text-gray-900">
                                                {conversation.visitor_name || conversation.visitor_email || 'Anonymous'}
                                            </p>
                                            {conversation.visitor_business && (
                                                <p className="text-sm text-gray-600">{conversation.visitor_business}</p>
                                            )}
                                        </div>
                                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getLeadScoreColor(conversation.lead_score)}`}>
                                            {conversation.lead_score}
                                        </span>
                                    </div>
                                    <div className="flex items-center gap-2 mb-2">
                                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusBadge(conversation.status)}`}>
                                            {conversation.status.replace('_', ' ')}
                                        </span>
                                    </div>
                                    <p className="text-xs text-gray-500">
                                        {new Date(conversation.created_at).toLocaleString()}
                                    </p>
                                </motion.div>
                            ))}
                        </div>
                    </div>

                    {/* Conversation Details */}
                    <div className="lg:col-span-2 bg-white rounded-xl shadow-sm border border-gray-100">
                        {selectedConversation ? (
                            <>
                                {/* Visitor Info */}
                                <div className="p-6 border-b border-gray-100">
                                    <h2 className="text-xl font-semibold text-gray-900 mb-4">Conversation Details</h2>
                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                        {selectedConversation.visitor_name && (
                                            <div className="flex items-center gap-2">
                                                <User className="w-4 h-4 text-gray-400" />
                                                <span className="text-sm text-gray-600">Name:</span>
                                                <span className="text-sm font-medium text-gray-900">{selectedConversation.visitor_name}</span>
                                            </div>
                                        )}
                                        {selectedConversation.visitor_email && (
                                            <div className="flex items-center gap-2">
                                                <Mail className="w-4 h-4 text-gray-400" />
                                                <span className="text-sm text-gray-600">Email:</span>
                                                <span className="text-sm font-medium text-gray-900">{selectedConversation.visitor_email}</span>
                                            </div>
                                        )}
                                        {selectedConversation.visitor_phone && (
                                            <div className="flex items-center gap-2">
                                                <Phone className="w-4 h-4 text-gray-400" />
                                                <span className="text-sm text-gray-600">Phone:</span>
                                                <span className="text-sm font-medium text-gray-900">{selectedConversation.visitor_phone}</span>
                                            </div>
                                        )}
                                        {selectedConversation.visitor_website && (
                                            <div className="flex items-center gap-2">
                                                <Globe className="w-4 h-4 text-gray-400" />
                                                <span className="text-sm text-gray-600">Website:</span>
                                                <span className="text-sm font-medium text-gray-900">{selectedConversation.visitor_website}</span>
                                            </div>
                                        )}
                                        {selectedConversation.visitor_goal && (
                                            <div className="flex items-center gap-2">
                                                <Target className="w-4 h-4 text-gray-400" />
                                                <span className="text-sm text-gray-600">Goal:</span>
                                                <span className="text-sm font-medium text-gray-900">{selectedConversation.visitor_goal}</span>
                                            </div>
                                        )}
                                    </div>
                                </div>

                                {/* Messages */}
                                <div className="p-6 overflow-y-auto max-h-[500px]">
                                    <h3 className="font-semibold text-gray-900 mb-4">Messages</h3>
                                    <div className="space-y-4">
                                        {selectedConversation.messages?.map((message) => (
                                            <div
                                                key={message.id}
                                                className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                                            >
                                                <div
                                                    className={`max-w-[80%] rounded-lg px-4 py-3 ${message.role === 'user'
                                                            ? 'bg-blue-600 text-white'
                                                            : 'bg-gray-100 text-gray-800'
                                                        }`}
                                                >
                                                    <p className="text-sm whitespace-pre-wrap">{message.content}</p>
                                                    <p className={`text-xs mt-1 ${message.role === 'user' ? 'text-blue-100' : 'text-gray-500'}`}>
                                                        {new Date(message.created_at).toLocaleTimeString()}
                                                    </p>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </>
                        ) : (
                            <div className="flex items-center justify-center h-full p-12">
                                <div className="text-center">
                                    <MessageCircle className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                                    <p className="text-gray-500">Select a conversation to view details</p>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { cn } from "@/lib/utils";
import {
  Users,
  TrendingUp,
  DollarSign,
  BarChart3,
  MoreHorizontal,
  CheckCircle2,
  Clock,
  XCircle
} from "lucide-react";

export default function AdminDashboard() {
  const [leads, setLeads] = useState<any[]>([]);
  const [blogs, setBlogs] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [leadsRes, blogsRes] = await Promise.all([
        fetch('/api/leads'),
        fetch('/api/blogs')
      ]);

      if (leadsRes.ok) {
        const leadsData = await leadsRes.json();
        setLeads(leadsData);
      }

      if (blogsRes.ok) {
        const blogsData = await blogsRes.json();
        setBlogs(blogsData);
      }
    } catch (error) {
      console.error('Failed to fetch data', error);
    } finally {
      setLoading(false);
    }
  };

  // Calculate Stats
  const totalLeads = leads.length;
  const activeLeads = leads.filter(l => l.status === 'New' || l.status === 'Contacted').length;
  const totalBlogs = blogs.length;
  const conversionRate = totalLeads > 0 ? ((activeLeads / totalLeads) * 100).toFixed(0) : 0;

  const stats = [
    {
      label: "Total Leads",
      value: totalLeads.toString(),
      change: "+2.0%",
      subtext: "Last month",
      icon: Users,
      gradient: "from-teal-500 to-cyan-600",
      bgGradient: "from-teal-500/10 to-cyan-600/10"
    },
    {
      label: "Active Inquiries",
      value: activeLeads.toString(),
      change: "+3.0%",
      subtext: "Last month",
      icon: BarChart3,
      gradient: "from-purple-500 to-indigo-600",
      bgGradient: "from-purple-500/10 to-indigo-600/10"
    },
    {
      label: "Total Content",
      value: totalBlogs.toString(),
      change: "+4.0%",
      subtext: "Last month",
      icon: TrendingUp,
      gradient: "from-orange-500 to-red-600",
      bgGradient: "from-orange-500/10 to-red-600/10"
    },
    {
      label: "Conversion Rate",
      value: `${conversionRate}%`,
      change: "+12%",
      subtext: "Last month",
      icon: DollarSign,
      gradient: "from-pink-500 to-rose-600",
      bgGradient: "from-pink-500/10 to-rose-600/10"
    },
  ];

  const getStatusBadge = (status: string) => {
    const statusMap: Record<string, { label: string; color: string; icon: any }> = {
      'New': { label: 'Active', color: 'bg-teal-500/10 text-teal-400 border-teal-500/20', icon: CheckCircle2 },
      'Contacted': { label: 'Active', color: 'bg-teal-500/10 text-teal-400 border-teal-500/20', icon: CheckCircle2 },
      'Qualified': { label: 'Active', color: 'bg-teal-500/10 text-teal-400 border-teal-500/20', icon: CheckCircle2 },
      'published': { label: 'Validated', color: 'bg-teal-500/10 text-teal-400 border-teal-500/20', icon: CheckCircle2 },
      'draft': { label: 'Pending', color: 'bg-yellow-500/10 text-yellow-400 border-yellow-500/20', icon: Clock },
      'Closed': { label: 'In Active', color: 'bg-gray-500/10 text-gray-400 border-gray-500/20', icon: XCircle },
    };

    const config = statusMap[status] || statusMap['New'];
    const Icon = config.icon;

    return (
      <span className={cn("inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-medium border", config.color)}>
        <Icon size={12} />
        {config.label}
      </span>
    );
  };

  return (
    <div className="max-w-[1400px] mx-auto">
      {/* Page Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-white mb-2">Admin Dashboard</h1>
        <div className="flex gap-3">
          <button className="px-4 py-2 bg-teal-500 text-white text-sm font-medium rounded-lg hover:bg-teal-600 transition-colors">
            Overview
          </button>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {stats.map((stat, index) => (
          <motion.div
            key={stat.label}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            className={cn(
              "relative rounded-2xl p-6 overflow-hidden border border-white/5",
              `bg-gradient-to-br ${stat.bgGradient}`
            )}
          >
            <div className="relative z-10">
              <div className="flex items-start justify-between mb-4">
                <div className={cn(
                  "p-2.5 rounded-xl bg-gradient-to-br",
                  stat.gradient
                )}>
                  <stat.icon size={20} className="text-white" />
                </div>
                <div className="text-right">
                  <div className="text-xs font-medium text-teal-400">{stat.change}</div>
                  <div className="text-[10px] text-gray-500">{stat.subtext}</div>
                </div>
              </div>
              <div>
                <h3 className="text-3xl font-bold text-white mb-1">{stat.value}</h3>
                <p className="text-sm text-gray-400">{stat.label}</p>
              </div>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Recent Lists Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
        {/* Recent Leads */}
        <div className="bg-[#0F141A] border border-white/5 rounded-2xl p-6">
          <h2 className="text-lg font-bold text-white mb-6">Recent Leads</h2>
          <div className="space-y-4">
            {loading ? (
              <p className="text-gray-500 text-sm">Loading...</p>
            ) : leads.length === 0 ? (
              <p className="text-gray-500 text-sm">No leads yet.</p>
            ) : (
              leads.slice(0, 3).map((lead: any) => (
                <div key={lead.id} className="flex items-center justify-between group">
                  <span className="text-sm text-gray-300 group-hover:text-white transition-colors truncate">
                    {lead.name}
                  </span>
                  {getStatusBadge(lead.status || 'New')}
                </div>
              ))
            )}
          </div>
        </div>

        {/* Recent Blogs */}
        <div className="bg-[#0F141A] border border-white/5 rounded-2xl p-6">
          <h2 className="text-lg font-bold text-white mb-6">Recent Blogs</h2>
          <div className="space-y-4">
            {loading ? (
              <p className="text-gray-500 text-sm">Loading...</p>
            ) : blogs.length === 0 ? (
              <p className="text-gray-500 text-sm">No blogs yet.</p>
            ) : (
              blogs.slice(0, 3).map((blog: any) => (
                <div key={blog.id} className="flex items-center justify-between group">
                  <span className="text-sm text-gray-300 group-hover:text-white transition-colors truncate">
                    {blog.title}
                  </span>
                  {getStatusBadge(blog.status || 'draft')}
                </div>
              ))
            )}
          </div>
        </div>

        {/* System Status */}
        <div className="bg-[#0F141A] border border-white/5 rounded-2xl p-6">
          <h2 className="text-lg font-bold text-white mb-6">System Status</h2>
          <div className="space-y-4">
            <div className="flex items-center justify-between group">
              <span className="text-sm text-gray-300">Database</span>
              {getStatusBadge('published')}
            </div>
            <div className="flex items-center justify-between group">
              <span className="text-sm text-gray-300">API Services</span>
              {getStatusBadge('published')}
            </div>
            <div className="flex items-center justify-between group">
              <span className="text-sm text-gray-300">Email Service</span>
              {getStatusBadge('draft')}
            </div>
          </div>
        </div>
      </div>

      {/* Detailed Table */}
      <div className="bg-[#0F141A] border border-white/5 rounded-2xl overflow-hidden">
        <div className="p-6 border-b border-white/5">
          <h2 className="text-lg font-bold text-white">All Inquiries</h2>
        </div>
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-white/5">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Company</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Contact</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Email</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Service</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-white/5">
              {loading ? (
                <tr>
                  <td colSpan={6} className="px-6 py-8 text-center text-gray-500">Loading inquiries...</td>
                </tr>
              ) : leads.length === 0 ? (
                <tr>
                  <td colSpan={6} className="px-6 py-8 text-center text-gray-500">No inquiries yet.</td>
                </tr>
              ) : (
                leads.map((lead: any) => (
                  <tr key={lead.id} className="hover:bg-white/5 transition-colors">
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-white">
                      {lead.company || 'N/A'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-300">
                      {lead.name}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-400">
                      {lead.email}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-400">
                      {lead.service || 'General'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {getStatusBadge(lead.status || 'New')}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-400">
                      <button className="p-1.5 hover:bg-white/10 rounded-lg transition-colors">
                        <MoreHorizontal size={16} />
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import AdminSidebar from "@/components/AdminSidebar";
import {
    Users,
    Search,
    Filter,
    MoreVertical,
    Mail,
    Phone,
    MessageSquare,
    ChevronRight,
    Download,
    CheckCircle2,
    Clock,
    AlertCircle,
    Trash2
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { motion, AnimatePresence } from "framer-motion";

export default function LeadsPage() {
    const [leads, setLeads] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState("");
    const [filterStatus, setFilterStatus] = useState("All");
    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");

    const [selectedLead, setSelectedLead] = useState<any | null>(null);
    const [isEditOpen, setIsEditOpen] = useState(false);
    const [saving, setSaving] = useState(false);

    // CRM Fields State
    const [editForm, setEditForm] = useState({
        company_name: "",
        budget_range: "",
        business_size: "",
        timeline: "",
        notes: "",
        status: ""
    });

    useEffect(() => {
        fetchLeads();
    }, []);

    const fetchLeads = async () => {
        try {
            const res = await fetch("/api/leads");
            if (res.ok) {
                const data = await res.json();
                setLeads(data);
            }
        } catch (error) {
            console.error("Failed to fetch leads", error);
        } finally {
            setLoading(false);
        }
    };

    const openEditModal = (lead: any) => {
        setSelectedLead(lead);
        setEditForm({
            company_name: lead.company_name || "",
            budget_range: lead.budget_range || "",
            business_size: lead.business_size || "",
            timeline: lead.timeline || "",
            notes: lead.notes || "",
            status: lead.status || "New"
        });
        setIsEditOpen(true);
    };

    const handleSaveLead = async () => {
        if (!selectedLead) return;
        setSaving(true);
        try {
            const res = await fetch("/api/leads", {
                method: "PATCH",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ id: selectedLead.id, ...editForm })
            });
            if (res.ok) {
                // Update local state
                setLeads(leads.map(l => l.id === selectedLead.id ? { ...l, ...editForm } : l));
                setIsEditOpen(false);
            }
        } catch (error) {
            console.error("Failed to save lead", error);
        } finally {
            setSaving(false);
        }
    };

    const deleteLead = async (id: string) => {
        if (!confirm("Are you sure you want to delete this lead?")) return;
        try {
            const res = await fetch(`/api/leads?id=${id}`, { method: "DELETE" });
            if (res.ok) {
                setLeads(leads.filter(l => l.id !== id));
                if (selectedLead?.id === id) setIsEditOpen(false);
            }
        } catch (error) {
            console.error("Failed to delete lead", error);
        }
    };

    const filteredLeads = leads.filter(lead => {
        const matchesSearch = lead.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            lead.email.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesStatus = filterStatus === "All" || lead.status === filterStatus;

        let matchesDate = true;
        if (startDate || endDate) {
            const leadDate = new Date(lead.createdAt);
            if (startDate) {
                const start = new Date(startDate);
                start.setHours(0, 0, 0, 0);
                if (leadDate < start) matchesDate = false;
            }
            if (endDate) {
                const end = new Date(endDate);
                end.setHours(23, 59, 59, 999);
                if (leadDate > end) matchesDate = false;
            }
        }
        return matchesSearch && matchesStatus && matchesDate;
    });

    const handleExport = () => {
        if (filteredLeads.length === 0) {
            alert("No leads to export");
            return;
        }

        const headers = ["Name", "Email", "Phone", "Service", "Company", "Budget", "Status", "Date", "Notes"];
        const csvRows = [headers.join(",")];

        filteredLeads.forEach(lead => {
            const row = [
                `"${lead.name}"`,
                `"${lead.email}"`,
                `"${lead.phone || ''}"`,
                `"${lead.service}"`,
                `"${lead.company_name || ''}"`,
                `"${lead.budget_range || ''}"`,
                `"${lead.status}"`,
                `"${new Date(lead.createdAt).toLocaleDateString()}"`,
                `"${(lead.notes || '').replace(/"/g, '""')}"`
            ];
            csvRows.push(row.join(","));
        });

        const csvContent = "data:text/csv;charset=utf-8," + csvRows.join("\n");
        const encodedUri = encodeURI(csvContent);
        const link = document.createElement("a");
        link.setAttribute("href", encodedUri);
        link.setAttribute("download", `leads_export.csv`);
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    const getStatusColor = (status: string) => {
        switch (status) {
            case "New": return "bg-blue-500/10 text-blue-400 border-blue-500/20";
            case "Contacted": return "bg-yellow-500/10 text-yellow-400 border-yellow-500/20";
            case "Qualified": return "bg-purple-500/10 text-purple-400 border-purple-500/20";
            case "Proposal Sent": return "bg-orange-500/10 text-orange-400 border-orange-500/20";
            case "Closed Won": return "bg-green-500/10 text-green-400 border-green-500/20";
            case "Closed Lost": return "bg-red-500/10 text-red-400 border-red-500/20";
            default: return "bg-gray-500/10 text-gray-400 border-gray-500/20";
        }
    };

    // Calculate counts for badges
    const getStatusCount = (status: string) => {
        if (status === "All") return leads.length;
        return leads.filter(l => l.status === status).length;
    };

    return (
        <div className="max-w-7xl mx-auto space-y-6">
            {/* Toolbar & Filters */}
            <div className="bg-[#0F141A] border border-white/5 rounded-2xl p-4 flex flex-col gap-4">
                <div className="flex flex-col md:flex-row gap-4 items-center justify-between">
                    <div className="relative flex-1 max-w-md w-full">
                        <label htmlFor="search-leads" className="sr-only">Search leads</label>
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-500" size={18} />
                        <input
                            id="search-leads"
                            type="text"
                            placeholder="Search by name, email, or company..."
                            className="w-full pl-10 pr-4 py-2.5 bg-white/5 border border-white/10 rounded-xl text-white focus:outline-none focus:ring-2 focus:ring-primary/50 transition-all placeholder:text-gray-600"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                    </div>
                    <div className="flex items-center gap-2 w-full md:w-auto bg-white/5 p-1 rounded-xl border border-white/10">
                        <div className="relative">
                            <label htmlFor="start-date" className="sr-only">Start Date</label>
                            <input
                                id="start-date"
                                type="date"
                                className="px-3 py-1.5 bg-transparent text-white text-sm focus:outline-none w-32"
                                value={startDate}
                                onChange={(e) => setStartDate(e.target.value)}
                            />
                        </div>
                        <span className="text-gray-500 text-sm">to</span>
                        <div className="relative">
                            <label htmlFor="end-date" className="sr-only">End Date</label>
                            <input
                                id="end-date"
                                type="date"
                                className="px-3 py-1.5 bg-transparent text-white text-sm focus:outline-none w-32"
                                value={endDate}
                                onChange={(e) => setEndDate(e.target.value)}
                            />
                        </div>
                    </div>
                    <Button className="gap-2 shadow-lg shadow-primary/20" onClick={handleExport}>
                        <Download size={18} />
                        Export CSV
                    </Button>
                </div>

                {/* Status Tabs */}
                <div className="flex gap-2 overflow-x-auto pb-2 border-t border-white/5 pt-4 no-scrollbar">
                    {["All", "New", "Contacted", "Qualified", "Proposal Sent", "Closed Won", "Closed Lost"].map(status => (
                        <button
                            key={status}
                            onClick={() => setFilterStatus(status)}
                            className={cn(
                                "px-3 py-1.5 rounded-full text-xs font-medium whitespace-nowrap transition-all flex items-center gap-2 border",
                                filterStatus === status
                                    ? "bg-primary/10 text-primary border-primary/50"
                                    : "bg-white/5 text-gray-400 border-transparent hover:bg-white/10 hover:text-gray-300"
                            )}
                        >
                            {status}
                            <span className={cn(
                                "px-1.5 py-0.5 rounded-full text-[10px]",
                                filterStatus === status ? "bg-primary text-black" : "bg-white/10 text-gray-500"
                            )}>
                                {getStatusCount(status)}
                            </span>
                        </button>
                    ))}
                </div>
            </div>

            {/* Leads Table */}
            <div className="bg-[#0F141A] border border-white/5 rounded-2xl overflow-hidden shadow-xl">
                <div className="overflow-x-auto">
                    <table className="w-full text-left border-collapse">
                        <thead>
                            <tr className="border-b border-white/5 bg-white/[0.02] text-gray-400 text-xs font-semibold uppercase tracking-wider">
                                <th className="px-6 py-5">Lead Name</th>
                                <th className="px-6 py-5">Service</th>
                                <th className="px-6 py-5 hidden lg:table-cell">Notes</th>
                                <th className="px-6 py-5">Status</th>
                                <th className="px-6 py-5 hidden md:table-cell">Date</th>
                                <th className="px-6 py-5 text-right">Action</th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-white/5">
                            <AnimatePresence mode="popLayout">
                                {loading ? (
                                    <tr><td colSpan={6} className="px-6 py-12 text-center text-gray-500">Loading pipeline...</td></tr>
                                ) : filteredLeads.length === 0 ? (
                                    <tr><td colSpan={6} className="px-6 py-12 text-center text-gray-500">No leads found matching your criteria.</td></tr>
                                ) : filteredLeads.map((lead) => (
                                    <motion.tr
                                        layout
                                        key={lead.id}
                                        initial={{ opacity: 0, y: 10 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        exit={{ opacity: 0, scale: 0.95 }}
                                        className="group hover:bg-white/[0.02] transition-colors cursor-pointer"
                                        onClick={() => openEditModal(lead)}
                                    >
                                        <td className="px-6 py-4">
                                            <div className="flex flex-col gap-1">
                                                <span className="text-white font-medium capitalize">{lead.name}</span>
                                                <div className="flex items-center gap-2 text-xs text-gray-500">
                                                    <Mail size={12} /> {lead.email}
                                                </div>
                                            </div>
                                        </td>
                                        <td className="px-6 py-4 text-sm text-gray-300 capitalize">
                                            {lead.service?.replace(/-/g, ' ')}
                                        </td>
                                        <td className="px-6 py-4 hidden lg:table-cell text-sm text-gray-400 max-w-[200px]" title={lead.notes}>
                                            <div className="truncate cursor-help">
                                                {lead.notes || <span className="text-white/10 italic">No notes</span>}
                                            </div>
                                        </td>
                                        <td className="px-6 py-4">
                                            <span className={cn("px-2.5 py-1 rounded-full text-[10px] font-bold border uppercase tracking-wider", getStatusColor(lead.status))}>
                                                {lead.status}
                                            </span>
                                        </td>
                                        <td className="px-6 py-4 hidden md:table-cell text-xs text-gray-500 font-mono">
                                            {new Date(lead.createdAt).toLocaleDateString('en-GB', {
                                                day: 'numeric', month: 'short', year: 'numeric'
                                            })}
                                        </td>
                                        <td className="px-6 py-4 text-right">
                                            <div className="flex justify-end relative group/action">
                                                <Button size="sm" variant="ghost" className="h-8 w-8 p-0 text-gray-400 hover:text-white">
                                                    <MoreVertical size={16} />
                                                </Button>
                                                <div className="absolute right-8 top-1/2 -translate-y-1/2 bg-black/90 text-white text-[10px] py-1 px-2 rounded opacity-0 group-hover/action:opacity-100 transition-opacity whitespace-nowrap pointer-events-none">
                                                    View Details
                                                </div>
                                            </div>
                                        </td>
                                    </motion.tr>
                                ))}
                            </AnimatePresence>
                        </tbody>
                    </table>
                </div>
            </div>

            {/* Edit Slide-over Modal */}
            <AnimatePresence>
                {isEditOpen && selectedLead && (
                    <>
                        <motion.div
                            initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                            className="fixed inset-0 bg-black/60 backdrop-blur-sm z-50"
                            onClick={() => setIsEditOpen(false)}
                        />
                        <motion.div
                            initial={{ x: "100%" }} animate={{ x: 0 }} exit={{ x: "100%" }}
                            transition={{ type: "spring", damping: 25, stiffness: 200 }}
                            className="fixed top-0 right-0 h-full w-full md:w-[500px] bg-[#0F141A] border-l border-white/5 shadow-2xl z-50 overflow-y-auto"
                        >
                            <div className="p-6 space-y-6">
                                <div className="flex items-center justify-between">
                                    <h2 className="text-2xl font-bold text-white">Lead Details</h2>
                                    <button onClick={() => setIsEditOpen(false)} className="text-gray-400 hover:text-white transition-transform hover:rotate-90">
                                        <CheckCircle2 size={24} className="rotate-45" /> {/* Use as close icon */}
                                    </button>
                                </div>

                                {/* Static Info */}
                                <div className="bg-white/5 p-4 rounded-xl space-y-3 border border-white/5">
                                    <div className="flex justify-between items-center border-b border-white/5 pb-2">
                                        <span className="text-gray-400 text-sm">Name</span>
                                        <span className="text-white text-sm font-medium capitalize">{selectedLead.name}</span>
                                    </div>
                                    <div className="flex justify-between items-center border-b border-white/5 pb-2">
                                        <span className="text-gray-400 text-sm">Email</span>
                                        <div className="flex items-center gap-2">
                                            <span className="text-white text-sm">{selectedLead.email}</span>
                                            <button className="text-primary hover:text-white" title="Copy Email">
                                                <Mail size={12} />
                                            </button>
                                        </div>
                                    </div>
                                    <div className="flex justify-between items-center border-b border-white/5 pb-2">
                                        <span className="text-gray-400 text-sm">Phone</span>
                                        <span className="text-white text-sm">{selectedLead.phone || "N/A"}</span>
                                    </div>
                                    <div className="flex justify-between items-center">
                                        <span className="text-gray-400 text-sm">Source</span>
                                        <span className="text-primary text-xs uppercase bg-primary/10 px-2 py-0.5 rounded border border-primary/20">
                                            {selectedLead.source || "Website"}
                                        </span>
                                    </div>
                                    <div className="pt-2">
                                        <p className="text-gray-500 text-xs mb-1 uppercase tracking-wider">Initial Message</p>
                                        <p className="text-gray-300 text-sm italic border-l-2 border-primary/30 pl-3 py-1">
                                            "{selectedLead.message}"
                                        </p>
                                    </div>
                                </div>

                                {/* CRM Fields */}
                                <div className="space-y-4">
                                    <h3 className="text-lg font-semibold text-white flex items-center gap-2">
                                        <Filter size={18} className="text-primary" /> Pipeline Management
                                    </h3>

                                    <div className="space-y-2">
                                        <label className="text-xs text-gray-400 uppercase font-semibold">Status</label>
                                        <select
                                            className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:ring-1 focus:ring-primary outline-none transition-all cursor-pointer hover:border-white/20"
                                            value={editForm.status}
                                            onChange={(e) => setEditForm({ ...editForm, status: e.target.value })}
                                        >
                                            {["New", "Contacted", "Qualified", "Proposal Sent", "Closed Won", "Closed Lost"].map(s => (
                                                <option key={s} value={s}>{s}</option>
                                            ))}
                                        </select>
                                    </div>

                                    <div className="grid grid-cols-2 gap-4">
                                        <div className="space-y-2">
                                            <label className="text-xs text-gray-400 uppercase font-semibold">Company Name</label>
                                            <input
                                                type="text"
                                                className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:ring-1 focus:ring-primary outline-none focus:border-primary/50 transition-all"
                                                value={editForm.company_name}
                                                onChange={(e) => setEditForm({ ...editForm, company_name: e.target.value })}
                                                placeholder="ACME Corp"
                                            />
                                        </div>
                                        <div className="space-y-2">
                                            <label className="text-xs text-gray-400 uppercase font-semibold">Business Size</label>
                                            <select
                                                className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:ring-1 focus:ring-primary outline-none focus:border-primary/50 transition-all cursor-pointer"
                                                value={editForm.business_size}
                                                onChange={(e) => setEditForm({ ...editForm, business_size: e.target.value })}
                                            >
                                                <option value="">Select...</option>
                                                <option value="Startup">Startup (1-10)</option>
                                                <option value="Small">Small (11-50)</option>
                                                <option value="Medium">Medium (51-200)</option>
                                                <option value="Enterprise">Enterprise (200+)</option>
                                            </select>
                                        </div>
                                    </div>

                                    <div className="grid grid-cols-2 gap-4">
                                        <div className="space-y-2">
                                            <label className="text-xs text-gray-400 uppercase font-semibold">Budget Range</label>
                                            <input
                                                type="text"
                                                className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:ring-1 focus:ring-primary outline-none focus:border-primary/50 transition-all"
                                                value={editForm.budget_range}
                                                onChange={(e) => setEditForm({ ...editForm, budget_range: e.target.value })}
                                                placeholder="$1k - $5k"
                                            />
                                        </div>
                                        <div className="space-y-2">
                                            <label className="text-xs text-gray-400 uppercase font-semibold">Timeline</label>
                                            <input
                                                type="text"
                                                className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:ring-1 focus:ring-primary outline-none focus:border-primary/50 transition-all"
                                                value={editForm.timeline}
                                                onChange={(e) => setEditForm({ ...editForm, timeline: e.target.value })}
                                                placeholder="ASAP / 1 Month"
                                            />
                                        </div>
                                    </div>

                                    <div className="space-y-2">
                                        <label className="text-xs text-gray-400 uppercase font-semibold">Internal Notes</label>
                                        <textarea
                                            rows={5}
                                            className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:ring-1 focus:ring-primary outline-none focus:border-primary/50 transition-all resize-none"
                                            value={editForm.notes}
                                            onChange={(e) => setEditForm({ ...editForm, notes: e.target.value })}
                                            placeholder="Call notes, requirements, meeting summary..."
                                        />
                                    </div>
                                </div>

                                {/* Action Buttons */}
                                <div className="flex gap-3 pt-6 border-t border-white/10">
                                    <Button
                                        variant="destructive"
                                        onClick={() => deleteLead(selectedLead.id)}
                                        className="flex-1 hover:bg-red-900/50"
                                    >
                                        <Trash2 size={16} className="mr-2" /> Delete
                                    </Button>
                                    <Button
                                        onClick={handleSaveLead}
                                        disabled={saving}
                                        className="flex-[2] shadow-[0_0_20px_-5px_var(--color-primary)] hover:shadow-[0_0_25px_-2px_var(--color-primary)] transition-all"
                                    >
                                        {saving ? (
                                            <>
                                                <Clock size={16} className="mr-2 animate-spin" /> Saving...
                                            </>
                                        ) : (
                                            <>
                                                <CheckCircle2 size={16} className="mr-2" /> Save Changes
                                            </>
                                        )}
                                    </Button>
                                </div>
                            </div>
                        </motion.div>
                    </>
                )}
            </AnimatePresence>
        </div>
    );
}

"use client";

import React, { useState } from "react";
import { Reorder } from "framer-motion";
import {
    Save, Eye, Upload, GripVertical, Plus, Edit2, Trash2,
    ToggleLeft, ToggleRight, Star, Settings,
    ChevronDown, ChevronUp, BarChart3, Clock,
    AlertCircle, CheckCircle2, Monitor, Smartphone,
    Zap, Package, ArrowRight, DollarSign
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuLabel,
    DropdownMenuSeparator,
    DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { ServicesMegaMenu } from "@/components/layout/ServicesMegaMenu";
import { useMegaMenu } from "@/contexts/MegaMenuContext";
import { cn } from "@/lib/utils";

export default function MegaMenuManagerPage() {
    const {
        categories,
        updateCategory,
        updateService,
        reorderServices,
        addService,
        showPricing,
        setShowPricing,
        availableServices,
        loadingServices
    } = useMegaMenu();

    const [showPreview, setShowPreview] = useState(false);
    const [previewMode, setPreviewMode] = useState<"desktop" | "mobile">("desktop");
    const [expandedCategory, setExpandedCategory] = useState<string | null>("acquisition");
    const [saveStatus, setSaveStatus] = useState<"idle" | "saving" | "saved">("idle");
    const [validationExpanded, setValidationExpanded] = useState(true);

    const handleSave = () => {
        setSaveStatus("saving");
        setTimeout(() => {
            setSaveStatus("saved");
            setTimeout(() => setSaveStatus("idle"), 3000);
        }, 1000);
    };

    const handleAddService = (categoryId: string, service: any) => {
        const category = categories.find(c => c.id === categoryId);
        if (category?.services.some(s => s.slug === service.slug)) {
            // Optional: alert or toast
            return;
        }

        addService(categoryId, {
            id: `${service.id}-${Date.now()}`,
            name: service.name,
            slug: service.slug,
            subtitle: service.description || "AI-Powered Solution",
            icon: "Rocket",
            visible: true,
            featured: false,
            price: "Contact for pricing",
            priceType: "fixed",
            order: category ? category.services.length + 1 : 1
        });
    };

    const cyclePriceType = (categoryId: string, serviceId: string, currentType?: string) => {
        const types = ['fixed', 'starting', 'quote', 'hidden'];
        const nextIndex = (types.indexOf(currentType || 'fixed') + 1) % types.length;
        // @ts-ignore
        updateService(categoryId, serviceId, { priceType: types[nextIndex] });
    };

    const toggleCategoryVisibility = (categoryId: string) => {
        const category = categories.find(c => c.id === categoryId);
        if (category) {
            updateCategory(categoryId, { visible: !category.visible });
        }
    };

    const toggleServiceVisibility = (categoryId: string, serviceId: string) => {
        const category = categories.find(c => c.id === categoryId);
        const service = category?.services.find(s => s.id === serviceId);
        if (service) {
            updateService(categoryId, serviceId, { visible: !service.visible });
        }
    };

    const toggleServiceFeatured = (categoryId: string, serviceId: string) => {
        const category = categories.find(c => c.id === categoryId);
        const service = category?.services.find(s => s.id === serviceId);
        if (service) {
            updateService(categoryId, serviceId, { featured: !service.featured });
        }
    };

    const validationIssues = categories
        .filter(cat => cat.visible && cat.services.filter(s => s.visible).length < 2)
        .map(cat => ({
            id: cat.id,
            message: `Category "${cat.name}" has ${cat.services.filter(s => s.visible).length} visible service(s) (recommended: 2+)`,
            categoryId: cat.id
        }));

    return (
        <div className="space-y-6">
            {/* Sticky Top Bar */}
            <div className="sticky top-0 z-40 bg-[#0B0F14]/95 backdrop-blur-md border-b border-white/10 -mx-4 md:-mx-8 px-4 md:px-8 py-4 -mt-4 md:-mt-8 mb-6">
                <div className="flex items-center justify-between">
                    <div>
                        <h1 className="text-2xl font-bold">Services Mega Menu</h1>
                        <p className="text-sm text-gray-400 mt-1">
                            Manage navigation menu in real-time
                        </p>
                    </div>

                    <div className="flex items-center gap-3">
                        {saveStatus === "saved" && (
                            <span className="text-sm text-green-400 flex items-center gap-1">
                                <CheckCircle2 size={14} />
                                Saved
                            </span>
                        )}
                        {saveStatus === "saving" && (
                            <span className="text-sm text-gray-400 flex items-center gap-1">
                                <Clock size={14} className="animate-spin" />
                                Saving...
                            </span>
                        )}
                        <Button
                            variant="outline"
                            onClick={() => setShowPreview(!showPreview)}
                            className="gap-2"
                            size="sm"
                        >
                            <Eye size={16} />
                            {showPreview ? "Hide" : "Show"} Preview
                        </Button>
                        <Button
                            onClick={handleSave}
                            className="gap-2 bg-primary hover:bg-primary/90"
                            size="sm"
                        >
                            <Save size={16} />
                            Save Changes
                        </Button>
                    </div>
                </div>
            </div>

            {/* Live Preview */}
            {showPreview && (
                <Card className="bg-[#0F141A] border-primary/20">
                    <CardHeader>
                        <div className="flex items-center justify-between">
                            <CardTitle className="flex items-center gap-2">
                                <Eye size={20} />
                                Live Preview
                            </CardTitle>
                            <div className="flex items-center gap-2">
                                <Button
                                    size="sm"
                                    variant={previewMode === "desktop" ? "default" : "outline"}
                                    onClick={() => setPreviewMode("desktop")}
                                >
                                    <Monitor size={14} className="mr-1" />
                                    Desktop
                                </Button>
                                <Button
                                    size="sm"
                                    variant={previewMode === "mobile" ? "default" : "outline"}
                                    onClick={() => setPreviewMode("mobile")}
                                >
                                    <Smartphone size={14} className="mr-1" />
                                    Mobile
                                </Button>
                            </div>
                        </div>
                    </CardHeader>
                    <CardContent>
                        <div className={cn(
                            "bg-[#0B0F14] border border-white/10 rounded-xl overflow-hidden",
                            previewMode === "mobile" ? "max-w-md mx-auto" : ""
                        )}>
                            <div className="bg-[#0B0F14] p-4 border-b border-white/5">
                                <div className="flex items-center justify-between max-w-7xl mx-auto">
                                    <div className="flex items-center gap-2">
                                        <div className="w-8 h-8 rounded-full bg-primary flex items-center justify-center">
                                            <span className="text-black font-bold text-sm">D</span>
                                        </div>
                                        <span className="text-white font-bold">Digihub</span>
                                    </div>
                                    <span className="px-3 py-1 rounded-full bg-primary text-black text-sm font-medium">
                                        Services
                                    </span>
                                </div>
                            </div>
                            <div className="p-6">
                                <ServicesMegaMenu onClose={() => { }} isPreview={true} />
                            </div>
                        </div>
                    </CardContent>
                </Card>
            )}

            {/* Main Content */}
            <Tabs defaultValue="categories" className="space-y-6">
                <TabsList className="bg-[#0F141A]">
                    <TabsTrigger value="categories">Categories & Services</TabsTrigger>
                    <TabsTrigger value="settings">Settings</TabsTrigger>
                    <TabsTrigger value="analytics">Analytics</TabsTrigger>
                </TabsList>

                {/* Categories Tab */}
                <TabsContent value="categories" className="space-y-6">
                    <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-4 gap-6">
                        {categories.map((category) => (
                            <Card key={category.id} className="bg-[#0F141A] border-white/10">
                                <CardHeader className="pb-3">
                                    <div className="flex items-center justify-between mb-2">
                                        <div className="flex items-center gap-2">
                                            <div className={`w-3 h-3 rounded-full bg-${category.color}-400`} />
                                            <CardTitle className="text-sm">{category.name}</CardTitle>
                                        </div>
                                        <Badge className={category.visible ? "bg-green-500/20 text-green-400" : "bg-gray-500/20 text-gray-400"}>
                                            {category.visible ? "Visible" : "Hidden"}
                                        </Badge>
                                    </div>
                                    <div className="flex items-center justify-between">
                                        <div className="flex items-center gap-2">
                                            <Button
                                                size="sm"
                                                variant="ghost"
                                                onClick={() => setExpandedCategory(
                                                    expandedCategory === category.id ? null : category.id
                                                )}
                                            >
                                                {expandedCategory === category.id ? (
                                                    <ChevronUp size={16} />
                                                ) : (
                                                    <ChevronDown size={16} />
                                                )}
                                            </Button>
                                            <span className="text-xs text-gray-400">
                                                {category.services.length} services
                                            </span>
                                        </div>
                                        <Switch
                                            checked={category.visible}
                                            onCheckedChange={() => toggleCategoryVisibility(category.id)}
                                            title="Show/hide category in menu"
                                        />
                                    </div>
                                </CardHeader>

                                {expandedCategory === category.id && (
                                    <CardContent className="space-y-3">
                                        {category.services.length === 0 ? (
                                            <div className="text-center py-8 px-4 border-2 border-dashed border-white/10 rounded-lg">
                                                <Package size={32} className="mx-auto text-gray-600 mb-2" />
                                                <p className="text-sm text-gray-400 mb-1">No services yet</p>
                                                <div className="mt-4 flex justify-center">
                                                    <DropdownMenu>
                                                        <DropdownMenuTrigger asChild>
                                                            <Button size="sm" variant="outline" className="gap-2 border-dashed">
                                                                <Plus size={14} />
                                                                Add Service
                                                            </Button>
                                                        </DropdownMenuTrigger>
                                                        <DropdownMenuContent align="center" className="w-56 max-h-64 overflow-y-auto bg-[#0F141A] border-white/10">
                                                            <DropdownMenuLabel>Available Services</DropdownMenuLabel>
                                                            <DropdownMenuSeparator className="bg-white/10" />
                                                            {loadingServices ? (
                                                                <div className="p-2 text-xs text-center text-gray-400">Loading...</div>
                                                            ) : availableServices.length === 0 ? (
                                                                <div className="p-2 text-xs text-center text-gray-400">No services found</div>
                                                            ) : (
                                                                availableServices.map((service) => (
                                                                    <DropdownMenuItem
                                                                        key={service.id}
                                                                        onClick={() => handleAddService(category.id, service)}
                                                                        className="cursor-pointer hover:bg-white/5 focus:bg-white/5"
                                                                    >
                                                                        {service.name}
                                                                    </DropdownMenuItem>
                                                                ))
                                                            )}
                                                        </DropdownMenuContent>
                                                    </DropdownMenu>
                                                </div>
                                            </div>
                                        ) : (
                                            <>
                                                <Reorder.Group
                                                    axis="y"
                                                    values={category.services}
                                                    onReorder={(newOrder) => reorderServices(category.id, newOrder)}
                                                    className="space-y-2"
                                                >
                                                    {category.services.map((service) => (
                                                        <Reorder.Item
                                                            key={service.id}
                                                            value={service}
                                                            className={cn(
                                                                "bg-[#0B0F14] border rounded-lg p-3 cursor-move hover:border-primary/30 transition-all group",
                                                                service.visible ? "border-white/5" : "border-gray-500/20 opacity-60"
                                                            )}
                                                        >
                                                            <div className="flex items-start gap-2">
                                                                <div title="Drag to reorder" className="mt-1">
                                                                    <GripVertical
                                                                        size={16}
                                                                        className="text-gray-500 group-hover:text-primary flex-shrink-0 transition-colors"
                                                                    />
                                                                </div>

                                                                <div className="flex-1 min-w-0">
                                                                    <div className="flex items-center gap-2 mb-1">
                                                                        <h4 className="font-semibold text-sm truncate">
                                                                            {service.name}
                                                                        </h4>
                                                                        {service.badge && (
                                                                            <Badge variant="secondary" className="text-[9px] px-1 py-0">
                                                                                {service.badge}
                                                                            </Badge>
                                                                        )}
                                                                    </div>
                                                                    <p className="text-xs text-gray-400 line-clamp-1 mb-2">
                                                                        {service.subtitle}
                                                                    </p>
                                                                    {showPricing && (
                                                                        <span className="text-[10px] text-gray-500">{service.price}</span>
                                                                    )}
                                                                </div>

                                                                <div className="flex flex-col gap-2">
                                                                    <Button
                                                                        size="sm"
                                                                        variant="ghost"
                                                                        onClick={() => toggleServiceVisibility(category.id, service.id)}
                                                                        className="h-6 w-6 p-0"
                                                                        title={service.visible ? "Hide from menu" : "Show in menu"}
                                                                    >
                                                                        {service.visible ? (
                                                                            <ToggleRight size={14} className="text-primary" />
                                                                        ) : (
                                                                            <ToggleLeft size={14} className="text-gray-500" />
                                                                        )}
                                                                    </Button>
                                                                    <Button
                                                                        size="sm"
                                                                        variant="ghost"
                                                                        onClick={() => toggleServiceFeatured(category.id, service.id)}
                                                                        className="h-6 w-6 p-0"
                                                                        title={service.featured ? "Remove featured" : "Mark as featured"}
                                                                    >
                                                                        <Star
                                                                            size={14}
                                                                            className={service.featured ? "text-yellow-400 fill-yellow-400" : "text-gray-500"}
                                                                        />
                                                                    </Button>
                                                                    <Button
                                                                        size="sm"
                                                                        variant="ghost"
                                                                        onClick={() => cyclePriceType(category.id, service.id, service.priceType)}
                                                                        className="h-6 w-6 p-0"
                                                                        title={`Price Mode: ${service.priceType || 'fixed'}`}
                                                                    >
                                                                        <DollarSign
                                                                            size={14}
                                                                            className={
                                                                                service.priceType === 'hidden' ? "text-gray-600 line-through" :
                                                                                    service.priceType === 'quote' ? "text-blue-400" :
                                                                                        service.priceType === 'starting' ? "text-green-400" :
                                                                                            "text-white"
                                                                            }
                                                                        />
                                                                    </Button>
                                                                </div>
                                                            </div>
                                                        </Reorder.Item>
                                                    ))}
                                                </Reorder.Group>
                                                <div className="mt-3">
                                                    <DropdownMenu>
                                                        <DropdownMenuTrigger asChild>
                                                            <Button variant="outline" size="sm" className="w-full gap-2 border-dashed hover:border-primary hover:text-primary">
                                                                <Plus size={14} />
                                                                Add Service
                                                            </Button>
                                                        </DropdownMenuTrigger>
                                                        <DropdownMenuContent align="end" className="w-56 max-h-64 overflow-y-auto bg-[#0F141A] border-white/10">
                                                            <DropdownMenuLabel>Available Services</DropdownMenuLabel>
                                                            <DropdownMenuSeparator className="bg-white/10" />
                                                            {loadingServices ? (
                                                                <div className="p-2 text-xs text-center text-gray-400">Loading...</div>
                                                            ) : availableServices.length === 0 ? (
                                                                <div className="p-2 text-xs text-center text-gray-400">No services found</div>
                                                            ) : (
                                                                availableServices.map((service) => (
                                                                    <DropdownMenuItem
                                                                        key={service.id}
                                                                        onClick={() => handleAddService(category.id, service)}
                                                                        className="cursor-pointer hover:bg-white/5 focus:bg-white/5"
                                                                    >
                                                                        {service.name}
                                                                    </DropdownMenuItem>
                                                                ))
                                                            )}
                                                        </DropdownMenuContent>
                                                    </DropdownMenu>
                                                </div>
                                            </>
                                        )}
                                    </CardContent>
                                )}
                            </Card>
                        ))}
                    </div>
                </TabsContent>

                {/* Settings Tab */}
                <TabsContent value="settings">
                    <Card className="bg-[#0F141A] border-white/10">
                        <CardHeader>
                            <CardTitle className="flex items-center gap-2">
                                <Settings size={20} />
                                Mega Menu Settings
                            </CardTitle>
                        </CardHeader>
                        <CardContent className="space-y-6">
                            <div className="flex items-center justify-between p-4 bg-[#0B0F14] rounded-lg border border-white/5">
                                <div>
                                    <Label className="text-base font-semibold">Show Pricing in Menu</Label>
                                    <p className="text-xs text-gray-400 mt-1">Display starting prices next to services</p>
                                </div>
                                <Switch checked={showPricing} onCheckedChange={setShowPricing} />
                            </div>
                        </CardContent>
                    </Card>
                </TabsContent>

                {/* Analytics Tab */}
                <TabsContent value="analytics">
                    <Card className="bg-[#0F141A] border-white/10">
                        <CardHeader>
                            <CardTitle className="flex items-center gap-2">
                                <BarChart3 size={20} />
                                Analytics
                            </CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className="grid grid-cols-3 gap-4">
                                <div className="bg-[#0B0F14] p-4 rounded-lg border border-white/5">
                                    <div className="text-2xl font-bold text-primary">
                                        {categories.reduce((acc, cat) => acc + cat.services.filter(s => s.visible).length, 0)}
                                    </div>
                                    <div className="text-sm text-gray-400">Visible Services</div>
                                </div>
                                <div className="bg-[#0B0F14] p-4 rounded-lg border border-white/5">
                                    <div className="text-2xl font-bold text-green-400">
                                        {categories.filter(c => c.visible).length}
                                    </div>
                                    <div className="text-sm text-gray-400">Active Categories</div>
                                </div>
                                <div className="bg-[#0B0F14] p-4 rounded-lg border border-white/5">
                                    <div className="text-2xl font-bold text-yellow-400">
                                        {categories.reduce((acc, cat) => acc + cat.services.filter(s => s.featured).length, 0)}
                                    </div>
                                    <div className="text-sm text-gray-400">Featured Services</div>
                                </div>
                            </div>
                        </CardContent>
                    </Card>
                </TabsContent>
            </Tabs>

            {/* Validation Panel */}
            {validationIssues.length > 0 && (
                <Card className={cn(
                    "bg-yellow-500/10 border-yellow-500/20 transition-all",
                    !validationExpanded && "cursor-pointer hover:bg-yellow-500/15"
                )} onClick={() => !validationExpanded && setValidationExpanded(true)}>
                    <CardContent className="pt-6">
                        <div className="flex items-start justify-between gap-3">
                            <div className="flex items-start gap-3 flex-1">
                                <AlertCircle size={20} className="text-yellow-400 flex-shrink-0 mt-0.5" />
                                <div className="flex-1">
                                    <div className="flex items-center justify-between mb-2">
                                        <h4 className="font-semibold text-yellow-400">
                                            Validation Warnings ({validationIssues.length})
                                        </h4>
                                        <Button
                                            size="sm"
                                            variant="ghost"
                                            onClick={(e) => {
                                                e.stopPropagation();
                                                setValidationExpanded(!validationExpanded);
                                            }}
                                        >
                                            {validationExpanded ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
                                        </Button>
                                    </div>
                                    {validationExpanded && (
                                        <ul className="text-sm text-yellow-200/80 space-y-2">
                                            {validationIssues.map((issue) => (
                                                <li key={issue.id} className="flex items-center gap-2">
                                                    <AlertCircle size={14} className="text-yellow-400" />
                                                    <span>{issue.message}</span>
                                                    <Button
                                                        size="sm"
                                                        variant="link"
                                                        className="h-auto p-0 text-primary hover:text-primary/80"
                                                        onClick={() => setExpandedCategory(issue.categoryId)}
                                                    >
                                                        Fix
                                                    </Button>
                                                </li>
                                            ))}
                                        </ul>
                                    )}
                                </div>
                            </div>
                        </div>
                    </CardContent>
                </Card>
            )}
        </div>
    );
}

"use client";

import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { cn } from "@/lib/utils";
import {
    Mail,
    Search,
    Filter,
    Download,
    Upload,
    RefreshCw,
    MoreVertical,
    Eye,
    Tag,
    Trash2,
    FileDown,
    X,
    Calendar,
    MapPin,
    Activity,
    TrendingUp,
    Users,
    UserPlus,
    UserMinus,
    AlertCircle,
    CheckCircle2,
    Clock,
    ExternalLink,
    Settings,
    Zap,
    Target,
    Flame,
    Snowflake,
    Sun,
    Loader2
} from "lucide-react";

export default function NewsletterLeadsPage() {
    const [subscribers, setSubscribers] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedStatus, setSelectedStatus] = useState("all");
    const [selectedSource, setSelectedSource] = useState("all");
    const [selectedEngagement, setSelectedEngagement] = useState("all");
    const [showFilters, setShowFilters] = useState(false);
    const [selectedSubscriber, setSelectedSubscriber] = useState<any>(null);
    const [selectedRows, setSelectedRows] = useState<number[]>([]);

    // Fetch subscribers on mount
    useEffect(() => {
        fetchSubscribers();
    }, []);

    const fetchSubscribers = async () => {
        setLoading(true);
        try {
            const response = await fetch("/api/newsletter");
            if (response.ok) {
                const data = await response.json();
                setSubscribers(data.subscribers || []);
            }
        } catch (error) {
            console.error("Failed to fetch subscribers:", error);
        } finally {
            setLoading(false);
        }
    };

    // Filter subscribers
    const filteredSubscribers = subscribers.filter(sub => {
        const matchesSearch = sub.email?.toLowerCase().includes(searchQuery.toLowerCase()) ||
            sub.name?.toLowerCase().includes(searchQuery.toLowerCase());
        const matchesStatus = selectedStatus === "all" || sub.status === selectedStatus;
        const matchesSource = selectedSource === "all" || sub.source === selectedSource;
        const matchesEngagement = selectedEngagement === "all" || sub.engagement === selectedEngagement;

        return matchesSearch && matchesStatus && matchesSource && matchesEngagement;
    });

    // Stats
    const activeCount = subscribers.filter(s => s.status === "active").length;
    const weekGrowth = Math.floor(activeCount * 0.01);
    const unsubscribedCount = subscribers.filter(s => s.status === "unsubscribed").length;
    const bouncedCount = subscribers.filter(s => s.status === "bounced").length;

    const getEngagementBadge = (engagement: string) => {
        const config = {
            hot: { icon: Flame, color: "bg-red-500/10 text-red-400 border-red-500/20", label: "Hot" },
            warm: { icon: Sun, color: "bg-yellow-500/10 text-yellow-400 border-yellow-500/20", label: "Warm" },
            cold: { icon: Snowflake, color: "bg-blue-500/10 text-blue-400 border-blue-500/20", label: "Cold" }
        };

        const { icon: Icon, color, label } = config[engagement as keyof typeof config];

        return (
            <span className={cn("inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-medium border", color)}>
                <Icon size={12} />
                {label}
            </span>
        );
    };

    const getStatusBadge = (status: string) => {
        const config = {
            active: { icon: CheckCircle2, color: "bg-teal-500/10 text-teal-400 border-teal-500/20", label: "Active" },
            unsubscribed: { icon: UserMinus, color: "bg-gray-500/10 text-gray-400 border-gray-500/20", label: "Unsubscribed" },
            bounced: { icon: AlertCircle, color: "bg-red-500/10 text-red-400 border-red-500/20", label: "Bounced" }
        };

        const { icon: Icon, color, label } = config[status as keyof typeof config];

        return (
            <span className={cn("inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-medium border", color)}>
                <Icon size={12} />
                {label}
            </span>
        );
    };


    const handleExport = () => {
        const csvContent = [
            ["Email", "Name", "Status", "Source", "First Seen", "Last Activity", "Tags", "Country", "Lead Score"].join(","),
            ...filteredSubscribers.map(sub => [
                sub.email,
                sub.name || "",
                sub.status,
                sub.source,
                new Date(sub.first_seen).toLocaleDateString(),
                new Date(sub.last_activity).toLocaleDateString(),
                sub.tags?.join(";") || "",
                sub.country || "",
                sub.lead_score
            ].join(","))
        ].join("\n");

        const blob = new Blob([csvContent], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `newsletter-subscribers-${Date.now()}.csv`;
        link.click();
    };

    const handleDelete = async (subscriberId: string) => {
        if (!confirm("Are you sure you want to delete this subscriber? This action cannot be undone.")) {
            return;
        }

        try {
            const response = await fetch(`/api/newsletter/${subscriberId}`, {
                method: "DELETE",
            });

            if (!response.ok) {
                throw new Error("Failed to delete subscriber");
            }

            // Refresh the list
            await fetchSubscribers();
            setSelectedSubscriber(null);
            alert("Subscriber deleted successfully");
        } catch (error) {
            console.error("Error deleting subscriber:", error);
            alert("Failed to delete subscriber. Please try again.");
        }
    };

    const handleBulkDelete = async () => {
        if (selectedRows.length === 0) return;

        if (!confirm(`Are you sure you want to delete ${selectedRows.length} subscriber(s)? This action cannot be undone.`)) {
            return;
        }

        try {
            const deletePromises = selectedRows.map(id =>
                fetch(`/api/newsletter/${id}`, { method: "DELETE" })
            );

            await Promise.all(deletePromises);

            // Refresh the list
            await fetchSubscribers();
            setSelectedRows([]);
            alert(`${selectedRows.length} subscriber(s) deleted successfully`);
        } catch (error) {
            console.error("Error deleting subscribers:", error);
            alert("Failed to delete some subscribers. Please try again.");
        }
    };

    const handleBulkAction = (action: string) => {
        if (action === 'delete') {
            handleBulkDelete();
        } else {
            console.log(`Bulk action: ${action} on ${selectedRows.length} subscribers`);
            // Implement other bulk actions
        }
    };


    return (
        <div className="max-w-[1600px] mx-auto">
            {/* Header */}
            <div className="mb-8">
                <div className="flex items-center justify-between mb-6">
                    <div>
                        <div className="flex items-center gap-4 mb-2">
                            <h1 className="text-3xl font-bold text-white">Newsletter Subscribers</h1>
                            <div className="flex items-center gap-2">
                                <span className="px-3 py-1.5 bg-teal-500/10 text-teal-400 rounded-full text-sm font-semibold border border-teal-500/20">
                                    {activeCount.toLocaleString()} Active
                                </span>
                                <span className="px-3 py-1.5 bg-green-500/10 text-green-400 rounded-full text-sm font-medium border border-green-500/20 flex items-center gap-1">
                                    <TrendingUp size={14} />
                                    +{weekGrowth} this week
                                </span>
                            </div>
                        </div>
                        <p className="text-gray-400 text-sm">Manage subscribers, track engagement, and export to CRM</p>
                    </div>
                    <div className="flex items-center gap-3">
                        <button className="flex items-center gap-2 px-4 py-2 bg-white/5 hover:bg-white/10 rounded-lg transition-colors text-sm text-gray-300">
                            <Upload size={16} />
                            Import
                        </button>
                        <button
                            onClick={handleExport}
                            className="flex items-center gap-2 px-4 py-2 bg-white/5 hover:bg-white/10 rounded-lg transition-colors text-sm text-gray-300"
                        >
                            <Download size={16} />
                            Export CSV
                        </button>
                        <button
                            onClick={fetchSubscribers}
                            disabled={loading}
                            className="flex items-center gap-2 px-4 py-2 bg-teal-500 hover:bg-teal-600 rounded-lg transition-colors text-sm text-white font-medium disabled:opacity-50"
                        >
                            <RefreshCw size={16} className={loading ? "animate-spin" : ""} />
                            {loading ? "Refreshing..." : "Refresh Data"}
                        </button>
                    </div>
                </div>

                {/* Stats Cards */}
                <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
                    <div className="bg-[#0F141A] border border-white/5 rounded-xl p-4">
                        <div className="flex items-center justify-between mb-2">
                            <span className="text-sm text-gray-400">Total Subscribers</span>
                            <Users size={16} className="text-gray-500" />
                        </div>
                        <p className="text-2xl font-bold text-white">{subscribers.length.toLocaleString()}</p>
                    </div>
                    <div className="bg-[#0F141A] border border-white/5 rounded-xl p-4">
                        <div className="flex items-center justify-between mb-2">
                            <span className="text-sm text-gray-400">Active</span>
                            <CheckCircle2 size={16} className="text-teal-500" />
                        </div>
                        <p className="text-2xl font-bold text-white">{activeCount.toLocaleString()}</p>
                    </div>
                    <div className="bg-[#0F141A] border border-white/5 rounded-xl p-4">
                        <div className="flex items-center justify-between mb-2">
                            <span className="text-sm text-gray-400">Unsubscribed</span>
                            <UserMinus size={16} className="text-gray-500" />
                        </div>
                        <p className="text-2xl font-bold text-white">{unsubscribedCount.toLocaleString()}</p>
                    </div>
                    <div className="bg-[#0F141A] border border-white/5 rounded-xl p-4">
                        <div className="flex items-center justify-between mb-2">
                            <span className="text-sm text-gray-400">Bounced</span>
                            <AlertCircle size={16} className="text-red-500" />
                        </div>
                        <p className="text-2xl font-bold text-white">{bouncedCount.toLocaleString()}</p>
                    </div>
                </div>

                {/* Filter Bar */}
                <div className="bg-[#0F141A] border border-white/5 rounded-xl p-4">
                    <div className="flex items-center gap-4 flex-wrap">
                        {/* Search */}
                        <div className="relative flex-1 min-w-[300px]">
                            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-500" size={18} />
                            <input
                                type="text"
                                placeholder="Search by email or name..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="w-full pl-10 pr-4 py-2 bg-white/5 border border-white/10 rounded-lg text-sm text-white placeholder-gray-500 focus:outline-none focus:border-teal-500/50"
                            />
                        </div>

                        {/* Status Filter */}
                        <select
                            value={selectedStatus}
                            onChange={(e) => setSelectedStatus(e.target.value)}
                            className="px-4 py-2 bg-white/5 border border-white/10 rounded-lg text-sm text-gray-300 focus:outline-none focus:border-teal-500/50"
                        >
                            <option value="all">All Status</option>
                            <option value="active">Active</option>
                            <option value="unsubscribed">Unsubscribed</option>
                            <option value="bounced">Bounced</option>
                        </select>

                        {/* Source Filter */}
                        <select
                            value={selectedSource}
                            onChange={(e) => setSelectedSource(e.target.value)}
                            className="px-4 py-2 bg-white/5 border border-white/10 rounded-lg text-sm text-gray-300 focus:outline-none focus:border-teal-500/50"
                        >
                            <option value="all">All Sources</option>
                            <option value="Blog Popup">Blog Popup</option>
                            <option value="Footer Form">Footer Form</option>
                            <option value="Webinar">Webinar</option>
                            <option value="Landing Page">Landing Page</option>
                            <option value="Social Media">Social Media</option>
                        </select>

                        {/* Engagement Filter */}
                        <select
                            value={selectedEngagement}
                            onChange={(e) => setSelectedEngagement(e.target.value)}
                            className="px-4 py-2 bg-white/5 border border-white/10 rounded-lg text-sm text-gray-300 focus:outline-none focus:border-teal-500/50"
                        >
                            <option value="all">All Engagement</option>
                            <option value="hot"> Hot</option>
                            <option value="warm"> Warm</option>
                            <option value="cold"> Cold</option>
                        </select>

                        <button
                            onClick={() => setShowFilters(!showFilters)}
                            className="flex items-center gap-2 px-4 py-2 bg-white/5 hover:bg-white/10 rounded-lg transition-colors text-sm text-gray-300"
                        >
                            <Filter size={16} />
                            More Filters
                        </button>
                    </div>

                    {/* Bulk Actions */}
                    {selectedRows.length > 0 && (
                        <div className="mt-4 pt-4 border-t border-white/5 flex items-center gap-3">
                            <span className="text-sm text-gray-400">{selectedRows.length} selected</span>
                            <button
                                onClick={() => handleBulkAction('tag')}
                                className="px-3 py-1.5 bg-white/5 hover:bg-white/10 rounded-lg text-xs text-gray-300"
                            >
                                Add Tags
                            </button>
                            <button
                                onClick={() => handleBulkAction('export')}
                                className="px-3 py-1.5 bg-white/5 hover:bg-white/10 rounded-lg text-xs text-gray-300"
                            >
                                Export Selected
                            </button>
                            <button
                                onClick={() => handleBulkAction('delete')}
                                className="px-3 py-1.5 bg-red-500/10 hover:bg-red-500/20 rounded-lg text-xs text-red-400"
                            >
                                Delete
                            </button>
                        </div>
                    )}
                </div>
            </div>

            {/* Subscribers Table */}
            <div className="bg-[#0F141A] border border-white/5 rounded-2xl overflow-hidden">
                <div className="overflow-x-auto">
                    <table className="w-full">
                        <thead className="bg-white/5">
                            <tr>
                                <th className="px-4 py-3 text-left">
                                    <input
                                        type="checkbox"
                                        onChange={(e) => {
                                            if (e.target.checked) {
                                                setSelectedRows(filteredSubscribers.map(s => s.id));
                                            } else {
                                                setSelectedRows([]);
                                            }
                                        }}
                                        className="rounded border-white/20 bg-white/5"
                                    />
                                </th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Email</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Name</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Status</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Source</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Engagement</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Lead Score</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Tags</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Last Activity</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Actions</th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-white/5">
                            {loading ? (
                                <tr>
                                    <td colSpan={10} className="px-6 py-12 text-center">
                                        <Loader2 className="w-8 h-8 animate-spin text-teal-500 mx-auto mb-3" />
                                        <p className="text-gray-400">Loading subscribers...</p>
                                    </td>
                                </tr>
                            ) : filteredSubscribers.length === 0 ? (
                                <tr>
                                    <td colSpan={10} className="px-6 py-12 text-center">
                                        <Mail className="w-12 h-12 text-gray-600 mx-auto mb-3" />
                                        <p className="text-gray-400 text-lg font-medium mb-1">No subscribers yet</p>
                                        <p className="text-gray-500 text-sm">Subscribers will appear here when they sign up via your newsletter forms.</p>
                                    </td>
                                </tr>
                            ) : (
                                filteredSubscribers.map((subscriber, index) => (
                                    <motion.tr
                                        key={subscriber.id}
                                        initial={{ opacity: 0, y: 20 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        transition={{ delay: index * 0.02 }}
                                        className="hover:bg-white/5 transition-colors cursor-pointer"
                                        onClick={() => setSelectedSubscriber(subscriber)}
                                    >
                                        <td className="px-4 py-4" onClick={(e) => e.stopPropagation()}>
                                            <input
                                                type="checkbox"
                                                checked={selectedRows.includes(subscriber.id)}
                                                onChange={(e) => {
                                                    if (e.target.checked) {
                                                        setSelectedRows([...selectedRows, subscriber.id]);
                                                    } else {
                                                        setSelectedRows(selectedRows.filter(id => id !== subscriber.id));
                                                    }
                                                }}
                                                className="rounded border-white/20 bg-white/5"
                                            />
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap">
                                            <div className="flex items-center gap-2">
                                                <Mail size={14} className="text-gray-500" />
                                                <span className="text-sm text-white">{subscriber.email}</span>
                                            </div>
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-300">{subscriber.name || "-"}</td>
                                        <td className="px-6 py-4 whitespace-nowrap">{getStatusBadge(subscriber.status)}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-400">{subscriber.source}</td>
                                        <td className="px-6 py-4 whitespace-nowrap">{getEngagementBadge(subscriber.engagement)}</td>
                                        <td className="px-6 py-4 whitespace-nowrap">
                                            <div className="flex items-center gap-2">
                                                <div className="flex-1 h-2 bg-white/5 rounded-full overflow-hidden w-20">
                                                    <div
                                                        className={cn(
                                                            "h-full rounded-full",
                                                            subscriber.lead_score >= 70 ? "bg-green-500" :
                                                                subscriber.lead_score >= 40 ? "bg-yellow-500" : "bg-red-500"
                                                        )}
                                                        style={{ width: `${subscriber.lead_score}%` }}
                                                    />
                                                </div>
                                                <span className="text-xs text-gray-400 w-8">{subscriber.lead_score}</span>
                                            </div>
                                        </td>
                                        <td className="px-6 py-4">
                                            <div className="flex flex-wrap gap-1">
                                                {subscriber.tags?.slice(0, 2).map((tag: string, i: number) => (
                                                    <span key={i} className="px-2 py-0.5 bg-blue-500/10 text-blue-400 rounded text-xs border border-blue-500/20">
                                                        {tag}
                                                    </span>
                                                ))}
                                                {subscriber.tags?.length > 2 && (
                                                    <span className="px-2 py-0.5 bg-white/5 text-gray-400 rounded text-xs">
                                                        +{subscriber.tags.length - 2}
                                                    </span>
                                                )}
                                            </div>
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-400">
                                            {new Date(subscriber.last_activity).toLocaleDateString()}
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap" onClick={(e) => e.stopPropagation()}>
                                            <button className="p-1.5 hover:bg-white/10 rounded-lg transition-colors">
                                                <MoreVertical size={16} className="text-gray-400" />
                                            </button>
                                        </td>
                                    </motion.tr>
                                ))
                            )}
                        </tbody>
                    </table>
                </div>

                {/* Pagination */}
                <div className="px-6 py-4 border-t border-white/5 flex items-center justify-between">
                    <span className="text-sm text-gray-400">
                        Showing {filteredSubscribers.length} of {subscribers.length} subscribers
                    </span>
                    <div className="flex gap-2">
                        <button className="px-3 py-1.5 bg-white/5 hover:bg-white/10 rounded-lg text-sm text-gray-300">
                            Previous
                        </button>
                        <button className="px-3 py-1.5 bg-white/5 hover:bg-white/10 rounded-lg text-sm text-gray-300">
                            Next
                        </button>
                    </div>
                </div>
            </div>

            {/* Subscriber Detail Drawer */}
            <AnimatePresence>
                {selectedSubscriber && (
                    <>
                        <motion.div
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            exit={{ opacity: 0 }}
                            className="fixed inset-0 bg-black/60 backdrop-blur-sm z-40"
                            onClick={() => setSelectedSubscriber(null)}
                        />
                        <motion.div
                            initial={{ x: "100%" }}
                            animate={{ x: 0 }}
                            exit={{ x: "100%" }}
                            transition={{ type: "spring", damping: 25 }}
                            className="fixed right-0 top-0 h-full w-full max-w-2xl bg-[#0B0F14] border-l border-white/5 z-50 overflow-y-auto"
                        >
                            {/* Drawer Header */}
                            <div className="sticky top-0 bg-[#0B0F14] border-b border-white/5 p-6 z-10">
                                <div className="flex items-start justify-between mb-4">
                                    <div>
                                        <h2 className="text-2xl font-bold text-white mb-1">{selectedSubscriber.name}</h2>
                                        <p className="text-gray-400">{selectedSubscriber.email}</p>
                                    </div>
                                    <button
                                        onClick={() => setSelectedSubscriber(null)}
                                        className="p-2 hover:bg-white/10 rounded-lg transition-colors"
                                    >
                                        <X size={20} className="text-gray-400" />
                                    </button>
                                </div>
                                <div className="flex items-center gap-3">
                                    {getStatusBadge(selectedSubscriber.status)}
                                    {getEngagementBadge(selectedSubscriber.engagement)}
                                    <span className="px-3 py-1 bg-white/5 text-gray-300 rounded-full text-xs flex items-center gap-1.5">
                                        <Target size={12} />
                                        Score: {selectedSubscriber.lead_score}
                                    </span>
                                </div>
                            </div>

                            {/* Drawer Content */}
                            <div className="p-6 space-y-6">
                                {/* Quick Stats */}
                                <div className="grid grid-cols-3 gap-4">
                                    <div className="bg-white/5 rounded-xl p-4 border border-white/5">
                                        <p className="text-xs text-gray-400 mb-1">Opens</p>
                                        <p className="text-2xl font-bold text-white">{selectedSubscriber.opens}</p>
                                    </div>
                                    <div className="bg-white/5 rounded-xl p-4 border border-white/5">
                                        <p className="text-xs text-gray-400 mb-1">Clicks</p>
                                        <p className="text-2xl font-bold text-white">{selectedSubscriber.clicks}</p>
                                    </div>
                                    <div className="bg-white/5 rounded-xl p-4 border border-white/5">
                                        <p className="text-xs text-gray-400 mb-1">Page Visits</p>
                                        <p className="text-2xl font-bold text-white">{selectedSubscriber.page_visits || 0}</p>
                                    </div>
                                </div>

                                {/* Details */}
                                <div className="space-y-4">
                                    <h3 className="text-lg font-bold text-white">Subscriber Details</h3>
                                    <div className="space-y-3">
                                        <div className="flex items-center justify-between py-2 border-b border-white/5">
                                            <span className="text-sm text-gray-400">Source</span>
                                            <span className="text-sm text-white">{selectedSubscriber.source}</span>
                                        </div>
                                        <div className="flex items-center justify-between py-2 border-b border-white/5">
                                            <span className="text-sm text-gray-400">First Seen</span>
                                            <span className="text-sm text-white">{selectedSubscriber.first_seen ? new Date(selectedSubscriber.first_seen).toLocaleDateString() : 'N/A'}</span>
                                        </div>
                                        <div className="flex items-center justify-between py-2 border-b border-white/5">
                                            <span className="text-sm text-gray-400">Last Activity</span>
                                            <span className="text-sm text-white">{selectedSubscriber.last_activity ? new Date(selectedSubscriber.last_activity).toLocaleDateString() : 'N/A'}</span>
                                        </div>
                                        <div className="flex items-center justify-between py-2 border-b border-white/5">
                                            <span className="text-sm text-gray-400">Country</span>
                                            <span className="text-sm text-white flex items-center gap-2">
                                                <MapPin size={14} />
                                                {selectedSubscriber.country}
                                            </span>
                                        </div>
                                        <div className="flex items-center justify-between py-2 border-b border-white/5">
                                            <span className="text-sm text-gray-400">Double Opt-In</span>
                                            <span className="text-sm text-white">
                                                {selectedSubscriber.double_opt_in ? " Yes" : " No"}
                                            </span>
                                        </div>
                                        <div className="flex items-center justify-between py-2">
                                            <span className="text-sm text-gray-400">Consent</span>
                                            <span className="text-sm text-green-400"> Given</span>
                                        </div>
                                    </div>
                                </div>

                                {/* Tags */}
                                <div className="space-y-4">
                                    <h3 className="text-lg font-bold text-white">Tags</h3>
                                    <div className="flex flex-wrap gap-2">
                                        {selectedSubscriber.tags.map((tag: string, i: number) => (
                                            <span key={i} className="px-3 py-1.5 bg-blue-500/10 text-blue-400 rounded-lg text-sm border border-blue-500/20 flex items-center gap-2">
                                                <Tag size={12} />
                                                {tag}
                                            </span>
                                        ))}
                                        <button className="px-3 py-1.5 bg-white/5 hover:bg-white/10 text-gray-400 rounded-lg text-sm border border-white/10">
                                            + Add Tag
                                        </button>
                                    </div>
                                </div>

                                {/* Actions */}
                                <div className="space-y-3 pt-4 border-t border-white/5">
                                    <button className="w-full flex items-center gap-3 px-4 py-3 bg-white/5 hover:bg-white/10 rounded-lg text-sm text-white transition-colors">
                                        <FileDown size={16} />
                                        GDPR Data Export
                                    </button>
                                    <button className="w-full flex items-center gap-3 px-4 py-3 bg-white/5 hover:bg-white/10 rounded-lg text-sm text-white transition-colors">
                                        <ExternalLink size={16} />
                                        Sync to CRM
                                    </button>
                                    <button
                                        onClick={() => handleDelete(selectedSubscriber.id)}
                                        className="w-full flex items-center gap-3 px-4 py-3 bg-red-500/10 hover:bg-red-500/20 rounded-lg text-sm text-red-400 transition-colors"
                                    >
                                        <Trash2 size={16} />
                                        Delete Subscriber
                                    </button>
                                </div>
                            </div>
                        </motion.div>
                    </>
                )}
            </AnimatePresence>
        </div>
    );
}

