"use client";

import { useState, useEffect } from "react";
import {
    Plus, Edit, Trash2, Check, DollarSign, Star, Copy, LayoutGrid, Tag,
    AlertTriangle, Archive, Eye, EyeOff, ArrowLeft
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { createClient } from "@/lib/supabase/client";
import { cn } from "@/lib/utils";
import PricingPlanModal from "@/components/admin/pricing/PricingPlanModal";
import { motion } from "framer-motion";

export default function AdminPricing() {
    const [services, setServices] = useState<any[]>([]);
    const [selectedService, setSelectedService] = useState<string | null>(null);
    const [plans, setPlans] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [modalOpen, setModalOpen] = useState(false);
    const [editingPlan, setEditingPlan] = useState<any | null>(null);

    const supabase = createClient();

    useEffect(() => {
        fetchServices();
    }, []);

    useEffect(() => {
        if (selectedService) {
            fetchPlans(selectedService);
        }
    }, [selectedService]);

    const fetchServices = async () => {
        const { data } = await supabase.from("services").select("id, name, slug").order("name");
        if (data && data.length > 0) {
            const uniqueServices = Array.from(new Map(data.map(item => [item.slug, item])).values());
            setServices(uniqueServices);
            if (!selectedService) setSelectedService(uniqueServices[0].id);
        }
        setLoading(false);
    };

    const fetchPlans = async (serviceId: string) => {
        setLoading(true);
        const res = await fetch(`/api/admin/pricing?serviceId=${serviceId}`);
        if (res.ok) {
            const data = await res.json();
            // Sort by price (mock sort)
            setPlans(data.sort((a: any, b: any) => a.price - b.price));
        }
        setLoading(false);
    };

    const handleCreate = () => {
        setEditingPlan(null);
        setModalOpen(true);
    };

    const handleEdit = (plan: any) => {
        setEditingPlan(plan);
        setModalOpen(true);
    };

    const handleDuplicate = async (plan: any) => {
        if (!confirm(`Duplicate "${plan.title}"?`)) return;

        const newPlan = {
            ...plan,
            title: `${plan.title} (Copy)`,
            id: undefined,
            is_active: false // Safety: Duplicate as draft
        };

        try {
            const res = await fetch("/api/admin/pricing", {
                method: "POST",
                body: JSON.stringify({ ...newPlan, service_id: selectedService })
            });
            if (res.ok) fetchPlans(selectedService!);
        } catch (e) {
            alert("Failed to duplicate");
        }
    };

    const handleDelete = async (id: string, title: string) => {
        const confirmed = prompt(`TYPE "DELETE" to confirm deleting plan: ${title}`);
        if (confirmed !== "DELETE") return;

        const res = await fetch(`/api/admin/pricing?id=${id}`, { method: "DELETE" });
        if (res.ok) {
            fetchPlans(selectedService!);
        } else {
            alert("Failed to delete plan");
        }
    };

    const formatCurrency = (amount: number, currency: string) => {
        if (currency === 'INR') {
            return new Intl.NumberFormat('en-IN', { style: 'currency', currency: 'INR', maximumFractionDigits: 0 }).format(amount);
        }
        return new Intl.NumberFormat('en-US', { style: 'currency', currency: currency || 'USD', maximumFractionDigits: 0 }).format(amount);
    };

    const selectedServiceData = services.find(s => s.id === selectedService);

    return (
        <div className="max-w-7xl mx-auto space-y-8 pb-20">

            {/* Header with Breadcrumbs */}
            <div className="space-y-4">
                <div className="flex items-center gap-2 text-sm text-gray-500 font-mono">
                    <span>Admin</span>
                    <span>/</span>
                    <span className="text-gray-300">Pricing</span>
                    {selectedServiceData && (
                        <>
                            <span>/</span>
                            <span className="text-primary">{selectedServiceData.name}</span>
                        </>
                    )}
                </div>

                <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                    <div>
                        <h1 className="text-3xl font-bold text-white font-poppins">Pricing Plans</h1>
                        <p className="text-gray-400 mt-1">Manage tiers, pricing models, and feature sets.</p>
                    </div>
                    {plans.length > 0 && selectedService && (
                        <Button onClick={handleCreate} className="gap-2 shadow-[0_0_20px_-5px_var(--color-primary)] bg-primary text-black hover:bg-primary/90">
                            <Plus size={18} /> Create Plan
                        </Button>
                    )}
                </div>
            </div>

            {/* Service Tabs */}
            <div className="border-b border-white/10">
                <div className="flex gap-1 overflow-x-auto no-scrollbar pb-1">
                    {services.map(service => (
                        <button
                            key={service.id}
                            onClick={() => setSelectedService(service.id)}
                            className={cn(
                                "whitespace-nowrap px-6 py-3 border-b-2 text-sm font-medium transition-all duration-200",
                                selectedService === service.id
                                    ? "border-primary text-primary bg-primary/5"
                                    : "border-transparent text-gray-400 hover:text-white hover:border-white/20"
                            )}
                        >
                            {service.name}
                        </button>
                    ))}
                </div>
            </div>

            {/* Main Content Area */}
            {loading ? (
                <div className="flex flex-col items-center justify-center py-24 space-y-4">
                    <div className="w-8 h-8 border-2 border-primary border-t-transparent rounded-full animate-spin" />
                    <p className="text-gray-500 animate-pulse">Loading plans...</p>
                </div>
            ) : !selectedService ? (
                <div className="text-center py-20 text-gray-500">Select a service to manage pricing</div>
            ) : plans.length === 0 ? (
                <div className="flex flex-col items-center justify-center py-24 bg-[#0F141A] border border-white/5 rounded-2xl dashed">
                    <div className="w-16 h-16 bg-white/5 rounded-full flex items-center justify-center mb-4">
                        <LayoutGrid size={32} className="text-gray-500" />
                    </div>
                    <h3 className="text-xl font-bold text-white">No plans configured</h3>
                    <p className="text-gray-400 text-sm mt-2 mb-8 max-w-md text-center">
                        This service has no active pricing tiers. Create 3 tiers (Starter, Pro, Enterprise) for best conversion.
                    </p>
                    <Button onClick={handleCreate} size="lg" className="bg-primary text-black hover:bg-primary/90">
                        <Plus size={18} className="mr-2" /> Add First Plan
                    </Button>
                </div>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {plans.map((plan) => (
                        <div
                            key={plan.id}
                            className={cn(
                                "group flex flex-col relative bg-[#0F141A] border rounded-2xl transition-all duration-300 hover:-translate-y-1 hover:shadow-2xl hover:shadow-black/50 overflow-hidden",
                                plan.is_active ? "border-white/10 hover:border-white/20" : "border-red-900/30 opacity-75 grayscale-[0.5] hover:opacity-100 hover:grayscale-0",
                                plan.is_popular && plan.is_active && "border-primary/50 shadow-[0_0_20px_-10px_var(--color-primary)]"
                            )}
                        >
                            {/* Status Bar */}
                            <div className={cn(
                                "h-1 w-full",
                                plan.is_active ? "bg-green-500" : "bg-red-500"
                            )} />

                            <div className="p-6 flex-1 flex flex-col">
                                <div className="flex justify-between items-start mb-4">
                                    {plan.is_popular ? (
                                        <div className="px-2 py-1 bg-primary/20 text-primary text-[10px] font-bold uppercase tracking-wider rounded border border-primary/20 flex items-center gap-1">
                                            <Star size={10} fill="currentColor" /> Popular
                                        </div>
                                    ) : <div></div>}

                                    <div className={cn(
                                        "px-2 py-0.5 text-[10px] font-bold uppercase tracking-wider rounded-full border",
                                        plan.is_active
                                            ? "bg-green-500/10 text-green-500 border-green-500/20"
                                            : "bg-red-500/10 text-red-500 border-red-500/20"
                                    )}>
                                        {plan.is_active ? "Live" : "Draft"}
                                    </div>
                                </div>

                                <h3 className="text-lg font-bold text-white mb-2">{plan.title}</h3>

                                <div className="mb-6 pb-6 border-b border-white/5">
                                    {(plan.price === 0 || plan.title.toLowerCase() === 'custom' || plan.is_custom) ? (
                                        <div className="text-2xl font-bold text-white leading-tight py-2">Contact Sales</div>
                                    ) : (
                                        <div className="flex items-baseline gap-1">
                                            <span className="text-3xl font-bold text-white tracking-tight">
                                                {formatCurrency(plan.price, plan.currency || 'INR')}
                                            </span>
                                            {plan.billing_cycle !== 'one-time' && (
                                                <span className="text-xs text-gray-500 font-medium uppercase">/{plan.billing_cycle}</span>
                                            )}
                                        </div>
                                    )}
                                </div>

                                <div className="space-y-3 flex-1">
                                    <div className="text-xs font-semibold text-gray-500 uppercase flex justify-between">
                                        <span>Features</span>
                                        <span className="text-gray-700">{plan.features?.length || 0}</span>
                                    </div>
                                    {plan.features?.slice(0, 5).map((f: any, i: number) => (
                                        <div key={i} className="flex items-start gap-3 text-sm text-gray-400">
                                            <Check size={14} className="mt-1 text-primary shrink-0 opacity-70" />
                                            <span className="line-clamp-1">{typeof f === 'string' ? f : f.feature_text}</span>
                                        </div>
                                    ))}
                                    {(plan.features?.length || 0) > 5 && (
                                        <p className="text-xs text-gray-600 italic pl-7">
                                            + {plan.features.length - 5} more...
                                        </p>
                                    )}
                                </div>
                            </div>

                            <div className="p-4 bg-white/[0.02] border-t border-white/5 flex items-center justify-between gap-2">
                                <Button
                                    variant="ghost"
                                    size="sm"
                                    className="h-8 text-gray-500 hover:text-white"
                                    onClick={() => handleDuplicate(plan)}
                                    title="Duplicate Plan"
                                >
                                    <Copy size={14} />
                                </Button>

                                <div className="h-4 w-px bg-white/10" />

                                <Button
                                    variant="ghost"
                                    size="sm"
                                    className="h-8 flex-1 text-gray-300 hover:text-white hover:bg-white/5"
                                    onClick={() => handleEdit(plan)}
                                >
                                    <Edit size={14} className="mr-2" /> Edit
                                </Button>

                                <Button
                                    variant="ghost"
                                    size="sm"
                                    className="h-8 w-8 p-0 text-red-500/60 hover:text-red-400 hover:bg-red-500/10"
                                    onClick={() => handleDelete(plan.id, plan.title)}
                                    title="Delete Plan"
                                >
                                    <Trash2 size={14} />
                                </Button>
                            </div>
                        </div>
                    ))}
                </div>
            )}

            {selectedService && (
                <PricingPlanModal
                    isOpen={modalOpen}
                    onClose={() => setModalOpen(false)}
                    serviceId={selectedService}
                    serviceName={selectedServiceData?.name}
                    planToEdit={editingPlan}
                    existingPlans={plans}
                    onSuccess={() => fetchPlans(selectedService)}
                />
            )}
        </div>
    );
}

"use client";

import { useState, useEffect } from "react";
import { useParams, useRouter } from "next/navigation";
import AdminSidebar from "@/components/AdminSidebar";
import { Button } from "@/components/ui/button";
import {
    Save, ArrowLeft, Layout, Search, Globe, DollarSign,
    BarChart, Image as ImageIcon, Eye, Trash2, CheckCircle2
} from "lucide-react";
import { cn } from "@/lib/utils";
import { motion } from "framer-motion";

export default function ServiceEditor() {
    const params = useParams();
    const router = useRouter();
    const id = params.id === 'new' ? '' : params.id;
    const isNew = !id;

    const [activeTab, setActiveTab] = useState("general");
    const [loading, setLoading] = useState(!isNew);
    const [saving, setSaving] = useState(false);

    const [formData, setFormData] = useState({
        id: "",
        name: "",
        category: "Marketing",
        shortDescription: "",
        content: "",
        status: "Draft",
        visible: false,
        display_tags: [] as string[],
        icon: "",
        metaTitle: "",
        metaDescription: "",
        pricingType: "custom",
        price: "",
        ctaText: "Get Started",
        ctaAction: "contact",
    });

    useEffect(() => {
        if (!isNew && id) {
            fetchService();
        }
    }, [id]);

    const fetchService = async () => {
        try {
            const res = await fetch(`/api/services/${id}`);
            if (res.ok) {
                const data = await res.json();
                setFormData(prev => ({ ...prev, ...data }));
            }
        } catch (error) {
            console.error("Failed to fetch service", error);
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (field: string, value: any) => {
        setFormData(prev => ({ ...prev, [field]: value }));
    };

    const handleSave = async () => {
        setSaving(true);
        try {
            const method = isNew ? "POST" : "PATCH";
            const url = isNew ? "/api/services" : `/api/services/${id}`;

            // Auto-generate ID from name if new
            const payload = { ...formData };
            if (isNew && !payload.id) {
                payload.id = payload.name.toLowerCase().replace(/[^a-z0-9]+/g, '-');
            }

            const res = await fetch(url, {
                method,
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(payload)
            });

            if (res.ok) {
                router.push("/admin/services");
            } else {
                alert("Failed to save service");
            }
        } catch (error) {
            console.error("Error saving:", error);
        } finally {
            setSaving(false);
        }
    };

    const handleDelete = async () => {
        if (!confirm("Are you sure you want to delete this service?")) return;
        try {
            const res = await fetch(`/api/services/${id}`, { method: "DELETE" });
            if (res.ok) router.push("/admin/services");
        } catch (error) {
            console.error("Failed to delete", error);
        }
    };

    return (
        <div className="flex h-screen bg-[#0B0F14]">
            <AdminSidebar />
            <main className="flex-1 md:ml-64 p-4 md:p-8 overflow-y-auto">
                <div className="max-w-5xl mx-auto pb-20">

                    {/* Header */}
                    <div className="flex items-center justify-between mb-8 sticky top-0 bg-[#0B0F14] z-10 py-4 border-b border-white/5">
                        <div className="flex items-center gap-4">
                            <Button variant="ghost" size="icon" onClick={() => router.back()}>
                                <ArrowLeft size={20} />
                            </Button>
                            <div>
                                <h1 className="text-2xl font-bold text-white font-poppins">
                                    {isNew ? "Add New Service" : "Edit Service"}
                                </h1>
                                <p className="text-sm text-gray-400">
                                    {isNew ? "Create a new service offering" : `Editing: ${formData.name}`}
                                </p>
                            </div>
                        </div>
                        <div className="flex gap-2">
                            {!isNew && (
                                <Button variant="destructive" size="icon" onClick={handleDelete} title="Delete Service">
                                    <Trash2 size={18} />
                                </Button>
                            )}
                            <Button
                                variant="outline"
                                className="border-white/10 hover:bg-white/5 gap-2"
                                onClick={() => window.open(`/services/${formData.id}`, '_blank')}
                                disabled={isNew}
                            >
                                <Eye size={16} /> Preview
                            </Button>
                            <Button className="gap-2" onClick={handleSave} disabled={saving}>
                                {saving ? <Layout className="animate-spin" size={16} /> : <Save size={16} />}
                                {isNew ? "Publish Service" : "Save Changes"}
                            </Button>
                        </div>
                    </div>

                    {/* Tabs */}
                    <div className="flex gap-1 bg-[#0F141A] p-1 rounded-xl mb-8 w-fit border border-white/5">
                        {[
                            { id: "general", label: "General", icon: Layout },
                            { id: "seo", label: "SEO", icon: Search },
                            { id: "pricing", label: "Pricing", icon: DollarSign },
                            { id: "analytics", label: "Analytics", icon: BarChart },
                        ].map(tab => (
                            <button
                                key={tab.id}
                                onClick={() => setActiveTab(tab.id)}
                                className={cn(
                                    "flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-medium transition-all",
                                    activeTab === tab.id
                                        ? "bg-white/10 text-white shadow-sm"
                                        : "text-gray-400 hover:text-white hover:bg-white/5"
                                )}
                            >
                                <tab.icon size={16} />
                                {tab.label}
                            </button>
                        ))}
                    </div>

                    {/* Content Area */}
                    <div className="space-y-6">

                        {/* GENERAL TAB */}
                        {activeTab === "general" && (
                            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                                <div className="lg:col-span-2 space-y-6">
                                    <div className="bg-[#0F141A] p-6 rounded-2xl border border-white/5 space-y-4">
                                        <h3 className="text-lg font-bold text-white mb-4">Core Info</h3>
                                        <div className="space-y-2">
                                            <label className="text-xs text-gray-400 uppercase font-semibold pl-1">Service Name</label>
                                            <input
                                                type="text"
                                                className="w-full bg-black/20 border border-white/10 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-primary/50"
                                                placeholder="e.g. AI Marketing Automation"
                                                value={formData.name}
                                                onChange={(e) => handleChange("name", e.target.value)}
                                            />
                                        </div>
                                        <div className="grid grid-cols-2 gap-4">
                                            <div className="space-y-2">
                                                <label className="text-xs text-gray-400 uppercase font-semibold pl-1">Category</label>
                                                <select
                                                    className="w-full bg-black/20 border border-white/10 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-primary/50"
                                                    value={formData.category}
                                                    onChange={(e) => handleChange("category", e.target.value)}
                                                >
                                                    <option>Marketing</option>
                                                    <option>Development</option>
                                                    <option>SEO</option>
                                                    <option>Design</option>
                                                    <option>AI Solutions</option>
                                                </select>
                                            </div>
                                            <div className="space-y-2">
                                                <label className="text-xs text-gray-400 uppercase font-semibold pl-1">Slug (URL)</label>
                                                <input
                                                    type="text"
                                                    className="w-full bg-black/20 border border-white/10 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-primary/50"
                                                    placeholder="auto-generated"
                                                    value={formData.id}
                                                    onChange={(e) => handleChange("id", e.target.value)}
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="bg-[#0F141A] p-6 rounded-2xl border border-white/5 space-y-4">
                                        <h3 className="text-lg font-bold text-white mb-4">Content</h3>
                                        <div className="space-y-2">
                                            <label className="text-xs text-gray-400 uppercase font-semibold pl-1">Short Description</label>
                                            <textarea
                                                rows={3}
                                                className="w-full bg-black/20 border border-white/10 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-primary/50 resize-none"
                                                placeholder="Brief summary for the service card..."
                                                value={formData.shortDescription}
                                                onChange={(e) => handleChange("shortDescription", e.target.value)}
                                            />
                                        </div>
                                        <div className="space-y-2">
                                            <label className="text-xs text-gray-400 uppercase font-semibold pl-1">Full Details</label>
                                            <textarea
                                                rows={15}
                                                className="w-full bg-black/20 border border-white/10 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-primary/50"
                                                placeholder="# Service Details..."
                                                value={formData.content}
                                                onChange={(e) => handleChange("content", e.target.value)}
                                            />
                                            <p className="text-[10px] text-gray-500 text-right">Markdown supported</p>
                                        </div>
                                    </div>
                                </div>

                                <div className="space-y-6">
                                    <div className="bg-[#0F141A] p-6 rounded-2xl border border-white/5 space-y-4">
                                        <h3 className="text-lg font-bold text-white mb-4">Visibility</h3>
                                        <div className="flex items-center justify-between bg-black/20 p-4 rounded-xl border border-white/5">
