import { NextRequest, NextResponse } from "next/server";
import { readData, writeData } from "@/lib/db";
import { isAdminAuthenticated } from "@/lib/auth";
import { createAdminClient } from "@/lib/supabase/server";

const USE_SUPABASE = process.env.NEXT_PUBLIC_SUPABASE_URL !== undefined && process.env.SUPABASE_SERVICE_ROLE_KEY !== undefined;

export async function GET(request: NextRequest, { params }: { params: Promise<{ id: string }> }) {
    const { id } = await params;

    if (USE_SUPABASE) {
        try {
            const supabase = createAdminClient();

            // Try to find by id first, then by slug
            let { data, error } = await supabase
                .from('services')
                .select('*')
                .eq('id', id)
                .single();

            // If not found by id, try slug
            if (error && error.code === 'PGRST116') {
                const result = await supabase
                    .from('services')
                    .select('*')
                    .eq('slug', id)
                    .single();

                data = result.data;
                error = result.error;
            }

            if (!error && data) return NextResponse.json(data);
            if (error && error.code !== 'PGRST116') console.error("Supabase error:", error);
        } catch (error) {
            console.error("Supabase fetch error:", error);
        }
    }

    const services = await readData<any>("services");
    const service = services.find((s: any) => s.id === id || s.slug === id);

    if (service) return NextResponse.json(service);
    return NextResponse.json({ error: "Service not found" }, { status: 404 });
}

export async function PATCH(request: NextRequest, { params }: { params: Promise<{ id: string }> }) {
    if (!(await isAdminAuthenticated())) {
        return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { id } = await params;
    const body = await request.json();

    if (USE_SUPABASE) {
        try {
            const supabase = createAdminClient();

            // First, find the service by id or slug to get the actual UUID
            let { data: existingService } = await supabase
                .from('services')
                .select('id')
                .eq('id', id)
                .single();

            // If not found by id, try slug
            if (!existingService) {
                const result = await supabase
                    .from('services')
                    .select('id')
                    .eq('slug', id)
                    .single();

                existingService = result.data;
            }

            if (existingService) {
                // Update using the actual UUID
                const { error } = await supabase
                    .from('services')
                    .update(body)
                    .eq('id', existingService.id);

                if (!error) return NextResponse.json({ success: true });
                console.error("Supabase update error:", error);
            }
        } catch (error) {
            console.error("Supabase error:", error);
        }
    }

    const services = await readData<any>("services");
    const index = services.findIndex((s: any) => s.id === id || s.slug === id);

    if (index !== -1) {
        services[index] = { ...services[index], ...body };
        await writeData("services", services);
        return NextResponse.json({ success: true });
    }

    return NextResponse.json({ error: "Service not found" }, { status: 404 });
}

export async function DELETE(request: NextRequest, { params }: { params: Promise<{ id: string }> }) {
    if (!(await isAdminAuthenticated())) {
        return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { id } = await params;

    if (USE_SUPABASE) {
        try {
            const supabase = createAdminClient();

            // First, find the service by id or slug to get the actual UUID
            let { data: existingService } = await supabase
                .from('services')
                .select('id')
                .eq('id', id)
                .single();

            // If not found by id, try slug
            if (!existingService) {
                const result = await supabase
                    .from('services')
                    .select('id')
                    .eq('slug', id)
                    .single();

                existingService = result.data;
            }

            if (existingService) {
                const { error } = await supabase
                    .from('services')
                    .delete()
                    .eq('id', existingService.id);

                if (!error) return NextResponse.json({ success: true });
            }
        } catch (error) {
            console.error("Supabase error:", error);
        }
    }

    const services = await readData<any>("services");
    const filtered = services.filter((s: any) => s.id !== id && s.slug !== id);
    await writeData("services", filtered);

    return NextResponse.json({ success: true });
}

import { NextRequest, NextResponse } from "next/server";
import { readData, writeData } from "@/lib/db";
import { isAdminAuthenticated } from "@/lib/auth";
import { createAdminClient } from "@/lib/supabase/server";

const USE_SUPABASE = process.env.NEXT_PUBLIC_SUPABASE_URL !== undefined && process.env.SUPABASE_SERVICE_ROLE_KEY !== undefined;

export async function GET() {
    if (USE_SUPABASE) {
        try {
            const supabase = createAdminClient();
            const { data, error } = await supabase
                .from('services')
                .select('*');

            if (!error) return NextResponse.json(data);
            console.error("Supabase error:", error);
        } catch (error) {
            console.error("Supabase fetch error:", error);
        }
    }

    const services = await readData("services");
    return NextResponse.json(services);
}

export async function PATCH(request: NextRequest) {
    if (!(await isAdminAuthenticated())) {
        return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }
    const { id, ...updates } = await request.json();

    if (USE_SUPABASE) {
        try {
            const supabase = createAdminClient();
            // Upsert (insert or update)
            const { error } = await supabase
                .from('services')
                .upsert({ id, ...updates }, { onConflict: 'id' });

            if (!error) return NextResponse.json({ success: true });
            console.error("Supabase update error:", error);
        } catch (error) {
            console.error("Supabase error:", error);
        }
    }

    const services = await readData<any>("services");

    const index = services.findIndex((s: any) => s.id === id);
    if (index === -1) {
        // If not in JSON, but exists in static data, we might want to add it
        services.push({ id, ...updates });
    } else {
        services[index] = { ...services[index], ...updates };
    }

    await writeData("services", services);
    return NextResponse.json({ success: true });
}

export async function POST(request: NextRequest) {
    if (!(await isAdminAuthenticated())) {
        return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }
    const data = await request.json();

    // Ensure ID exists
    if (!data.id) {
        return NextResponse.json({ error: "Service ID is required" }, { status: 400 });
    }

    if (USE_SUPABASE) {
        try {
            const supabase = createAdminClient();
            const { error } = await supabase
                .from('services')
                .insert([data]);

            if (!error) return NextResponse.json({ success: true });
            console.error("Supabase insert error:", error);
            // If error, we might fall back or return error depending on strategy. 
            // For now let's return error to debug.
            return NextResponse.json({ error: error.message }, { status: 500 });
        } catch (error) {
            console.error("Supabase error:", error);
            return NextResponse.json({ error: "Supabase error" }, { status: 500 });
        }
    }

    const services = await readData<any>("services");
    if (services.some((s: any) => s.id === data.id)) {
        return NextResponse.json({ error: "Service ID already exists" }, { status: 400 });
    }

    services.push(data);
    await writeData("services", services);
    return NextResponse.json({ success: true });
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { X, Send, MessageCircle, Minimize2, Loader2 } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

interface Message {
    id: string;
    role: 'user' | 'assistant';
    content: string;
    timestamp: Date;
}

export default function ChatWidget() {
    const [isOpen, setIsOpen] = useState(false);
    const [isMinimized, setIsMinimized] = useState(false);
    const [messages, setMessages] = useState<Message[]>([]);
    const [inputValue, setInputValue] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [sessionId] = useState(() => `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
    const [conversationId, setConversationId] = useState<string | null>(null);
    const [hasNewMessage, setHasNewMessage] = useState(false);

    const messagesEndRef = useRef<HTMLDivElement>(null);
    const inputRef = useRef<HTMLInputElement>(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    useEffect(() => {
        if (isOpen && inputRef.current) {
            inputRef.current.focus();
        }
    }, [isOpen]);

    const sendMessage = async (content: string) => {
        if (!content.trim() || isLoading) return;

        const userMessage: Message = {
            id: `msg_${Date.now()}`,
            role: 'user',
            content: content.trim(),
            timestamp: new Date(),
        };

        setMessages((prev) => [...prev, userMessage]);
        setInputValue('');
        setIsLoading(true);

        try {
            const response = await fetch('/api/chat', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    sessionId,
                    message: content.trim(),
                    conversationId,
                }),
            });

            const data = await response.json();

            if (data.success) {
                setConversationId(data.conversationId);

                const assistantMessage: Message = {
                    id: `msg_${Date.now()}_ai`,
                    role: 'assistant',
                    content: data.response,
                    timestamp: new Date(),
                };

                setMessages((prev) => [...prev, assistantMessage]);

                // Show notification if chat is minimized
                if (isMinimized || !isOpen) {
                    setHasNewMessage(true);
                }
            } else {
                throw new Error(data.error || 'Failed to get response');
            }
        } catch (error: any) {
            console.error('Failed to send message:', error);
            const errorMessage: Message = {
                id: `msg_${Date.now()}_error`,
                role: 'assistant',
                content: error.message || 'Sorry, I encountered an error. Please try again or contact us directly.',
                timestamp: new Date(),
            };
            setMessages((prev) => [...prev, errorMessage]);
        } finally {
            setIsLoading(false);
        }
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        sendMessage(inputValue);
    };

    const openChat = () => {
        setIsOpen(true);
        setIsMinimized(false);
        setHasNewMessage(false);

        // Send initial greeting if no messages
        if (messages.length === 0) {
            setTimeout(() => {
                sendMessage('Hello');
            }, 500);
        }
    };

    const closeChat = () => {
        setIsOpen(false);
        setIsMinimized(false);
    };

    const toggleMinimize = () => {
        setIsMinimized(!isMinimized);
        if (isMinimized) {
            setHasNewMessage(false);
        }
    };

    return (
        <>
            {/* Chat Button */}
            <AnimatePresence>
                {!isOpen && (
                    <motion.button
                        initial={{ scale: 0, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        exit={{ scale: 0, opacity: 0 }}
                        whileHover={{ scale: 1.1 }}
                        whileTap={{ scale: 0.95 }}
                        onClick={openChat}
                        className="fixed bottom-6 right-6 z-50 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-full p-4 shadow-2xl hover:shadow-blue-500/50 transition-all duration-300"
                        aria-label="Open chat"
                    >
                        <MessageCircle className="w-6 h-6" />
                        {hasNewMessage && (
                            <span className="absolute -top-1 -right-1 w-4 h-4 bg-red-500 rounded-full animate-pulse" />
                        )}
                    </motion.button>
                )}
            </AnimatePresence>

            {/* Chat Window */}
            <AnimatePresence>
                {isOpen && (
                    <motion.div
                        initial={{ opacity: 0, y: 100, scale: 0.8 }}
                        animate={{
                            opacity: 1,
                            y: 0,
                            scale: 1,
                            height: isMinimized ? '60px' : '600px'
                        }}
                        exit={{ opacity: 0, y: 100, scale: 0.8 }}
                        transition={{ type: 'spring', damping: 25, stiffness: 300 }}
                        className="fixed bottom-6 right-6 z-50 w-full max-w-md bg-white rounded-2xl shadow-2xl overflow-hidden flex flex-col"
                        style={{ maxHeight: '90vh' }}
                    >
                        {/* Header */}
                        <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-4 flex items-center justify-between">
                            <div className="flex items-center gap-3">
                                <div className="w-10 h-10 bg-white/20 rounded-full flex items-center justify-center backdrop-blur-sm">
                                    <MessageCircle className="w-5 h-5" />
                                </div>
                                <div>
                                    <h3 className="font-semibold text-lg">Digihub Assistant</h3>
                                    <p className="text-xs text-white/80">Your Digital Growth Consultant</p>
                                </div>
                            </div>
                            <div className="flex items-center gap-2">
                                <button
                                    onClick={toggleMinimize}
                                    className="hover:bg-white/20 p-2 rounded-lg transition-colors"
                                    aria-label="Minimize chat"
                                >
                                    <Minimize2 className="w-4 h-4" />
                                </button>
                                <button
                                    onClick={closeChat}
                                    className="hover:bg-white/20 p-2 rounded-lg transition-colors"
                                    aria-label="Close chat"
                                >
                                    <X className="w-4 h-4" />
                                </button>
                            </div>
                        </div>

                        {!isMinimized && (
                            <>
                                {/* Messages */}
                                <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-50">
                                    {messages.map((message) => (
                                        <motion.div
                                            key={message.id}
                                            initial={{ opacity: 0, y: 10 }}
                                            animate={{ opacity: 1, y: 0 }}
                                            className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                                        >
                                            <div
                                                className={`max-w-[80%] rounded-2xl px-4 py-3 ${message.role === 'user'
                                                    ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white'
                                                    : 'bg-white text-gray-800 shadow-sm border border-gray-100'
                                                    }`}
                                            >
                                                <p className="text-sm whitespace-pre-wrap leading-relaxed">
                                                    {message.content}
                                                </p>
                                                <p
                                                    className={`text-xs mt-1 ${message.role === 'user' ? 'text-white/70' : 'text-gray-400'
                                                        }`}
                                                >
                                                    {message.timestamp.toLocaleTimeString([], {
                                                        hour: '2-digit',
                                                        minute: '2-digit',
                                                    })}
                                                </p>
                                            </div>
                                        </motion.div>
                                    ))}

                                    {isLoading && (
                                        <motion.div
                                            initial={{ opacity: 0 }}
                                            animate={{ opacity: 1 }}
                                            className="flex justify-start"
                                        >
                                            <div className="bg-white rounded-2xl px-4 py-3 shadow-sm border border-gray-100">
                                                <div className="flex items-center gap-2">
                                                    <Loader2 className="w-4 h-4 animate-spin text-blue-600" />
                                                    <span className="text-sm text-gray-600">Typing...</span>
                                                </div>
                                            </div>
                                        </motion.div>
                                    )}

                                    <div ref={messagesEndRef} />
                                </div>

                                {/* Input */}
                                <form onSubmit={handleSubmit} className="p-4 bg-white border-t border-gray-100">
                                    <div className="flex gap-2">
                                        <input
                                            ref={inputRef}
                                            type="text"
                                            value={inputValue}
                                            onChange={(e) => setInputValue(e.target.value)}
                                            placeholder="Type your message..."
                                            className="flex-1 px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                                            disabled={isLoading}
                                        />
                                        <button
                                            type="submit"
                                            disabled={!inputValue.trim() || isLoading}
                                            className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-3 rounded-xl hover:shadow-lg disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 hover:scale-105 active:scale-95"
                                            aria-label="Send message"
                                        >
                                            <Send className="w-5 h-5" />
                                        </button>
                                    </div>
                                    <p className="text-xs text-gray-400 mt-2 text-center">
                                        Powered by Digihub AI  We typically reply in seconds
                                    </p>
                                </form>
                            </>
                        )}
                    </motion.div>
                )}
            </AnimatePresence>
        </>
    );
}

"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import { createClient } from "@/lib/supabase/client";
import { Button } from "@/components/ui/button";
import { ArrowLeft, Save, Loader2, Image as ImageIcon } from "lucide-react";
import Link from "next/link";
import { BlogPost } from "@/types";

interface BlogEditorProps {
    initialData?: BlogPost;
    isEditing?: boolean;
}

export default function BlogEditor({ initialData, isEditing = false }: BlogEditorProps) {
    const router = useRouter();
    const supabase = createClient();
    const [loading, setLoading] = useState(false);

    const [formData, setFormData] = useState({
        title: "",
        slug: "",
        excerpt: "",
        content: "",
        type: "blog",
        category: "Strategy",
        image: "",
        status: "draft",
        meta_title: "",
        meta_description: "",
        author_name: "Digihub Team",
        author_role: "Editor",
        published_at: null as string | null,
    });

    useEffect(() => {
        if (initialData) {
            setFormData({
                title: initialData.title || "",
                slug: initialData.slug || "",
                excerpt: initialData.excerpt || "",
                content: initialData.content || "",
                type: initialData.type || "blog",
                category: initialData.category || "Strategy",
                image: initialData.image || "",
                status: initialData.status || "draft",
                meta_title: initialData.meta_title || "",
                meta_description: initialData.meta_desc || "",
                author_name: initialData.author?.name || "Digihub Team",
                author_role: initialData.author?.role || "Editor",
                published_at: initialData.published_at || null,
            });
        }
    }, [initialData]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
        const { name, value } = e.target;
        setFormData(prev => {
            const newData = { ...prev, [name]: value };

            // Auto-generate slug from title if not editing manually and valid title
            if (name === "title" && !isEditing && !prev.slug) {
                newData.slug = value.toLowerCase().replace(/[^a-z0-9]+/g, "-").replace(/^-|-$/g, "");
            }
            return newData;
        });
    };

    const generateSlug = () => {
        const slug = formData.title.toLowerCase().replace(/[^a-z0-9]+/g, "-").replace(/^-|-$/g, "");
        setFormData(prev => ({ ...prev, slug }));
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);

        const payload = {
            title: formData.title,
            slug: formData.slug,
            excerpt: formData.excerpt,
            content: formData.content,
            type: formData.type,
            category: formData.category,
            image: formData.image,
            status: formData.status,
            meta_title: formData.meta_title || formData.title,
            meta_description: formData.meta_description || formData.excerpt,
            // Store author as JSON or flat? DB has flattened fields in my SQL schema, wait...
            // Let's check SQL schema I wrote. 
            // author_name, author_role, author_avatar.
            author_name: formData.author_name,
            author_role: formData.author_role,
            published_at: formData.status === 'published' && !formData.published_at ? new Date().toISOString() : formData.published_at
        };

        if (isEditing && initialData) {
            const { error } = await supabase
                .from('blogs')
                .update({ ...payload, updated_at: new Date().toISOString() })
                .eq('id', initialData.id);

            if (error) {
                alert(`Error updating blog: ${error.message}`);
            } else {
                router.push('/admin/blogs');
            }
        } else {
            const { error } = await supabase
                .from('blogs')
                .insert(payload);

            if (error) {
                alert(`Error creating blog: ${error.message}`);
            } else {
                router.push('/admin/blogs');
            }
        }
        setLoading(false);
    };

    return (
        <form onSubmit={handleSubmit} className="max-w-5xl mx-auto space-y-8 pb-20">
            {/* Header */}
            <div className="flex items-center justify-between">
                <div className="flex items-center gap-4">
                    <Link href="/admin/blogs">
                        <Button variant="ghost" size="icon" type="button" className="text-gray-400 hover:text-white">
                            <ArrowLeft size={20} />
                        </Button>
                    </Link>
                    <div>
                        <h1 className="text-2xl font-bold text-white">{isEditing ? "Edit Blog Post" : "Write New Blog"}</h1>
                        <p className="text-gray-400 text-sm">Fill in the details below.</p>
                    </div>
                </div>
                <div className="flex gap-3">
                    <Button
                        type="button"
                        variant="ghost"
                        className="text-gray-400"
                        onClick={() => router.push('/admin/blogs')}
                    >
                        Cancel
                    </Button>
                    <Button
                        type="submit"
                        className="bg-primary text-black hover:bg-primary/90 min-w-[120px]"
                        disabled={loading}
                    >
                        {loading ? <Loader2 className="animate-spin mr-2" /> : <Save className="mr-2" size={18} />}
                        {isEditing ? "Update" : "Save Blog"}
                    </Button>
                </div>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Main Content (Left) */}
                <div className="lg:col-span-2 space-y-6">
                    {/* Title */}
                    <div className="bg-[#0F141A] border border-white/5 p-6 rounded-2xl space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-400 mb-2">Blog Title</label>
                            <input
                                type="text"
                                name="title"
                                required
                                value={formData.title}
                                onChange={handleChange}
                                placeholder="Enter a catchy title..."
                                className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white text-lg focus:border-primary/50 focus:outline-none"
                            />
                        </div>

                        {/* Slug */}
                        <div>
                            <label className="block text-sm font-medium text-gray-400 mb-2">Slug (URL)</label>
                            <div className="flex gap-2">
                                <span className="bg-white/5 border border-white/10 rounded-lg px-3 py-2 text-gray-500 font-mono text-sm flex items-center">
                                    /blog/
                                </span>
                                <input
                                    type="text"
                                    name="slug"
                                    required
                                    value={formData.slug}
                                    onChange={handleChange}
                                    className="flex-1 bg-[#0B0F14] border border-white/10 rounded-lg p-2 text-gray-300 font-mono text-sm focus:border-primary/50 focus:outline-none"
                                />
                                <Button type="button" onClick={generateSlug} variant="outline" size="sm" className="h-[42px]">
                                    Generate
                                </Button>
                            </div>
                        </div>
                    </div>

                    {/* Editor */}
                    <div className="bg-[#0F141A] border border-white/5 p-6 rounded-2xl space-y-4 min-h-[500px] flex flex-col">
                        <label className="block text-sm font-medium text-gray-400">Content (Markdown)</label>
                        <textarea
                            name="content"
                            required
                            value={formData.content}
                            onChange={handleChange}
                            placeholder="# Write your masterpiece here...\n\nUse markdown for formatting."
                            className="flex-1 w-full bg-[#0B0F14] border border-white/10 rounded-lg p-4 text-white font-mono text-sm focus:border-primary/50 focus:outline-none leading-relaxed resize-none min-h-[400px]"
                        />
                        <p className="text-xs text-gray-500">
                            Supports Markdown: **bold**, # Heading 1, ## Heading 2, - List items, &gt; Quotes
                        </p>
                    </div>

                    {/* Excerpt */}
                    <div className="bg-[#0F141A] border border-white/5 p-6 rounded-2xl">
                        <label className="block text-sm font-medium text-gray-400 mb-2">Short Excerpt (SEO Description)</label>
                        <textarea
                            name="excerpt"
                            rows={3}
                            value={formData.excerpt}
                            onChange={handleChange}
                            className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-gray-300 text-sm focus:border-primary/50 focus:outline-none"
                        />
                    </div>
                </div>

                {/* Sidebar (Right) */}
                <div className="space-y-6">
                    {/* Publishing */}
                    <div className="bg-[#0F141A] border border-white/5 p-6 rounded-2xl space-y-4">
                        <h3 className="font-semibold text-white">Publishing</h3>

                        <div>
                            <label className="block text-sm font-medium text-gray-400 mb-2">Status</label>
                            <select
                                name="status"
                                value={formData.status}
                                onChange={handleChange}
                                className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:border-primary/50 focus:outline-none"
                            >
                                <option value="draft">Draft</option>
                                <option value="published">Published</option>
                                <option value="archived">Archived</option>
                            </select>
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-400 mb-2">Content Type</label>
                            <select
                                name="type"
                                value={formData.type}
                                onChange={handleChange}
                                className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:border-primary/50 focus:outline-none"
                            >
                                <option value="blog">Blog Post</option>
                                <option value="news">News / Press</option>
                                <option value="research">Research / Case Study</option>
                            </select>
                        </div>
                    </div>

                    {/* Organization */}
                    <div className="bg-[#0F141A] border border-white/5 p-6 rounded-2xl space-y-4">
                        <h3 className="font-semibold text-white">Organization</h3>
                        <div>
                            <label className="block text-sm font-medium text-gray-400 mb-2">Category</label>
                            <input
                                type="text"
                                name="category"
                                value={formData.category}
                                onChange={handleChange}
                                list="categories-list"
                                className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:border-primary/50 focus:outline-none"
                            />
                            <datalist id="categories-list">
                                <option value="Strategy" />
                                <option value="SEO" />
                                <option value="Social Media" />
                                <option value="Automation" />
                                <option value="Web Development" />
                            </datalist>
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-400 mb-2">Featured Image URL</label>
                            <div className="space-y-3">
                                <input
                                    type="text"
                                    name="image"
                                    value={formData.image}
                                    onChange={handleChange}
                                    placeholder="https://..."
                                    className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-xs text-gray-300 focus:border-primary/50 focus:outline-none"
                                />
                                {formData.image && (
                                    <div className="rounded-lg overflow-hidden border border-white/10 aspect-video relative group">
                                        <img src={formData.image} alt="Preview" className="w-full h-full object-cover" />
                                        <div className="absolute inset-0 bg-black/50 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                                            <p className="text-xs text-white">Preview</p>
                                        </div>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>

                    {/* Author */}
                    <div className="bg-[#0F141A] border border-white/5 p-6 rounded-2xl space-y-4">
                        <h3 className="font-semibold text-white">Author</h3>
                        <div>
                            <label className="block text-sm font-medium text-gray-400 mb-2">Name</label>
                            <input
                                type="text"
                                name="author_name"
                                value={formData.author_name}
                                onChange={handleChange}
                                className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:border-primary/50 focus:outline-none"
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-400 mb-2">Role</label>
                            <input
                                type="text"
                                name="author_role"
                                value={formData.author_role}
                                onChange={handleChange}
                                className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:border-primary/50 focus:outline-none"
                            />
                        </div>
                    </div>
                </div>
            </div>
        </form>
    );
}

"use client";

import { useState, useEffect } from "react";
import {
    X, Check, Plus, Trash2, GripVertical, AlertCircle,
    DollarSign, Calendar, Star, Zap, Info, Save, Percent, ArrowRight
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { motion, AnimatePresence } from "framer-motion";

interface PricingPlanModalProps {
    isOpen: boolean;
    onClose: () => void;
    serviceId: string;
    serviceName?: string;
    planToEdit?: any;
    existingPlans?: any[]; // For validation
    onSuccess: () => void;
}

export default function PricingPlanModal({
    isOpen,
    onClose,
    serviceId,
    serviceName = "Service",
    planToEdit,
    existingPlans = [],
    onSuccess
}: PricingPlanModalProps) {
    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({
        title: "",
        description: "", // Scope Summary
        price: "", // Monthly Price
        discount_percent: "", // Yearly Discount %
        currency: "INR",
        billing_cycle: "monthly",
        is_popular: false,
        is_active: false,
        is_custom: false,
        features: [] as string[]
    });

    const [yearlyPreview, setYearlyPreview] = useState(0);

    // Load data for edit mode
    useEffect(() => {
        if (planToEdit) {
            setFormData({
                title: planToEdit.title,
                description: planToEdit.description || "",
                price: planToEdit.price,
                discount_percent: planToEdit.discount_percent || "20", // Default to 20 if missing
                currency: planToEdit.currency || "INR",
                billing_cycle: planToEdit.billing_cycle || "monthly",
                is_popular: planToEdit.is_popular,
                is_active: planToEdit.is_active,
                is_custom: planToEdit.is_custom || planToEdit.price === 0 || planToEdit.title.toLowerCase() === 'custom',
                features: planToEdit.features
                    ? planToEdit.features.map((f: any) => typeof f === 'string' ? f : f.feature_text)
                    : []
            });
        } else {
            setFormData({
                title: "",
                description: "",
                price: "",
                discount_percent: "20", // Default 20%
                currency: "INR",
                billing_cycle: "monthly",
                is_popular: false,
                is_active: false,
                is_custom: false,
                features: [""]
            });
        }
    }, [planToEdit, isOpen]);

    // Calculate Yearly Price Preview
    useEffect(() => {
        const monthly = parseFloat(formData.price) || 0;
        const discount = parseFloat(formData.discount_percent) || 0;
        if (monthly > 0) {
            const yearlyTotal = monthly * 12;
            const savings = yearlyTotal * (discount / 100);
            setYearlyPreview(yearlyTotal - savings);
        } else {
            setYearlyPreview(0);
        }
    }, [formData.price, formData.discount_percent]);

    const handleFeatureChange = (index: number, value: string) => {
        const newFeatures = [...formData.features];
        newFeatures[index] = value;
        setFormData({ ...formData, features: newFeatures });
    };

    const addFeature = () => {
        setFormData({ ...formData, features: [...formData.features, ""] });
    };

    const removeFeature = (index: number) => {
        const newFeatures = formData.features.filter((_, i) => i !== index);
        setFormData({ ...formData, features: newFeatures });
    };

    const formatCurrency = (amount: number) => {
        try {
            return new Intl.NumberFormat('en-IN', {
                style: 'currency',
                currency: formData.currency,
                maximumFractionDigits: 0
            }).format(amount);
        } catch (e) {
            return amount.toString();
        }
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);

        // --- VALIDATIONS ---

        // 1. Title Required
        if (!formData.title) {
            alert("Please enter a Plan Title.");
            setLoading(false);
            return;
        }

        // 1.5. Description Required
        if (!formData.description || formData.description.trim() === "") {
            alert("Please enter a Monthly Scope Summary.");
            setLoading(false);
            return;
        }

        // 2. High Price Check for Starter
        if (formData.title.toLowerCase().includes("starter")) {
            const priceVal = parseFloat(formData.price);
            if (priceVal > 50000 && formData.currency === 'INR') {
                if (!confirm(`Warning: ${priceVal} seems extremely high for a "Starter" plan. Are you sure?`)) {
                    setLoading(false);
                    return;
                }
            }
        }

        // 3. Price Validation
        if (!formData.is_custom) {
            if (!formData.price || isNaN(Number(formData.price)) || Number(formData.price) < 0) {
                alert("Please enter a valid monthly price.");
                setLoading(false);
                return;
            }
        }

        // 4. Popular Flag Check
        if (formData.is_popular) {
            const otherPopular = existingPlans.find(p => p.is_popular && p.id !== planToEdit?.id);
            if (otherPopular) {
                if (!confirm(`Plan "${otherPopular.title}" is already marked as Popular. This will switch the popular tag to the current plan. Continue?`)) {
                    setLoading(false);
                    return;
                }
            }
        }

        const payload = {
            ...formData,
            price: formData.is_custom ? 0 : parseFloat(formData.price),
            discount_percent: parseFloat(formData.discount_percent) || 0,
            yearly_price: yearlyPreview, // Send calculated yearly price
            service_id: serviceId,
            features: formData.features.filter(f => f.trim() !== "")
        };

        try {
            let res;
            if (planToEdit) {
                // Update
                res = await fetch("/api/admin/pricing", {
                    method: "PUT",
                    body: JSON.stringify({ id: planToEdit.id, ...payload })
                });
            } else {
                // Create
                res = await fetch("/api/admin/pricing", {
                    method: "POST",
                    body: JSON.stringify(payload)
                });
            }

            if (res.ok) {
                onSuccess();
                onClose();
            } else {
                const error = await res.json();
                alert("Error: " + error.error);
            }
        } catch (err: any) {
            console.error(err);
            alert("Failed to save plan: " + (err.message || JSON.stringify(err)));
        } finally {
            setLoading(false);
        }
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <>
                    <motion.div
                        initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
                        className="fixed inset-0 bg-black/80 backdrop-blur-md z-[60]"
                        onClick={onClose}
                    />
                    <motion.div
                        initial={{ x: "100%" }} animate={{ x: 0 }} exit={{ x: "100%" }}
                        transition={{ type: "spring", damping: 25, stiffness: 200 }}
                        className="fixed top-0 right-0 h-full w-full md:w-[700px] bg-[#0F141A] border-l border-white/10 shadow-2xl z-[70] overflow-y-auto flex flex-col"
                    >
                        {/* Header */}
                        <div className="flex items-center justify-between p-6 border-b border-white/5 bg-[#0F141A] sticky top-0 z-10">
                            <div>
                                <span className="text-xs font-mono text-primary mb-1 block uppercase tracking-wider">
                                    {serviceName}
                                </span>
                                <h2 className="text-xl font-bold text-white flex items-center gap-2">
                                    {planToEdit ? <><ArrowRight className="w-5 h-5" /> Edit Plan</> : <><Plus className="w-5 h-5" /> Create Plan</>}
                                </h2>
                            </div>
                            <button onClick={onClose} className="text-gray-400 hover:text-white transition-colors p-2 hover:bg-white/5 rounded-lg">
                                <X size={20} />
                            </button>
                        </div>

                        {/* Form */}
                        <form onSubmit={handleSubmit} className="flex-1 p-8 space-y-10 overflow-y-auto">

                            {/* Section 1: Review & Scope */}
                            <div className="space-y-6">
                                <h3 className="text-sm font-bold text-gray-400 uppercase tracking-widest flex items-center gap-2">
                                    <Star className="w-4 h-4 text-primary" /> Core Information
                                </h3>

                                <div className="grid grid-cols-2 gap-6">
                                    <div className="space-y-2">
                                        <label className="text-xs text-gray-300 font-medium">Plan Title <span className="text-red-400">*</span></label>
                                        <input
                                            list="plan-titles"
                                            type="text"
                                            placeholder="e.g. Starter, Growth"
                                            className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:border-primary/50 outline-none transition-all placeholder:text-gray-600 focus:ring-1 focus:ring-primary/50"
                                            value={formData.title}
                                            onChange={(e) => {
                                                const val = e.target.value;
                                                setFormData({
                                                    ...formData,
                                                    title: val,
                                                    is_popular: val.toLowerCase() === 'growth' ? true : formData.is_popular,
                                                });
                                            }}
                                        />
                                        <datalist id="plan-titles">
                                            <option value="Starter" />
                                            <option value="Growth" />
                                            <option value="Scale" />
                                            <option value="Enterprise" />
                                        </datalist>
                                    </div>
                                    <div className="space-y-2">
                                        <label className="text-xs text-gray-300 font-medium">Monthly Scope Summary <span className="text-red-400">*</span></label>
                                        <input
                                            type="text"
                                            placeholder="e.g. 10 Posts + Analytics"
                                            className="w-full bg-[#0B0F14] border border-white/10 rounded-lg p-3 text-white focus:border-primary/50 outline-none transition-all placeholder:text-gray-600"
                                            value={formData.description}
                                            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                        />
                                    </div>
                                </div>

                                {/* Custom/Enterprise Toggle */}
                                <label className="flex items-center gap-3 p-4 bg-white/[0.02] border border-white/5 rounded-xl cursor-pointer hover:bg-white/[0.04] transition-colors">
                                    <input
                                        type="checkbox"
                                        className="w-5 h-5 rounded border-gray-600 text-primary focus:ring-offset-0 focus:ring-primary bg-transparent"
                                        checked={formData.is_custom}
                                        onChange={(e) => setFormData({ ...formData, is_custom: e.target.checked })}
                                    />
                                    <div>
                                        <span className="text-sm text-white font-bold block">Enterprise / Custom Pricing</span>
                                        <span className="text-xs text-gray-500">Enable this for high-ticket plans requiring sales consultation. Pricing fields will be disabled.</span>
                                    </div>
                                </label>
                            </div>

                            {/* Section 2: Pricing Logic */}
                            {!formData.is_custom && (
                                <div className="space-y-6 animate-in fade-in slide-in-from-top-4">
                                    <h3 className="text-sm font-bold text-gray-400 uppercase tracking-widest flex items-center gap-2">
                                        <DollarSign className="w-4 h-4 text-primary" /> Pricing & Discounts
                                    </h3>

                                    <div className="p-6 bg-[#0B0F14] rounded-xl border border-white/10 space-y-6">
                                        <div className="grid grid-cols-2 gap-6">
                                            <div className="space-y-2">
                                                <label className="text-xs text-gray-300 font-medium">Monthly Price <span className="text-red-400">*</span></label>
                                                <div className="relative">
                                                    <span className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-500 font-bold">
                                                        {formData.currency === 'INR' ? '' : '$'}
                                                    </span>
                                                    <input
                                                        type="number"
                                                        placeholder="45000"
                                                        className="w-full bg-white/5 border border-white/10 rounded-lg pl-8 pr-3 py-3 text-white focus:border-primary outline-none transition-all font-mono text-lg"
                                                        value={formData.price}
                                                        onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                                                    />
                                                </div>
                                                {/* Text Formatter Preview */}
                                                {formData.price && (
                                                    <div className="text-[10px] text-gray-500 text-right">
                                                        Reads as: <span className="text-gray-300">{formatCurrency(parseFloat(formData.price))} /mo</span>
                                                    </div>
                                                )}
                                            </div>

                                            <div className="space-y-2">
                                                <label className="text-xs text-gray-300 font-medium">Currency</label>
                                                <select
                                                    className="w-full bg-white/5 border border-white/10 rounded-lg p-3 text-white outline-none focus:border-primary transition-all"
                                                    value={formData.currency}
                                                    onChange={(e) => setFormData({ ...formData, currency: e.target.value })}
                                                >
                                                    <option value="INR">INR ()</option>
                                                    <option value="USD">USD ($)</option>
                                                </select>
                                            </div>
                                        </div>

                                        {/* Yearly Discount Logic */}
                                        <div className="pt-4 border-t border-white/10">
                                            <div className="flex items-center justify-between mb-4">
                                                <label className="text-xs text-blue-400 font-bold uppercase">Yearly Plan Settings</label>
                                                <span className="text-[10px] bg-green-500/10 text-green-500 px-2 py-0.5 rounded border border-green-500/20">Auto-calculated</span>
                                            </div>

                                            <div className="grid grid-cols-12 gap-4 items-end">
                                                <div className="col-span-4 space-y-2">
                                                    <label className="text-xs text-gray-400">Discount %</label>
                                                    <div className="relative">
                                                        <input
                                                            type="number"
                                                            className="w-full bg-white/5 border border-white/10 rounded-lg p-3 text-white focus:border-primary outline-none text-right pr-8"
                                                            value={formData.discount_percent}
                                                            onChange={(e) => setFormData({ ...formData, discount_percent: e.target.value })}
                                                        />
                                                        <Percent className="w-4 h-4 text-gray-500 absolute right-3 top-1/2 -translate-y-1/2" />
                                                    </div>
                                                </div>

                                                <div className="col-span-8 p-4 bg-primary/5 border border-primary/20 rounded-lg flex justify-between items-center">
                                                    <div>
                                                        <div className="text-xs text-gray-400 mb-1">Yearly Price (Billed Annually)</div>
                                                        <div className="text-xl font-bold text-white font-mono">{formatCurrency(yearlyPreview)}</div>
                                                    </div>
                                                    <div className="text-right">
                                                        <div className="text-xs text-green-400 font-bold mb-1">Save {formData.discount_percent || 0}%</div>
                                                        <div className="text-[10px] text-gray-500">
                                                            {formatCurrency(((parseFloat(formData.price) || 0) * 12) - yearlyPreview)} saved
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {/* Section 3: Configuration */}
                            <div className="space-y-6">
                                <h3 className="text-sm font-bold text-gray-400 uppercase tracking-widest flex items-center gap-2">
                                    <Zap className="w-4 h-4 text-primary" /> Configuration
                                </h3>

                                <div className="grid grid-cols-2 gap-4">
                                    <label className={`flex items-center gap-3 p-4 border rounded-xl cursor-pointer transition-all ${formData.is_popular ? 'bg-primary/10 border-primary' : 'bg-[#0B0F14] border-white/10 hover:border-white/20'}`}>
                                        <input type="checkbox" className="hidden" checked={formData.is_popular} onChange={(e) => setFormData({ ...formData, is_popular: e.target.checked })} />
                                        <div className="w-5 h-5 rounded-full border border-gray-500 flex items-center justify-center">
                                            {formData.is_popular && <div className="w-3 h-3 bg-primary rounded-full" />}
                                        </div>
                                        <div>
                                            <span className={`text-sm font-bold block ${formData.is_popular ? 'text-white' : 'text-gray-400'}`}>Most Popular</span>
                                            <span className="text-[10px] text-gray-500">Highlight this plan</span>
                                        </div>
                                    </label>

                                    <label className={`flex items-center gap-3 p-4 border rounded-xl cursor-pointer transition-all ${formData.is_active ? 'bg-green-500/10 border-green-500' : 'bg-[#0B0F14] border-white/10 hover:border-white/20'}`}>
                                        <input type="checkbox" className="hidden" checked={formData.is_active} onChange={(e) => setFormData({ ...formData, is_active: e.target.checked })} />
                                        <div className="w-5 h-5 rounded-full border border-gray-500 flex items-center justify-center">
                                            {formData.is_active && <div className="w-3 h-3 bg-green-500 rounded-full" />}
                                        </div>
                                        <div>
                                            <span className={`text-sm font-bold block ${formData.is_active ? 'text-white' : 'text-gray-400'}`}>Active Status</span>
                                            <span className="text-[10px] text-gray-500">Make visible to users</span>
                                        </div>
                                    </label>
                                </div>
                            </div>

                            {/* Section 4: Features */}
                            <div className="space-y-4">
                                <div className="flex justify-between items-center border-b border-white/5 pb-2">
                                    <h3 className="text-sm font-bold text-gray-400 uppercase tracking-widest flex items-center gap-2">
                                        <Check className="w-4 h-4 text-primary" /> Deliverables & SCOPE
                                    </h3>
                                    <button type="button" onClick={addFeature} className="text-xs text-primary font-bold hover:text-white flex items-center gap-1 p-2 hover:bg-primary/10 rounded transition-colors">
                                        <Plus size={14} /> ADD ITEM
                                    </button>
                                </div>

                                <div className="space-y-2">
                                    {formData.features.map((feature, index) => (
                                        <div key={index} className="flex gap-3 items-center group animate-in slide-in-from-left-2 duration-300">
                                            <GripVertical size={16} className="text-gray-700 cursor-grab hover:text-gray-400" />
                                            <div className="flex-1 relative">
                                                <input
                                                    type="text"
                                                    value={feature}
                                                    onChange={(e) => handleFeatureChange(index, e.target.value)}
