    is_popular: boolean;
    is_active: boolean;
    is_custom: boolean;
    description?: string;
}

interface SocialPricingProps {
    plans: PricingPlan[];
}

const fallbackPlans: PricingPlan[] = [
    {
        id: "fallback-1",
        service_id: "social-media-marketing",
        name: "Starter",
        price: 45000,
        currency: "INR",
