    }
}

/**
 * Add contact to Brevo list (for segmentation)
 */
export async function addToBrevoList(email: string, listId: number, attributes?: Record<string, any>) {
    try {
        const contactsApi = new brevo.ContactsApi();
        contactsApi.setApiKey(
            brevo.ContactsApiApiKeys.apiKey,
            process.env.BREVO_API_KEY || ''
        );

        const createContact = new brevo.CreateContact();
        createContact.email = email;
        createContact.listIds = [listId];
        createContact.attributes = attributes || {};
        createContact.updateEnabled = true;

        const response = await contactsApi.createContact(createContact);
        console.log('Contact added to Brevo list:', response.body);
        return { success: true, id: response.body.id };
    } catch (error: any) {
        // If contact already exists, update it
        if (error.response?.body?.code === 'duplicate_parameter') {
            console.log('Contact already exists, updating...');
            return { success: true, message: 'Contact already exists' };
        }
        console.error('Error adding contact to Brevo:', error);
        throw error;
    }
}

import {
    Search, Megaphone, Share2, Bot, Code, LineChart,
    Zap, Target, Sparkles, PenTool, User, Users,
    Video, MousePointer2, Mail, MessageCircle, ShieldCheck,
    Layers, ShoppingCart, MapPin, Database, BarChart3,
    Rocket // Added Rocket as fallback
} from "lucide-react";

export const IconMap: Record<string, any> = {
    "sparkles": Sparkles,
    "target": Target,
    "search": Search,
    "bot": Bot,
    "pentool": PenTool,
    "share2": Share2,
    "user": User,
    "users": Users,
    "video": Video,
    "mousepointer2": MousePointer2,
    "zap": Zap,
    "database": Database,
    "mail": Mail,
    "messagecircle": MessageCircle,
    "shieldcheck": ShieldCheck,
    "layers": Layers,
    "shoppingcart": ShoppingCart,
    "mappin": MapPin,
    "barchart3": BarChart3,
    "megaphone": Megaphone,
    "code": Code,
    "linechart": LineChart,
    "default": Rocket // Fallback
};

export const getIcon = (slug: string) => {
    // Basic mapping based on slug keywords or direct slug match
    // ideally, we store the icon name in the db, but for now we map slug to icon
    if (slug.includes('ai')) return IconMap['sparkles'] || IconMap['bot'];
    if (slug.includes('seo')) return IconMap['search'];
    if (slug.includes('content')) return IconMap['pentool'];
    if (slug.includes('social')) return IconMap['share2'];
    if (slug.includes('personal')) return IconMap['user'];
    if (slug.includes('influencer')) return IconMap['users'];
    if (slug.includes('video')) return IconMap['video'];
    if (slug.includes('cro')) return IconMap['mousepointer2'];
    if (slug.includes('automation')) return IconMap['zap'];
    if (slug.includes('crm')) return IconMap['database'];
    if (slug.includes('email')) return IconMap['mail'];
    if (slug.includes('whatsapp')) return IconMap['messagecircle'];
    if (slug.includes('orm')) return IconMap['shieldcheck'];
    if (slug.includes('funnel')) return IconMap['layers'];
    if (slug.includes('ecommerce')) return IconMap['shoppingcart'];
    if (slug.includes('local')) return IconMap['mappin'];
    if (slug.includes('analytics')) return IconMap['barchart3'];
    if (slug.includes('performance')) return IconMap['target'];

    return IconMap['default'];
}

import { createServerClient } from '@supabase/ssr'
import { NextResponse, type NextRequest } from 'next/server'

export async function updateSession(request: NextRequest) {
    let supabaseResponse = NextResponse.next({
        request,
    })

    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL || process.env.NEXT_PUBLIC_digisolutions_SUPABASE_URL || process.env.digisolutions_SUPABASE_URL;
    const supabaseAnonKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || process.env.NEXT_PUBLIC_digisolutions_SUPABASE_ANON_KEY || process.env.digisolutions_SUPABASE_ANON_KEY;

    const supabase = createServerClient(
        supabaseUrl!,
        supabaseAnonKey!,
        {
            cookies: {
                getAll() {
                    return request.cookies.getAll()
                },
                setAll(cookiesToSet) {
                    cookiesToSet.forEach(({ name, value, options }) => request.cookies.set(name, value))
                    supabaseResponse = NextResponse.next({
                        request,
                    })
                    cookiesToSet.forEach(({ name, value, options }) =>
                        supabaseResponse.cookies.set(name, value, options)
                    )
                },
            },
        }
    )

    // refreshing the auth token
    const { data: { user } } = await supabase.auth.getUser()

    return { response: supabaseResponse, supabase, user }
}

import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'

export async function createClient() {
    const cookieStore = await cookies()

    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL || process.env.NEXT_PUBLIC_digisolutions_SUPABASE_URL || process.env.digisolutions_SUPABASE_URL;
    const supabaseAnonKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || process.env.NEXT_PUBLIC_digisolutions_SUPABASE_ANON_KEY || process.env.digisolutions_SUPABASE_ANON_KEY;

    return createServerClient(
        supabaseUrl!,
        supabaseAnonKey!,
        {
            cookies: {
                getAll() {
                    return cookieStore.getAll()
                },
                setAll(cookiesToSet) {
                    try {
                        cookiesToSet.forEach(({ name, value, options }) =>
                            cookieStore.set(name, value, options)
                        )
                    } catch {
                        // The `setAll` method was called from a Server Component.
                        // This can be ignored if you have middleware refreshing
                        // user sessions.
                    }
                },
            },
        }
    )
}

// Special Admin client with Service Role Key for server-side bypassing of RLS
export function createAdminClient() {
    const { createClient } = require('@supabase/supabase-js')
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL || process.env.NEXT_PUBLIC_digisolutions_SUPABASE_URL || process.env.digisolutions_SUPABASE_URL;
    const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY || process.env.digisolutions_SUPABASE_SERVICE_ROLE_KEY;

    return createClient(
        supabaseUrl!,
        supabaseServiceKey!
    )
}
